    { id: 'ROOT', label: 'ISPRMVE_', freq: 17, tier: 5, pos: {x: 280, y: 50}, children: ['IS', 'PRMVE_'] }
  ];

  const nodeById = Object.fromEntries(nodes.map(n => [n.id, n]));

  // UI Elements
  const tierButtons = [
    document.getElementById('btn-tier-1'),
    document.getElementById('btn-tier-2'),
    document.getElementById('btn-tier-3'),
    document.getElementById('btn-tier-4'),
    document.getElementById('btn-tier-5')
  ];
  const checkboxBinary = document.getElementById('checkbox-binary');
  const radioCharsNo = document.getElementById('radio-chars-no');
  const radioCharsYes = document.getElementById('radio-chars-yes');
  const explanationText = document.getElementById('explanation-text');
  const encodedText = document.getElementById('encoded-text');

  // Explanation text per tier
  function explanationForTier(t) {
    if (t === 1) {
      return "After determining the frequency of each letter, we draw the tree bottom up, one tier at a time.";
    } else if (t >= 2 && t <= 4) {
      return "We continue grouping until we get to the highest tier.";
    } else {
      return "Once at the top, the total frequency becomes 17, which is the total number of characters.";
    }
  }

  function setActiveTierButton(tier) {
    tierButtons.forEach((btn, idx) => {
      if ((idx + 1) === tier) btn.classList.add('active');
      else btn.classList.remove('active');
    });
  }

  function updatePanels() {
    explanationText.textContent = explanationForTier(currentTier);

    // encoding-table visibility
    encodingTableDiv.style.display = (currentTier >= 2) ? 'block' : 'none';

    // encoded-text visibility/content
    if (currentTier === 5) {
      encodedText.style.display = 'block';
      encodedText.textContent = finalEncoded;
    } else {
      encodedText.style.display = 'none';
      // keep content intact or clear; spec focuses on visibility
    }
  }

  // Hook up controls
  tierButtons.forEach((btn, idx) => {
    btn.addEventListener('click', () => {
      currentTier = idx + 1;
      setActiveTierButton(currentTier);
      updatePanels();
      if (window._p5instance) window._p5instance.redraw();
    });
  });

  checkboxBinary.addEventListener('change', (e) => {
    showBinary = e.target.checked;
    if (window._p5instance) window._p5instance.redraw();
  });

  radioCharsNo.addEventListener('change', (e) => {
    if (e.target.checked) {
      showNodeChars = false;
      if (window._p5instance) window._p5instance.redraw();
    }
  });
  radioCharsYes.addEventListener('change', (e) => {
    if (e.target.checked) {
      showNodeChars = true;
      if (window._p5instance) window._p5instance.redraw();
    }
  });

  // p5 sketch
  new p5((p) => {
    window._p5instance = p;

    p.setup = function() {
      const cnv = p.createCanvas(800, 500);
      cnv.parent('canvas-area');
      p.noLoop();
      p.textFont('monospace');
      p.textSize(13);
      p.rectMode(p.CENTER);
      p.textAlign(p.CENTER, p.CENTER);
      p.pixelDensity(1); // keep crisp and consistent
    };

    function drawEdge(parent, child, bitLabel) {
      p.push();
      p.stroke('#C70000');
      p.strokeWeight(2);
      p.line(parent.pos.x, parent.pos.y, child.pos.x, child.pos.y);

      if (showBinary) {
        const mx = (parent.pos.x + child.pos.x) / 2;
        const my = (parent.pos.y + child.pos.y) / 2;
        // Slight offset so text doesn't sit directly on the line
        const offset = bitLabel === '0' ? -10 : 10;
        p.noStroke();
        p.fill(255);
        p.rectMode(p.CENTER);
        p.rect(mx, my + offset, 16, 14, 3);
        p.fill('#C70000');
        p.text(bitLabel, mx, my + offset);
      }
      p.pop();
    }

    function nodeLabel(n) {
      const isLeaf = n.children.length === 0;
      if (isLeaf) return `${n.label}(${n.freq})`;
      if (showNodeChars) return `${n.label}(${n.freq})`;
      return `(${n.freq})`;
    }

    function drawNode(n) {
      const label = nodeLabel(n);
      const padX = 12;
      const padY = 8;
      p.push();
      p.stroke('#C70000');
      p.fill('#FEFDD5');
      // Measure text width to size the rounded rectangle
      const w = Math.max(34, p.textWidth(label) + padX * 2);
      const h = 26 + padY;
      p.rect(n.pos.x, n.pos.y, w, h, 7);
      p.noStroke();
      p.fill(20);
      p.text(label, n.pos.x, n.pos.y + 1);
      p.pop();
    }

    p.draw = function() {
      p.background(255);

      // Draw edges up to currentTier
      nodes.forEach(parent => {
        if (parent.children.length === 0) return;
        if (parent.tier > currentTier) return;
        parent.children.forEach((childId, idx) => {
          const child = nodeById[childId];
          if (!child) return;
          if (child.tier <= currentTier) {
            const bitLabel = idx === 0 ? '0' : '1'; // left child => 0, right child => 1
            drawEdge(parent, child, bitLabel);
          }
        });
      });

      // Draw nodes up to currentTier
      nodes.forEach(n => {
        if (n.tier <= currentTier) {
          drawNode(n);
        }
      });
    };
  });

  // Initialize UI to default state
  setActiveTierButton(currentTier);
  updatePanels();
</script>
</body>
