<body>
  <div id="app">
    <!-- Control Panel -->
    <div id="control-panel">
      <label for="slider-n">n</label>
      <input type="range" id="slider-n" min="1" max="20" step="1" value="1" />
      <input type="number" id="input-n" min="1" max="20" step="1" value="1" />
    </div>

    <!-- Visualization Area -->
    <div id="visualization-area">
      <!-- Function Plot (Left) -->
      <div id="function-plot-container">
        <div class="caption">Function plot: y = 2/x with rectangles</div>
        <canvas id="canvas-function"></canvas>
        <div id="formula-label">$$y = \\frac{2}{x}$$</div>
      </div>

      <!-- Summation Plot (Right) -->
      <div id="sum-plot-container">
        <div class="caption">Stacked areas (telescoping sum)</div>
        <canvas id="canvas-sum"></canvas>
      </div>
    </div>
  </div>

  <script>
    // Global state and utilities
    const state = {
      n: 1,
      colors: [
        '#ffff80', '#98fb98', '#7fffd4', '#00ffff', '#87cefa',
        '#6495ed', '#7b68ee', '#9370db', '#8a2be2', '#6a5acd',
        '#4169e1', '#1e90ff', '#00bfff', '#00ced1', '#20b2aa',
        '#3cb371', '#66cdaa', '#76eec6', '#a7ffeb', '#e0ffff'
      ],
      sketches: { left: null, right: null }
    };

    // Control synchronization
    function setupControls() {
      const slider = document.getElementById('slider-n');
      const input = document.getElementById('input-n');

      const clamp = (v) => {
        const min = parseInt(slider.min, 10);
        const max = parseInt(slider.max, 10);
        return Math.min(max, Math.max(min, v));
      };

      const updateN = (v) => {
        const val = clamp(parseInt(v, 10) || 1);
        if (state.n !== val) {
          state.n = val;
          slider.value = val;
          input.value = val;
          requestRedraw();
        } else {
          // keep inputs in sync even if same
          slider.value = val;
          input.value = val;
        }
      };

      slider.addEventListener('input', (e) => updateN(e.target.value));
      input.addEventListener('input', (e) => updateN(e.target.value));
    }

    // Redraw both sketches on state change
    function requestRedraw() {
      if (state.sketches.left) state.sketches.left.redraw();
      if (state.sketches.right) state.sketches.right.redraw();
    }

    // Helper: Replace placeholder canvas with p5 canvas (keeping same id)
    function replaceCanvasWithP5(p, placeholderCanvasId, parentContainerId, desiredHeight) {
      const parent = document.getElementById(parentContainerId);
      const oldCanvas = document.getElementById(placeholderCanvasId);
      const width = parent.clientWidth || 600;
      const height = Math.max(desiredHeight, 320);
      if (oldCanvas && oldCanvas.parentNode) {
        oldCanvas.parentNode.removeChild(oldCanvas);
      }
      const cnv = p.createCanvas(width, height);
      cnv.id(placeholderCanvasId);
      cnv.parent(parent);
      return cnv;
    }

    // Resize canvases responsively
    function setupResizeHandlers(leftInstance, rightInstance) {
      const doResize = () => {
        const leftContainer = document.getElementById('function-plot-container');
        const rightContainer = document.getElementById('sum-plot-container');
        if (leftInstance) {
          const lw = leftContainer.clientWidth;
          const lh = Math.max(leftContainer.clientHeight, 380);
          leftInstance.resizeCanvas(lw, lh);
          leftInstance.redraw();
        }
        if (rightInstance) {
          const rw = rightContainer.clientWidth;
          const rh = Math.max(rightContainer.clientHeight, 380);
          rightInstance.resizeCanvas(rw, rh);
          rightInstance.redraw();
        }
      };
      window.addEventListener('resize', () => {
        // Debounce
        clearTimeout(window.__vizResizeTimer);
        window.__vizResizeTimer = setTimeout(doResize, 100);
      });
    }

    // Left plot (function y = 2/x and rectangles)
    const functionPlot = (p) => {
      // Logical bounds
      const X_MIN = 0, X_MAX = 10.5;
      const Y_MIN = 0, Y_MAX = 2.2;
      const PAD = { left: 56, right: 20, top: 20, bottom: 48 };

      const mapX = (x) => PAD.left + (x - X_MIN) / (X_MAX - X_MIN) * (p.width - PAD.left - PAD.right);
      const mapY = (y) => p.height - PAD.bottom - (y - Y_MIN) / (Y_MAX - Y_MIN) * (p.height - PAD.top - PAD.bottom);

      p.setup = () => {
        replaceCanvasWithP5(p, 'canvas-function', 'function-plot-container', 420);
        p.noLoop();
        p.pixelDensity(1);
        p.textFont('system-ui, -apple-system, Segoe UI, Roboto, Helvetica, Arial');
      };

      function drawAxes() {
        p.push();
        p.strokeWeight(1.2);
        p.stroke('#cbd5e1');
        // Axes
        p.line(mapX(X_MIN), mapY(0), mapX(X_MAX), mapY(0)); // x-axis
        p.line(mapX(0), mapY(Y_MIN), mapX(0), mapY(Y_MAX)); // y-axis

        // Ticks and labels
        p.textAlign(p.CENTER, p.TOP);
        p.fill('#e5e7eb');
        p.noStroke();
        p.textSize(12);

        // X ticks 0..10
        p.stroke('#64748b');
        for (let xi = 0; xi <= 10; xi++) {
          const xpx = mapX(xi);
          p.stroke('#475569');
          p.line(xpx, mapY(0) - 4, xpx, mapY(0) + 4);
          p.stroke('rgba(148,163,184,0.15)');
          if (xi !== 0) {
            p.line(xpx, mapY(Y_MIN), xpx, mapY(Y_MAX));
          }
          p.noStroke();
          p.fill('#94a3b8');
          p.text(xi.toString(), xpx, mapY(0) + 8);
        }

        // Y ticks 0..2
        p.textAlign(p.RIGHT, p.CENTER);
        for (let yi = 0; yi <= 2; yi++) {
          const ypx = mapY(yi);
          p.stroke('#475569');
          p.line(mapX(0) - 4, ypx, mapX(0) + 4, ypx);
          p.stroke('rgba(148,163,184,0.15)');
          if (yi !== 0) {
            p.line(mapX(X_MIN), ypx, mapX(X_MAX), ypx);
          }
          p.noStroke();
          p.fill('#94a3b8');
          p.text(yi.toString(), mapX(0) - 8, ypx);
        }
        p.pop();
      }

      function drawCurve() {
        p.push();
        p.noFill();
        p.stroke('#3b82f6');
        p.strokeWeight(2);
        p.beginShape();
        const xStart = 0.5;
        const xEnd = 10;
        const steps = 700;
        for (let i = 0; i <= steps; i++) {
          const x = xStart + (xEnd - xStart) * (i / steps);
          const y = 2 / x;
          p.vertex(mapX(x), mapY(y));
        }
        p.endShape();
        p.pop();
      }

      function drawRectangles(n) {
        for (let k = 1; k <= n; k++) {
          const x0 = k, x1 = k + 1;
          const y0 = 2 / (k + 1), y1 = 2 / k;
          const px0 = mapX(x0), px1 = mapX(x1);
          const py0 = mapY(y0), py1 = mapY(y1);
          const left = px0, top = Math.min(py0, py1), w = px1 - px0, h = Math.abs(py1 - py0);

          const color = state.colors[(k - 1) % state.colors.length];
          p.noStroke();
          p.fill(color);
          p.rect(left, top, w, h);

          // subtle outline
          p.noFill();
          p.stroke(255, 255, 255, 30);
          p.rect(left, top, w, h);

          // Dashed vertical line for first 3 rectangles
          if (k <= 3) {
            p.push();
            p.stroke('#9ca3af');
            p.strokeWeight(1.2);
            p.drawingContext.setLineDash([6, 5]);
            const xDash = mapX(k);
            p.line(xDash, mapY(0), xDash, mapY(2 / k));
            p.pop();
          }
        }
      }

      p.draw = () => {
        p.clear();
        // Background gradient
        p.push();
        p.noStroke();
        p.fill(255, 255, 255, 6);
        p.rect(0, 0, p.width, p.height);
        p.pop();

        drawAxes();
        drawRectangles(state.n);
        drawCurve();

        // Title
        p.push();
        p.noStroke();
        p.fill('#e5e7eb');
        p.textAlign(p.LEFT, p.BOTTOM);
        p.textSize(13);
        p.text('Rectangles for k = 1..' + state.n, mapX(0) + 6, mapY(Y_MAX) + 16);
        p.pop();
      };
    };

    // Right plot (stacked sum)
    const sumPlot = (p) => {
      // Logical bounds
      const X_MIN = 0, X_MAX = 1.5;
      const Y_MIN = 0, Y_MAX = 2.2;
      const PAD = { left: 56, right: 20, top: 20, bottom: 48 };

      const mapX = (x) => PAD.left + (x - X_MIN) / (X_MAX - X_MIN) * (p.width - PAD.left - PAD.right);
      const mapY = (y) => p.height - PAD.bottom - (y - Y_MIN) / (Y_MAX - Y_MIN) * (p.height - PAD.top - PAD.bottom);

      p.setup = () => {
        replaceCanvasWithP5(p, 'canvas-sum', 'sum-plot-container', 420);
        p.noLoop();
        p.pixelDensity(1);
        p.textFont('system-ui, -apple-system, Segoe UI, Roboto, Helvetica, Arial');
      };

      function drawAxes() {
        p.push();
        p.stroke('#cbd5e1');
        p.strokeWeight(1.2);
        // axes
        p.line(mapX(X_MIN), mapY(0), mapX(X_MAX), mapY(0));
        p.line(mapX(0), mapY(Y_MIN), mapX(0), mapY(Y_MAX));

        // ticks: x at 0,1
        p.textSize(12);
        p.fill('#94a3b8');
        p.noStroke();
        p.textAlign(p.CENTER, p.TOP);
        [0, 1].forEach(xi => {
          const xpx = mapX(xi);
          p.stroke('#475569');
          p.line(xpx, mapY(0) - 4, xpx, mapY(0) + 4);
          p.noStroke();
          p.text(xi.toString(), xpx, mapY(0) + 8);
        });

        // y ticks: 0,1,2
        p.textAlign(p.RIGHT, p.CENTER);
        [0, 1, 2].forEach(yi => {
          const ypx = mapY(yi);
          p.stroke('#475569');
          p.line(mapX(0) - 4, ypx, mapX(0) + 4, ypx);
          p.stroke('rgba(148,163,184,0.15)');
          if (yi !== 0) p.line(mapX(X_MIN), ypx, mapX(X_MAX), ypx);
          p.noStroke();
          p.fill('#94a3b8');
          p.text(yi.toString(), mapX(0) - 8, ypx);
        });
        p.pop();
      }

      function drawStack(n) {
        let y_stack = 0;
        // Draw from k=n down to 1 to have k=1 on top
        for (let k = n; k >= 1; k--) {
          const h = 2 / k - 2 / (k + 1);
          const color = state.colors[(k - 1) % state.colors.length];

          const x0 = 0, y0 = y_stack;
          const w = 1;

          const px = mapX(x0);
          const py = mapY(y0 + h); // top pixel
          const pw = mapX(x0 + w) - mapX(x0);
          const ph = mapY(y0) - mapY(y0 + h); // positive height in pixels

          p.noStroke();
          p.fill(color);
          p.rect(px, py, pw, ph);

          // subtle outline
          p.noFill();
          p.stroke(255, 255, 255, 30);
          p.rect(px, py, pw, ph);

          y_stack += h;
        }

        // Optional: show total height line and label
        const total = 2 - 2 / (n + 1);
        p.push();
        p.stroke('#22d3ee');
        p.strokeWeight(1.2);
        p.drawingContext.setLineDash([5, 4]);
        const ytot = mapY(total);
        p.line(mapX(0), ytot, mapX(1.2), ytot);
        p.noStroke();
        p.fill('#22d3ee');
        p.textAlign(p.LEFT, p.BOTTOM);
        p.textSize(12);
        p.text(`total: 2 - 2/(${n+1}) = ${total.toFixed(3)}`, mapX(0.05), ytot - 6);
        p.pop();
      }

      p.draw = () => {
        p.clear();
        p.push(); p.noStroke(); p.fill(255,255,255,6); p.rect(0,0,p.width,p.height); p.pop();

        drawAxes();
        drawStack(state.n);

        // Title
        p.push();
        p.noStroke();
        p.fill('#e5e7eb');
        p.textSize(13);
        p.textAlign(p.LEFT, p.BOTTOM);
        p.text('Stacked areas for k = 1..' + state.n, mapX(0) + 6, mapY(Y_MAX) + 16);
        p.pop();
      };
    };

    // Initialize everything when DOM is ready
    document.addEventListener('DOMContentLoaded', () => {
      setupControls();

      // Create p5 instances in instance mode
      const leftInstance = new p5(functionPlot);
      const rightInstance = new p5(sumPlot);
      state.sketches.left = leftInstance;
      state.sketches.right = rightInstance;

      // Initial typeset for formula label
      if (window.MathJax && MathJax.typesetPromise) {
        MathJax.typesetPromise();
      } else if (window.MathJax && MathJax.typeset) {
        MathJax.typeset();
      }

      setupResizeHandlers(leftInstance, rightInstance);
    });
  </script>
</body>
</html>
