  <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
  <script>
    // Global state
    let currentModel = 'monod';     // 'monod' or 'si'
    let currentView  = 'bifurcation'; // 'bifurcation' or 'nontrivial'

    const params = {
      x2f: 4.0,
      umax: 0.6,
      km: 0.12,
      k1: 0.4545,
      ds: 0.6
    };

    const decimals = {
      x2f: 1,
      umax: 2,
      km: 2,
      k1: 4,
      ds: 2
    };

    function fmt(val, key){
      const d = decimals[key] || 2;
      return Number(val).toFixed(d);
    }

    function updateValueDisplays(){
      document.getElementById('value-x2f').textContent = fmt(params.x2f, 'x2f');
      document.getElementById('value-umax').textContent = fmt(params.umax, 'umax');
      document.getElementById('value-km').textContent = fmt(params.km, 'km');
      document.getElementById('value-k1').textContent = fmt(params.k1, 'k1');
      document.getElementById('value-ds').textContent = fmt(params.ds, 'ds');
    }

    function setupSliders(){
      const s_x2f = document.getElementById('slider-x2f');
      const s_umax = document.getElementById('slider-umax');
      const s_km = document.getElementById('slider-km');
      const s_k1 = document.getElementById('slider-k1');
      const s_ds = document.getElementById('slider-ds');

      const bind = (sliderEl, key) => {
        sliderEl.addEventListener('input', () => {
          params[key] = parseFloat(sliderEl.value);
          updateValueDisplays();
          updatePlot();
        });
      };

      bind(s_x2f, 'x2f');
      bind(s_umax, 'umax');
      bind(s_km, 'km');
      bind(s_k1, 'k1');
      bind(s_ds, 'ds');

      updateValueDisplays();
    }

    // Model selector buttons
    function setupButtons(){
      const btnMonod = document.getElementById('btn-monod');
      const btnSI = document.getElementById('btn-si');
      const btnBif = document.getElementById('btn-bifurcation');
      const btnNon = document.getElementById('btn-nontrivial');

      btnMonod.addEventListener('click', () => {
        currentModel = 'monod';
        btnMonod.classList.add('active');
        btnSI.classList.remove('active');
        // Hide k1 slider container
        const cont = document.getElementById('slider-k1').parentElement;
        cont.style.display = 'none';
        updatePlot();
      });
      btnSI.addEventListener('click', () => {
        currentModel = 'si';
        btnSI.classList.add('active');
        btnMonod.classList.remove('active');
        // Show k1 slider container
        const cont = document.getElementById('slider-k1').parentElement;
        cont.style.display = '';
        updatePlot();
      });

      btnBif.addEventListener('click', () => {
        currentView = 'bifurcation';
        btnBif.classList.add('active');
        btnNon.classList.remove('active');
        updatePlot();
      });
      btnNon.addEventListener('click', () => {
        currentView = 'nontrivial';
        btnNon.classList.add('active');
        btnBif.classList.remove('active');
        updatePlot();
      });
    }

    // Kinetic functions
    function mu(x){ // specific growth rate as function of substrate x2
      const {umax, km, k1} = params;
      if (currentModel === 'si'){
        return umax * x / (km + x + k1 * x * x);
      } else {
        return umax * x / (km + x);
      }
    }
    function D_of_x2(x){ // same as mu(x) in these nondimensional definitions
      return mu(x);
    }

    // Numeric helpers
    function derivative(f, x, h){
      const hh = h || Math.max(1e-6, 1e-6 * (1 + Math.abs(x)));
      const x1 = Math.max(0, x - hh);
      const x2 = x + hh;
      return (f(x2) - f(x1)) / (x2 - x1);
    }

    function bisection(f, a, b, tol=1e-8, maxIter=64){
      let fa = f(a), fb = f(b);
      if (!isFinite(fa) || !isFinite(fb)) return null;
      if (fa === 0) return a;
      if (fb === 0) return b;
      if (fa * fb > 0) return null;
      let left = a, right = b, mid = 0;
      for (let i=0;i<maxIter;i++){
        mid = 0.5*(left+right);
        const fm = f(mid);
        if (!isFinite(fm)) return null;
        if (Math.abs(fm) < tol || (right-left)/2 < tol) return mid;
        if (fa * fm <= 0){
          right = mid; fb = fm;
        } else {
          left = mid; fa = fm;
        }
      }
      return mid;
    }

    function findRootsScan(f, a, b, n=800){
      const xs = [];
      const roots = [];
      const fs = [];
      for (let i=0; i<=n; i++){
        const x = a + (b - a) * (i / n);
        xs.push(x);
        fs.push(f(x));
      }
      for (let i=0; i<n; i++){
        const f1 = fs[i], f2 = fs[i+1];
        if (!isFinite(f1) || !isFinite(f2)) continue;
        if (f1 === 0){
          roots.push(xs[i]);
          continue;
        }
        if (f1 * f2 < 0 || f2 === 0){
          const r = bisection(f, xs[i], xs[i+1]);
          if (r != null) roots.push(r);
        }
      }
      // Deduplicate close roots
      roots.sort((a,b)=>a-b);
      const unique = [];
      for (let x of roots){
        if (unique.length === 0 || Math.abs(x - unique[unique.length-1]) > 1e-4){
          unique.push(x);
        }
      }
      return unique;
    }

    // Plotting
    function drawBifurcationDiagram(){
      const { x2f, ds } = params;

      // Sample x2 from 0 to x2f
      const N = 600;
      const x2 = [];
      const D = [];
      for (let i=0; i<=N; i++){
        const xi = x2f * (i/N);
        x2.push(xi);
        D.push(D_of_x2(xi));
      }

      const traces = [];
      // Nontrivial steady state curve: D vs x2 (x-axis = D, y-axis = x2)
      if (currentModel === 'si'){
        // Split at critical point
        const km = params.km, k1 = params.k1;
        const xcrit = Math.sqrt(Math.max(0, km / Math.max(1e-12, k1)));

        const x2_stable = [], D_stable = [];
        const x2_unstable = [], D_unstable = [];
        for (let i=0;i<x2.length;i++){
          if (x2[i] <= xcrit){
            x2_stable.push(x2[i]);
            D_stable.push(D[i]);
          } else {
            x2_unstable.push(x2[i]);
            D_unstable.push(D[i]);
          }
        }

        traces.push({
          x: D_stable, y: x2_stable, mode: 'lines', name: 'Nontrivial (stable)',
          line: {color: 'rgba(59,130,246,1)', width: 3}, hovertemplate: 'D=%{x:.3f}<br>X₂=%{y:.3f}<extra></extra>'
        });
        if (x2_unstable.length){
          traces.push({
            x: D_unstable, y: x2_unstable, mode: 'lines', name: 'Nontrivial (unstable)',
            line: {color: 'rgba(239,68,68,1)', width: 3, dash: 'dash'},
            hovertemplate: 'D=%{x:.3f}<br>X₂=%{y:.3f}<extra></extra>'
          });
        }
      } else {
        traces.push({
          x: D, y: x2, mode: 'lines', name: 'Nontrivial (stable)',
          line: {color: 'rgba(59,130,246,1)', width: 3}, hovertemplate: 'D=%{x:.3f}<br>X₂=%{y:.3f}<extra></extra>'
        });
      }

      // Trivial steady state line at y = x2f: unstable from 0..Dbif, stable from Dbif..end
      const Dbif = D_of_x2(x2f);
      const xMin = 0, xMax = 1.0; // axis range
      const xSplit = Dbif;

      const xUnstable = [], yUnstable = [];
      const xStable = [], yStable = [];
      // Build piece-wise horizontal segments within [0,1]
      if (xMin < Math.min(xMax, xSplit)){
        xUnstable.push(xMin, Math.min(xMax, xSplit));
        yUnstable.push(x2f, x2f);
      }
      if (Math.max(xMin, xSplit) < xMax){
        xStable.push(Math.max(xMin, xSplit), xMax);
        yStable.push(x2f, x2f);
      }

      if (xUnstable.length){
        traces.push({
          x: xUnstable, y: yUnstable, mode: 'lines', name: 'Trivial (unstable)',
          line: {color: 'rgba(239,68,68,1)', width: 3, dash: 'dash'},
          hovertemplate: 'D=%{x:.3f}<br>X₂=%{y:.3f}<extra></extra>'
        });
      }
      if (xStable.length){
        traces.push({
          x: xStable, y: yStable, mode: 'lines', name: 'Trivial (stable)',
          line: {color: 'rgba(59,130,246,1)', width: 3},
          hovertemplate: 'D=%{x:.3f}<br>X₂=%{y:.3f}<extra></extra>'
        });
      }

      // Vertical line at D = Ds
      const shapes = [{
        type: 'line',
        x0: params.ds, x1: params.ds, y0: 0, y1: 6.0,
        line: {color: 'rgba(148,163,184,0.8)', width: 2, dash: 'dot'}
      }];

      // Intersection points: solve D(x2) = Ds for x2 in (0, x2f)
      const f = (x) => D_of_x2(x) - params.ds;
      const roots = findRootsScan(f, 0, x2f, 1000).filter(r => isFinite(r) && r >= 0 && r <= x2f);

      const xPts = [], yPts = [], colors = [], names = [];
      for (const r of roots){
        const dDdx = derivative(D_of_x2, r, 1e-5);
        const stable = dDdx > 0; // per spec
        xPts.push(params.ds);
        yPts.push(r);
        colors.push(stable ? 'rgba(217,70,239,1)' : 'rgba(34,211,238,1)'); // magenta / cyan
        names.push(stable ? 'Nontrivial intersection (stable)' : 'Nontrivial intersection (unstable)');
      }
      if (xPts.length){
        traces.push({
          x: xPts, y: yPts, mode: 'markers',
          name: 'Nontrivial intersections',
          marker: {size: 10, color: colors, line: {width: 1, color: '#fff'}},
          hovertemplate: 'D=%{x:.3f}<br>X₂=%{y:.3f}<extra></extra>',
          showlegend: false
        });
      }

      // Trivial solution point at (Ds, x2f)
      traces.push({
        x: [params.ds], y: [x2f], mode: 'markers',
        name: 'Trivial intersection',
        marker: {size: 10, color: 'rgba(16,185,129,1)', line: {width: 1, color: '#fff'}},
        hovertemplate: 'D=%{x:.3f}<br>X₂=%{y:.3f}<extra></extra>'
      });

      const layout = {
        paper_bgcolor: 'rgba(0,0,0,0)',
        plot_bgcolor: 'rgba(0,0,0,0)',
        margin: {l: 60, r: 20, t: 40, b: 55},
        title: {text: 'Bifurcation diagram: D vs X₂', font: {color: '#e5e7eb'}},
        xaxis: {
          title: {text: 'dilution rate (D)', font: {color: '#e5e7eb'}},
          range: [0, 1.0],
          gridcolor: 'rgba(148,163,184,0.15)',
          zerolinecolor: 'rgba(148,163,184,0.2)',
          tickfont: {color: '#cbd5e1'},
          titlefont: {color: '#e5e7eb'}
        },
        yaxis: {
          title: {text: 'X₂', font: {color: '#e5e7eb'}},
          range: [0, 6.0],
          gridcolor: 'rgba(148,163,184,0.15)',
          tickfont: {color: '#cbd5e1'},
          titlefont: {color: '#e5e7eb'}
        },
        legend: {
          font: {color: '#e5e7eb'},
          bgcolor: 'rgba(2,6,23,0.6)',
          bordercolor: 'rgba(148,163,184,0.2)',
          borderwidth: 1
        },
        shapes: shapes
      };

      Plotly.newPlot('plot-container', traces, layout, {responsive: true, displaylogo: false});
    }

    function drawNontrivialStatePlot(){
      const traces = [];
      const xMax = 7;
      const N = 700;
      const xs = [];
      const ys = [];
      for (let i=0;i<=N;i++){
        const x = xMax * (i / N);
        xs.push(x);
        ys.push(mu(x));
      }

      // Main mu(x2) curve
      traces.push({
        x: xs, y: ys, mode: 'lines', name: 'μ(x₂)',
        line: {color: 'rgba(59,130,246,1)', width: 3},
        hovertemplate: 'x₂=%{x:.3f}<br>μ=%{y:.3f}<extra></extra>'
      });

      // Horizontal line at y = Ds
      traces.push({
        x: [0, xMax], y: [params.ds, params.ds], mode: 'lines', name: 'Dₛ',
        line: {color: 'rgba(148,163,184,0.9)', width: 2},
        hoverinfo: 'skip',
        showlegend: true
      });

      const shapes = [];
      // Bistability region for SI model only
      if (currentModel === 'si'){
        const {umax, km, k1, x2f} = params;
        const D_upper = umax / (1 + 2 * Math.sqrt(Math.max(0, k1 * km)));
        const D_lower = umax * x2f / (km + x2f + k1 * x2f * x2f);

        // Dashed red lines
        traces.push({
          x: [0, xMax], y: [D_upper, D_upper], mode: 'lines',
          name: 'Upper bound (peak μ)',
          line: {color: 'rgba(239,68,68,1)', dash: 'dash', width: 2},
          hoverinfo: 'skip'
        });
        traces.push({
          x: [0, xMax], y: [D_lower, D_lower], mode: 'lines',
          name: 'Lower bound (μ at x₂f)',
          line: {color: 'rgba(239,68,68,1)', dash: 'dash', width: 2},
          hoverinfo: 'skip'
        });

        // Shaded region between the two lines
        traces.push({
          x: [0, xMax, xMax, 0],
          y: [D_lower, D_lower, D_upper, D_upper],
          mode: 'lines',
          fill: 'toself',
          fillcolor: 'rgba(251,191,36,0.15)',
          line: {color: 'rgba(251,191,36,0)'},
          name: 'Bistability region',
          hoverinfo: 'skip'
        });
      }

      // Intersection points: solve μ(x2) = Ds
      const f = (x) => mu(x) - params.ds;
      const roots = findRootsScan(f, 0, xMax, 1500).filter(r => isFinite(r) && r >= 0 && r <= xMax);

      if (roots.length){
        const xPts = [], yPts = [], colors = [];
        for (const r of roots){
          const dmdx = derivative(mu, r, 1e-5);
          const stable = dmdx > 0;
          xPts.push(r);
          yPts.push(params.ds);
          colors.push(stable ? 'rgba(217,70,239,1)' : 'rgba(34,211,238,1)'); // magenta / cyan
        }
        traces.push({
          x: xPts, y: yPts, mode: 'markers',
          name: 'Intersections μ(x₂)=Dₛ',
          marker: {size: 10, color: colors, line: {width: 1, color: '#fff'}},
          hovertemplate: 'x₂=%{x:.3f}<br>μ=%{y:.3f}<extra></extra>',
          showlegend: false
        });
      }

      const layout = {
        paper_bgcolor: 'rgba(0,0,0,0)',
        plot_bgcolor: 'rgba(0,0,0,0)',
        margin: {l: 70, r: 20, t: 40, b: 55},
        title: {text: 'Nontrivial steady state: μ(x₂)', font: {color: '#e5e7eb'}},
        xaxis: {
          title: {text: 'substrate concentration (x₂)', font: {color: '#e5e7eb'}},
          range: [0, 7],
          gridcolor: 'rgba(148,163,184,0.15)',
          tickfont: {color: '#cbd5e1'},
          titlefont: {color: '#e5e7eb'}
        },
        yaxis: {
          title: {text: 'specific growth rate coefficient (μ)', font: {color: '#e5e7eb'}},
          range: [0, 1], // per spec
          gridcolor: 'rgba(148,163,184,0.15)',
          tickfont: {color: '#cbd5e1'},
          titlefont: {color: '#e5e7eb'}
        },
        legend: {
          font: {color: '#e5e7eb'},
          bgcolor: 'rgba(2,6,23,0.6)',
          bordercolor: 'rgba(148,163,184,0.2)',
          borderwidth: 1
        },
        shapes: shapes
      };

      Plotly.newPlot('plot-container', traces, layout, {responsive: true, displaylogo: false});
    }

    function updatePlot(){
      if (currentView === 'bifurcation'){
        drawBifurcationDiagram();
      } else {
        drawNontrivialStatePlot();
      }
    }

    // Initial setup
    window.addEventListener('DOMContentLoaded', () => {
      // Set defaults in global state
      currentModel = 'monod';
      currentView  = 'bifurcation';

      // Ensure active classes
      document.getElementById('btn-monod').classList.add('active');
      document.getElementById('btn-bifurcation').classList.add('active');

      setupSliders();
      setupButtons();

      // Hide k1 container by default (Monod)
      const k1Container = document.getElementById('slider-k1').parentElement;
      k1Container.style.display = 'none';

      updatePlot();
    });
  </script>
</body>
</html>
