  // Make a simple procedural Earth texture to avoid external assets
  function makeEarthTexture(){
    const w = 1024, h = 512;
    const c = document.createElement('canvas');
    c.width = w; c.height = h;
    const ctx = c.getContext('2d');

    // Ocean
    const grd = ctx.createLinearGradient(0,0,0,h);
    grd.addColorStop(0, '#0b3c6f');
    grd.addColorStop(1, '#0e5a9c');
    ctx.fillStyle = grd;
    ctx.fillRect(0,0,w,h);

    // Very rough "continents"
    ctx.fillStyle = '#2e8b57';
    ctx.globalAlpha = 0.9;

    function blob(cx, cy, rx, ry, rot, bumps=6){
      ctx.save();
      ctx.translate(cx, cy);
      ctx.rotate(rot || 0);
      ctx.beginPath();
      for(let i=0;i<=bumps;i++){
        const a = (i/bumps)*Math.PI*2;
        const r = 0.85 + 0.25*Math.sin(i*1.7);
        const x = Math.cos(a)*rx*r;
        const y = Math.sin(a)*ry*r;
        if(i===0) ctx.moveTo(x, y);
        else ctx.lineTo(x, y);
      }
      ctx.closePath();
      ctx.fill();
      ctx.restore();
    }

    blob(260, 230, 120, 90, 0.2);    // Americas
    blob(620, 210, 140, 100, -0.1);  // Africa/Europe
    blob(820, 230, 160, 100, 0.15);  // Asia
    blob(900, 360, 100, 70, -0.2);   // SE Asia/Australia
    blob(520, 380, 90, 60, 0.0);     // South Atlantic blob

    // Light clouds (simple noise-like strokes)
    ctx.globalAlpha = 0.08;
    ctx.strokeStyle = '#ffffff';
    for(let i=0;i<200;i++){
      const x = Math.random()*w, y = Math.random()*h;
      const len = 10 + Math.random()*40;
      ctx.lineWidth = 1 + Math.random()*2;
      ctx.beginPath();
      ctx.moveTo(x, y);
      ctx.lineTo(x+len, y + (Math.random()*8-4));
      ctx.stroke();
    }

    const tex = new THREE.CanvasTexture(c);
    tex.anisotropy = 4;
    tex.needsUpdate = true;
    return tex;
  }

  function makeLatitudeLine(latDeg, material, dashed){
    const latRad = latDeg * DEG2RAD;
    const y = Math.sin(latRad);
    const r = Math.cos(latRad);
