    // CircleGeometry lies in XY -> rotate to XZ plane
    geom.rotateX(Math.PI / 2);
    // translate upward by y
    geom.translate(0, y, 0);
    let line;
    if(dashed){
      material = material || new THREE.LineDashedMaterial({
        color: 0x2c89ff, dashSize: 0.05, gapSize: 0.03, linewidth: 1, depthTest: false
      });
      line = new THREE.Line(geom, material);
      line.computeLineDistances();
    } else {
      material = material || new THREE.LineBasicMaterial({color: 0xffffff, opacity: 0.6, transparent: true, depthTest: false});
      line = new THREE.Line(geom, material);
    }
    return line;
  }

