      font: {color: '#e9eef7'},
      margin: {l: 50, r: 20, t: 30, b: 40},
      title: {text: 'energy'},
      xaxis: {title: 'time (hr)', range: [0,24], gridcolor: '#233'},
      yaxis: {title: 'watts/m^2', range: [0,1400], gridcolor: '#233'},
      showlegend: false
    };
    Plotly.newPlot('plot-instant', instantData, instantLayout, {displayModeBar: false, responsive: true});

    const cumulativeData = [
      {x: hours, y: hours.map(_=>0), mode: 'lines', line: {color: '#e74c3c', width: 2}, name: 'equator'},
      {x: hours, y: hours.map(_=>0), mode: 'lines', line: {color: '#222', width: 2}, name: 'latitude'},
      {x: hours, y: hours.map(_=>0), mode: 'lines', line: {color: '#3498db', width: 2}, name: 'north pole'},
    ];
    const cumulativeLayout = {
      paper_bgcolor: 'rgba(0,0,0,0)',
      plot_bgcolor: '#0e1433',
      font: {color: '#e9eef7'},
      margin: {l: 50, r: 20, t: 30, b: 40},
