  }

  function energyInstant(latDeg, decDeg, hour){
    const S0 = 1361; // solar constant (W/m^2)
    const lat = latDeg * DEG2RAD;
    const dec = decDeg * DEG2RAD;
    const HRA = 15 * (hour - 12) * DEG2RAD; // Hour angle in radians
    const mu = Math.sin(lat)*Math.sin(dec) + Math.cos(lat)*Math.cos(dec)*Math.cos(HRA);
    return S0 * Math.max(0, mu);
  }

  function cumulativeFromInstant(instArray){
    let sum = 0;
    const out = [];
    for(let i=0; i<instArray.length; i++){
      sum += instArray[i]; // simple rectangular integration with 1-hour step
      out.push(sum);
    }
    return out;
  }

  function updatePlots(decDeg, latDeg, currentTime){
