      font: {color: '#e9eef7'},
      margin: {l: 50, r: 20, t: 30, b: 40},
      title: {text: 'energy'},
      xaxis: {title: 'time (hr)', range: [0,24], gridcolor: '#233'},
      yaxis: {title: 'watts/m^2', range: [0,1400], gridcolor: '#233'},
      showlegend: false
    }, {displayModeBar: false, responsive: true});

    // Update cumulative plot
    const cumulativeData = [
      {x: hours, y: cumEq, mode: 'lines', line: {color: '#e74c3c', width: 2}, name: 'equator'},
      {x: hours, y: cumLat, mode: 'lines', line: {color: '#222', width: 2}, name: 'latitude'},
      {x: hours, y: cumPole, mode: 'lines', line: {color: '#3498db', width: 2}, name: 'north pole'},
    ];
    Plotly.react('plot-cumulative', cumulativeData, {
      paper_bgcolor: 'rgba(0,0,0,0)',
      plot_bgcolor: '#0e1433',
      font: {color: '#e9eef7'},
      margin: {l: 50, r: 20, t: 30, b: 40},
      title: {text: 'energy'},
      xaxis: {title: 'time (hr)', range: [0,24], gridcolor: '#233'},
      yaxis: {title: 'watts/m^2', range: [0,14000], gridcolor: '#233'},
      showlegend: false
    }, {displayModeBar: false, responsive: true});
  }

  function update(){
    // Read slider values
    const season = parseFloat(sliders.season.value);   // [0,1]
    const latDeg = parseFloat(sliders.latitude.value); // [0,90]
    const t = parseFloat(sliders.time.value);          // [0,24]

    // Update span values
    spans.season.textContent = season.toFixed(3);
    spans.latitude.textContent = latDeg.toFixed(2) + '°';
    spans.time.textContent = t.toFixed(2) + ' h';

    // Physics calculations
    const decDeg = 23.5 * Math.cos(2 * Math.PI * season); // Declination
    spans.declination.textContent = decDeg.toFixed(2);

    const decRad = decDeg * DEG2RAD;

    // 3D Globe update
    // Rotate Earth around Y-axis based on time of day
    earthGroup.rotation.y = (t / 24) * 2 * Math.PI;

    // Sun direction vector: (0, sin δ, -cos δ)
    const sunDir = new THREE.Vector3(0, Math.sin(decRad), -Math.cos(decRad)).normalize();
    // For DirectionalLight: direction at origin is -position normalized.
    // To make direction = sunDir, set position = -sunDir * distance
    const sunDist = 10;
    sunLight.position.set(-sunDir.x * sunDist, -sunDir.y * sunDist, -sunDir.z * sunDist);
    sunLight.target.position.set(0,0,0);
    sunLight.target.updateMatrixWorld();

    // Update selected latitude dashed line
    if (selectedLatLine) {
      earthGroup.remove(selectedLatLine);
      selectedLatLine.geometry.dispose();
      if (selectedLatLine.material) selectedLatLine.material.dispose();
      selectedLatLine = null;
    }
    selectedLatLine = makeLatitudeLine(latDeg, new THREE.LineDashedMaterial({
      color: 0x2c89ff,
      dashSize: 0.05,
      gapSize: 0.03,
      linewidth: 1,
      depthTest: false
    }), true);
    earthGroup.add(selectedLatLine);

    // Surface point at chosen latitude, longitude = 0° in Earth's local frame
    const latRad = latDeg * DEG2RAD;
    const localP = new THREE.Vector3(Math.cos(latRad), Math.sin(latRad), 0); // (x = cosλ, y = sinλ, z = 0)
    const worldP = localP.clone().applyMatrix4(earthGroup.matrixWorld);

    // Surface normal vector (from center through point)
    const normalDir = worldP.clone().sub(earthGroup.position).normalize();

    // Update arrows
    normalArrow.position.copy(worldP);
    normalArrow.setDirection(normalDir);
    normalArrow.setLength(0.6);

    sunArrow.position.copy(worldP);
    sunArrow.setDirection(sunDir); // Parallel to light direction
    sunArrow.setLength(0.8);

    // Update plots
    updatePlots(decDeg, latDeg, t);
  }

  function animate(){
    requestAnimationFrame(animate);
    controls.update();
    renderer.render(scene, camera);
  }

})();
</script>
</body>
</html>
