<body>
  <div id="demo-container">
    <div class="controls">
      <span>area</span>
      <input id="slider-area" type="range" min="1" max="101" step="1" value="13" />
      <input id="input-area" type="number" min="1" max="101" step="1" value="13" />
    </div>
    <div id="canvas-container"></div>
    <div class="footnote">
      Tip: Drag the slider or enter a number. If the number is a congruent number, a rational right triangle realizing that area is shown. Otherwise a message appears.
    </div>
  </div>

  <!-- p5.js library -->
  <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
  <script>
    // -----------------------------------------------
    // Congruent number data (predefined known entries)
    // Each side is stored as fraction {n: numerator, d: denominator}
    // Numerators/denominators may be large; we keep them as strings (or numbers if small)
    // -----------------------------------------------
    const congruentData = {
      // Small canonical examples
      5:  { a: {n: "3", d: "2"}, b: {n: "20", d: "3"}, c: {n: "41", d: "6"} },
      6:  { a: {n: "3", d: "1"}, b: {n: "4", d: "1"},  c: {n: "5", d: "1"} },
      7:  { a: {n: "35", d: "12"}, b: {n: "24", d: "5"}, c: {n: "337", d: "60"} },
      13: { a: {n: "780", d: "323"}, b: {n: "323", d: "30"}, c: {n: "106921", d: "9690"} },

      // Integer right triangles that realize these areas exactly
      30: { a: {n: "5", d: "1"},  b: {n: "12", d: "1"}, c: {n: "13", d: "1"} },
      60: { a: {n: "8", d: "1"},  b: {n: "15", d: "1"}, c: {n: "17", d: "1"} },
      84: { a: {n: "7", d: "1"},  b: {n: "24", d: "1"}, c: {n: "25", d: "1"} },

      // Special 79 entry (as requested; unusually large hypotenuse)
      79: { 
        a: { n: "233126551", d: "167973000" },
        b: { n: "335946000", d: "2950969" },
        c: { n: "56434050774922081", d: "495683115837000" }
      }
    };

    // -----------------------------------------------
    // Utility: BigInt-based fraction helpers
    // -----------------------------------------------
    function toBigInt(v) {
      if (typeof v === "bigint") return v;
      if (typeof v === "number") return BigInt(v);
      if (typeof v === "string") return BigInt(v);
      return BigInt(v);
    }

    function gcdBigInt(a, b) {
      a = a < 0n ? -a : a;
      b = b < 0n ? -b : b;
      while (b !== 0n) {
        const t = b;
        b = a % b;
        a = t;
      }
      return a;
    }

    function reduceFrac(n, d) {
      n = toBigInt(n);
      d = toBigInt(d);
      if (d === 0n) return {n: 0n, d: 1n};
      if (n === 0n) return {n: 0n, d: 1n};
      const g = gcdBigInt(n, d);
      n = n / g;
      d = d / g;
      if (d < 0n) { n = -n; d = -d; }
      return {n, d};
    }

    function fracMul(a, b) {
      return reduceFrac(a.n * b.n, a.d * b.d);
    }
    function fracAdd(a, b) {
      return reduceFrac(a.n * b.d + b.n * a.d, a.d * b.d);
    }
    function fracSub(a, b) {
      return reduceFrac(a.n * b.d - b.n * a.d, a.d * b.d);
    }
    function fracToNumber(fr) {
      return Number(fr.n) / Number(fr.d);
    }

    // Format with spaces for thousands
    function formatWithSpaces(numLike) {
      const s = numLike.toString();
      const neg = s.startsWith("-");
      const t = neg ? s.slice(1) : s;
      const formatted = t.replace(/\B(?=(\d{3})+(?!\d))/g, " ");
      return neg ? "-" + formatted : formatted;
    }

    // -----------------------------------------------
    // Rational search precomputation
    // We search for rational u, v with small numerators/denominators
    // such that area = uv(u^2 - v^2) is a positive integer in [1..101].
    // This gradually fills 'congruentData' with many entries.
    // -----------------------------------------------
    const PRECOMP_LIMIT_NUM = 36; // numerators up to 36
    const PRECOMP_LIMIT_DEN = 36; // denominators up to 36
    const FOUND_AREAS = new Set(Object.keys(congruentData).map(Number));

    // Build a list of reduced positive rationals p/q
    function buildRationalList(maxNum, maxDen) {
      const list = [];
      for (let p = 1; p <= maxNum; p++) {
        for (let q = 1; q <= maxDen; q++) {
          if (gcdInt(p, q) === 1) {
            list.push({
              n: BigInt(p),
              d: BigInt(q),
              val: p / q
            });
          }
        }
      }
      list.sort((a, b) => a.val - b.val);
      return list;
    }

    function gcdInt(a, b) {
      let x = Math.abs(a), y = Math.abs(b);
      while (y !== 0) { const t = y; y = x % y; x = t; }
      return x;
    }

    const rationals = buildRationalList(PRECOMP_LIMIT_NUM, PRECOMP_LIMIT_DEN);

    // Iterate pairs (u>v) and record triangles for integer areas in [1..101]
    let precomp_i = 0, precomp_j = 0, precompDone = false;

    function stepPrecompute(batch = 2000) {
      if (precompDone) return;
      const len = rationals.length;
      let count = 0;
      while (precomp_i < len) {
        while (precomp_j < precomp_i) {
          const u = rationals[precomp_i];
          const v = rationals[precomp_j];
          precomp_j++;

          if (u.val <= v.val) continue; // ensure u > v

          // Compute area = uv(u^2 - v^2) exactly with BigInt:
          // num = pu*pv*(pu^2*qv^2 - pv^2*qu^2), den = qu^3*qv^3
          const pu = u.n, qu = u.d, pv = v.n, qv = v.d;
          const pu2 = pu * pu, pv2 = pv * pv, qu2 = qu * qu, qv2 = qv * qv;
          const qu3 = qu2 * qu, qv3 = qv2 * qv;

          const diff = pu2 * qv2 - pv2 * qu2; // (pu^2*qv^2 - pv^2*qu^2)
          if (diff <= 0n) continue; // ensure positive area

          const num = pu * pv * diff;
          const den = qu3 * qv3;

          // Area integer?
          if (num % den !== 0n) {
            count++; if (count >= batch) { schedulePrecompute(); return; }
            continue;
          }
          const area = Number(num / den);
          if (area >= 1 && area <= 101 && !FOUND_AREAS.has(area)) {
            // Build sides:
            // a = u^2 - v^2 = (pu^2*qv^2 - pv^2*qu^2) / (qu^2*qv^2)
            // b = 2*u*v = (2*pu*pv) / (qu*qv)
            // c = u^2 + v^2 = (pu^2*qv^2 + pv^2*qu^2) / (qu^2*qv^2)
            const a_num = diff;
            const a_den = qu2 * qv2;
            const b_num = 2n * pu * pv;
            const b_den = qu * qv;
            const c_num = pu2 * qv2 + pv2 * qu2;
            const c_den = a_den;

            const aR = reduceFrac(a_num, a_den);
            const bR = reduceFrac(b_num, b_den);
            const cR = reduceFrac(c_num, c_den);

            congruentData[area] = {
              a: { n: aR.n.toString(), d: aR.d.toString() },
              b: { n: bR.n.toString(), d: bR.d.toString() },
              c: { n: cR.n.toString(), d: cR.d.toString() }
            };
            FOUND_AREAS.add(area);
          }

          count++;
          if (count >= batch) { schedulePrecompute(); return; }
        }
        precomp_i++;
        precomp_j = 0;
      }
      // Ensure special 79 override persists
      congruentData[79] = { 
        a: { n: "233126551", d: "167973000" },
        b: { n: "335946000", d: "2950969" },
        c: { n: "56434050774922081", d: "495683115837000" }
      };
      precompDone = true;
    }

    function schedulePrecompute() {
      // Use setTimeout to yield to UI
      setTimeout(() => stepPrecompute(), 0);
    }

    // Kick off precomputation lazily after page load
    window.addEventListener('load', () => {
      schedulePrecompute();
    });

    // -----------------------------------------------
    // p5.js sketch
    // -----------------------------------------------
    let cnv;
    let currentN = 13;

    function setup() {
      const container = document.getElementById('canvas-container');
      const w = container.clientWidth;
      const h = container.clientHeight;
      cnv = createCanvas(w, h);
      cnv.parent('canvas-container');
      pixelDensity(window.devicePixelRatio || 1);
      textFont('Georgia, serif');

      // Initial draw
      currentN = getCurrentAreaValue();
      drawScene(currentN);
    }

    function windowResized() {
      const container = document.getElementById('canvas-container');
      const w = container.clientWidth;
      const h = container.clientHeight;
      resizeCanvas(w, h);
      drawScene(currentN);
    }

    function clearCanvas() {
      background(255);
    }

    function getTriangleForArea(n) {
      const key = String(n);
      if (congruentData[key]) {
        return congruentData[key];
      }
      return null;
    }

    function drawScene(n) {
      clearCanvas();

      const tri = getTriangleForArea(n);
      if (!tri) {
        // Not congruent (or not yet found)
        noStroke();
        fill('#FF0000');
        textAlign(CENTER, CENTER);
        textSize(20);
        text(`${n} is not a congruent number`, width / 2, height / 2);
        return;
      }

      // Extract fractions and convert to numbers for geometry
      const aF = {
        n: toBigInt(tri.a.n),
        d: toBigInt(tri.a.d)
      };
      const bF = {
        n: toBigInt(tri.b.n),
        d: toBigInt(tri.b.d)
      };
      const cF = {
        n: toBigInt(tri.c.n),
        d: toBigInt(tri.c.d)
      };

      const aVal = fracToNumber(aF);
      const bVal = fracToNumber(bF);

      // Scaling
      const pad = 60;
      const scaleFactor = Math.min(
        (width - 2 * pad) / aVal,
        (height - 2 * pad) / bVal
      );

      const A = aVal * scaleFactor;
      const B = bVal * scaleFactor;

      // Centering: right angle at bottom-left of triangle
      const x0 = (width - A) / 2;
      const y0 = (height + B) / 2;

      // Draw triangle
      stroke('#000000');
      strokeWeight(2);
      fill('#FFF2E5');
      beginShape();
      vertex(x0, y0);           // right angle
      vertex(x0 + A, y0);       // along base
      vertex(x0, y0 - B);       // up vertical
      endShape(CLOSE);

      // Draw labels as fractions
      const brown = '#8B5A2B';
      const fontSize = 16;

      // Base (a): centered below the base
      drawFraction(aF, x0 + A / 2, y0 + 24, 0, fontSize, brown);

      // Height (b): to the right of the vertical side
      drawFraction(bF, x0 + 30, y0 - B / 2, 0, fontSize, brown);

      // Hypotenuse (c): along and offset from hypotenuse, rotated parallel
      // Hypotenuse endpoints: P1 = (x0, y0 - B), P2 = (x0 + A, y0)
      const hx1 = x0, hy1 = y0 - B;
      const hx2 = x0 + A, hy2 = y0;
      const mx = (hx1 + hx2) / 2;
      const my = (hy1 + hy2) / 2;
      const dx = hx2 - hx1, dy = hy2 - hy1;
      const hypAngle = Math.atan2(dy, dx);
      // Offset outward perpendicular to hypotenuse
      const len = Math.hypot(dx, dy);
      let ox = 0, oy = 0;
      if (len > 0) {
        const nx = -dy / len;
        const ny = dx / len;
        const offset = 26;
        ox = nx * offset;
        oy = ny * offset;
      }
      drawFraction(cF, mx + ox, my + oy, hypAngle, fontSize, brown);
    }

    function drawFraction(fr, x, y, rotation, fontSize, color = '#8B5A2B') {
      push();
      translate(x, y);
      rotate(rotation);
      textAlign(CENTER, CENTER);
      fill(color);
      noStroke();
      textSize(fontSize);

      const numStr = formatWithSpaces(fr.n);
      const denStr = formatWithSpaces(fr.d);

      const numW = textWidth(numStr);
      const denW = textWidth(denStr);
      const barW = Math.max(numW, denW) + 4;

      const lineGap = fontSize * 0.65;

      // Numerator
      text(numStr, 0, -lineGap);
      // Denominator
      text(denStr, 0, lineGap);

      // Fraction bar
      stroke(color);
      strokeWeight(1.5);
      line(-barW / 2, 0, barW / 2, 0);
      pop();
    }

    // -----------------------------------------------
    // Controls (synchronization and interaction)
    // -----------------------------------------------
    const slider = document.getElementById('slider-area');
    const numeric = document.getElementById('input-area');

    function getCurrentAreaValue() {
      const v = Math.max(1, Math.min(101, parseInt(slider.value, 10) || 1));
      return v;
    }

    function updateFromSlider() {
      const n = Math.max(1, Math.min(101, parseInt(slider.value, 10) || 1));
      numeric.value = String(n);
      currentN = n;
      drawScene(currentN);
    }

    function updateFromNumber() {
      let n = parseInt(numeric.value, 10);
      if (!Number.isFinite(n)) n = 1;
      n = Math.max(1, Math.min(101, n));
      numeric.value = String(n);
      slider.value = String(n);
      currentN = n;
      drawScene(currentN);
    }

    slider.addEventListener('input', updateFromSlider);
    numeric.addEventListener('input', updateFromNumber);
  </script>
</body>
</html>
