    </div>

</div>

<!-- three.js and helpers -->
<script src="https://cdn.jsdelivr.net/npm/three@0.152.2/build/three.min.js"></script>
<script src="https://cdn.jsdelivr.net/npm/three@0.152.2/examples/js/controls/OrbitControls.js"></script>
<script src="https://cdn.jsdelivr.net/npm/three@0.152.2/examples/js/geometries/ParametricGeometry.js"></script>
<script>
(function(){
    // Constants and defaults
    const DEFAULTS = {
        alpha: 0.5, beta: 0.5,
        w: 1.0, r: 1.0,
        Q: 3.0, C: 4.0
    };
    const LIMITS = {
        X_MAX: 10, Y_MAX: 10, Z_MAX: 10 // Z is C in primal, Q in dual
    };

    // Elements
    const el = {
        alpha: document.getElementById('slider-alpha'),
        beta: document.getElementById('slider-beta'),
        w: document.getElementById('slider-w'),
        r: document.getElementById('slider-r'),
        labAlpha: document.getElementById('label-alpha'),
        labBeta: document.getElementById('label-beta'),
        labW: document.getElementById('label-w'),
        labR: document.getElementById('label-r'),
        Q: document.getElementById('slider-Q'),
        C: document.getElementById('slider-C'),
        labQ: document.getElementById('label-Q'),
        labC: document.getElementById('label-C'),
        btnReset: document.getElementById('btn-reset'),
        btnQFromDual: document.getElementById('btn-Q-from-dual'),
        btnCFromPrimal: document.getElementById('btn-C-from-primal'),
        outPrimal: document.getElementById('output-primal'),
        outDual: document.getElementById('output-dual'),
        plotPrimal: document.getElementById('plot-primal'),
        plotDual: document.getElementById('plot-dual')
    };

    // Set slider attributes
    function setSliderAttributes(){
        const setup = (input, {min,max,step,value})=>{
            input.min = min; input.max = max; input.step = step; input.value = value;
        };
        setup(el.alpha, {min:0.1,max:1.0,step:0.01,value:DEFAULTS.alpha});
        setup(el.beta,  {min:0.1,max:1.0,step:0.01,value:DEFAULTS.beta});
        setup(el.w,     {min:0.1,max:2.0,step:0.01,value:DEFAULTS.w});
        setup(el.r,     {min:0.1,max:2.0,step:0.01,value:DEFAULTS.r});
        setup(el.Q,     {min:1.0,max:10.0,step:0.1,value:DEFAULTS.Q});
        setup(el.C,     {min:1.0,max:10.0,step:0.1,value:DEFAULTS.C});
    }
    setSliderAttributes();

    // Three.js scenes
    const scenePrimal = new THREE.Scene();
    const sceneDual = new THREE.Scene();
    scenePrimal.background = new THREE.Color(0x0a0f1a);
    sceneDual.background = new THREE.Color(0x0a0f1a);

    const rendererPrimal = new THREE.WebGLRenderer({antialias:true});
    const rendererDual = new THREE.WebGLRenderer({antialias:true});
    rendererPrimal.setPixelRatio(Math.min(window.devicePixelRatio, 2));
    rendererDual.setPixelRatio(Math.min(window.devicePixelRatio, 2));
    el.plotPrimal.appendChild(rendererPrimal.domElement);
    el.plotDual.appendChild(rendererDual.domElement);

    // Cameras
    const cameraPrimal = new THREE.PerspectiveCamera(45, 1, 0.1, 100);
    const cameraDual   = new THREE.PerspectiveCamera(45, 1, 0.1, 100);
    cameraPrimal.position.set(14, 14, 14);
    cameraDual.position.set(14, 14, 14);

    const controlsPrimal = new THREE.OrbitControls(cameraPrimal, rendererPrimal.domElement);
    const controlsDual   = new THREE.OrbitControls(cameraDual, rendererDual.domElement);
    [controlsPrimal, controlsDual].forEach(c=>{
        c.enableDamping = true;
        c.dampingFactor = 0.08;
        c.target.set(5,5,4);
    });

    // Helpers (axes + bounding box)
    function addCommonHelpers(scene){
        const axes = new THREE.AxesHelper(10);
        scene.add(axes);

        const box = new THREE.Box3(
            new THREE.Vector3(0,0,0),
            new THREE.Vector3(LIMITS.X_MAX, LIMITS.Y_MAX, LIMITS.Z_MAX)
        );
        const boxHelper = new THREE.Box3Helper(box, new THREE.Color(0x334166));
        scene.add(boxHelper);

        const amb = new THREE.AmbientLight(0xffffff, 0.8);
        scene.add(amb);
        const dir = new THREE.DirectionalLight(0xffffff, 0.7);
        dir.position.set(8,12,10);
        scene.add(dir);
    }
    addCommonHelpers(scenePrimal);
    addCommonHelpers(sceneDual);

    // Dynamic groups to refresh each update
    const dynamicPrimal = new THREE.Group();
    const dynamicDual = new THREE.Group();
    scenePrimal.add(dynamicPrimal);
    sceneDual.add(dynamicDual);

    // Resize handling
    function resizeRenderers(){
        const setSize = (container, renderer, camera)=>{
            const rect = container.getBoundingClientRect();
            const w = Math.max(200, rect.width);
            const h = Math.max(200, rect.height);
            renderer.setSize(w, h, false);
            camera.aspect = w / h;
            camera.updateProjectionMatrix();
        };
        setSize(el.plotPrimal, rendererPrimal, cameraPrimal);
        setSize(el.plotDual, rendererDual, cameraDual);
    }
    window.addEventListener('resize', resizeRenderers);
    resizeRenderers();

    // Utility
    const fmt = (v)=> Number.isFinite(v) ? v.toFixed(2) : '—';

    function clearGroup(g){
        for(let i=g.children.length-1;i>=0;i--){
            const obj = g.children[i];
            g.remove(obj);
            if (obj.geometry) obj.geometry.dispose();
            if (obj.material){
                if (Array.isArray(obj.material)) obj.material.forEach(m=>m.dispose());
                else obj.material.dispose();
            }
        }
    }

    // Build orange "curtain" from polyline (vertical strip)
    function buildVerticalCurtainPolyline(points2D, zTop, color=0xff8c42, opacity=0.8){
        if (points2D.length < 2) return null;
        const positions = [];
        const normals = [];
        const uvs = [];
        const indices = [];

        // Triangulate strip between consecutive points
        for (let i=0;i<points2D.length-1;i++){
            const [x1,y1] = points2D[i];
            const [x2,y2] = points2D[i+1];

            // Four vertices: A(x1,y1,0), B(x2,y2,0), A'(x1,y1,zTop), B'(x2,y2,zTop)
            const baseIndex = positions.length/3;

            const pushV = (x,y,z)=>{ positions.push(x,y,z); uvs.push(x/10, y/10); };

            pushV(x1,y1,0);
            pushV(x2,y2,0);
            pushV(x1,y1,zTop);
            pushV(x2,y2,zTop);

            // Two triangles: A B A'  and  B B' A'
            indices.push(baseIndex+0, baseIndex+1, baseIndex+2);
            indices.push(baseIndex+1, baseIndex+3, baseIndex+2);
        }

        const geom = new THREE.BufferGeometry();
        geom.setAttribute('position', new THREE.Float32BufferAttribute(positions, 3));
        geom.setAttribute('uv', new THREE.Float32BufferAttribute(uvs, 2));
        geom.setIndex(indices);
        geom.computeVertexNormals();

        const mat = new THREE.MeshPhongMaterial({
            color: color,
            emissive: 0x331d08,
            side: THREE.DoubleSide,
            transparent: true,
            opacity
        });
        return new THREE.Mesh(geom, mat);
    }

    // Build red polyline curve
    function buildLine(points3D, color=0xff4545){
        const geom = new THREE.BufferGeometry();
        const pos = [];
        for(const p of points3D){ pos.push(p.x, p.y, p.z); }
        geom.setAttribute('position', new THREE.Float32BufferAttribute(pos, 3));
        const mat = new THREE.LineBasicMaterial({ color, linewidth: 2 });
        return new THREE.Line(geom, mat);
    }

    // Cost plane as rotated PlaneGeometry to match z = w x + r y
    function buildCostPlane(w, r, size=14, color=0xcccccc, opacity=0.35){
        const geom = new THREE.PlaneGeometry(size, size, 1, 1);
        const mat = new THREE.MeshPhongMaterial({
            color: color,
            transparent: true,
            opacity: opacity,
            side: THREE.DoubleSide
        });
        const mesh = new THREE.Mesh(geom, mat);

        // Default plane normal is (0,0,1). Target normal for z = w x + r y is n = normalize(-w, -r, 1).
        const n = new THREE.Vector3(-w, -r, 1).normalize();
        const zAxis = new THREE.Vector3(0,0,1);
        const q = new THREE.Quaternion().setFromUnitVectors(zAxis, n);
        mesh.quaternion.copy(q);

        // Center stays at origin to satisfy z=wx+ry plane (passes through 0).
        return mesh;
    }

    // Production surface Q = x^a * y^b as ParametricGeometry
    function buildProductionSurface(alpha, beta, xMax, yMax, color=0xff8c42){
        const slices = 64, stacks = 64;
        const func = function(u, v, target){
            const x = u * xMax;
            const y = v * yMax;
            const z = (x>0 && y>0) ? Math.pow(x, alpha) * Math.pow(y, beta) : 0;
            target.set(x, y, Math.min(z, LIMITS.Z_MAX));
        };
        const geom = new THREE.ParametricGeometry(func, slices, stacks);
        const mat = new THREE.MeshPhongMaterial({
            color: color,
            emissive: 0x371f08,
            shininess: 30,
            side: THREE.DoubleSide
        });
        return new THREE.Mesh(geom, mat);
    }

    // Render/update function
    let lastPrimalC = DEFAULTS.w*3 + DEFAULTS.r*3; // for cross-set buttons
    let lastDualQ = 2.0;

    function update(){
        const alpha = parseFloat(el.alpha.value);
        const beta  = parseFloat(el.beta.value);
        const w     = parseFloat(el.w.value);
        const r     = parseFloat(el.r.value);
        const Qin   = parseFloat(el.Q.value);
        const Cin   = parseFloat(el.C.value);

        // Update labels
        el.labAlpha.textContent = alpha.toFixed(2);
        el.labBeta.textContent  = beta.toFixed(2);
        el.labW.textContent     = w.toFixed(2);
        el.labR.textContent     = r.toFixed(2);
        el.labQ.textContent     = Qin.toFixed(2);
        el.labC.textContent     = Cin.toFixed(2);

        // PRIMAL: minimize cost for given Q
        const k = (w * beta) / (r * alpha);
        const xp = Math.pow(Qin / Math.pow(k, beta), 1 / (alpha + beta));
        const yp = xp * k;
        const Cout = w * xp + r * yp;

        // DUAL: maximize Q for given C
        const xd = (Cin * alpha) / (w * (alpha + beta));
        const yd = (Cin * beta)  / (r * (alpha + beta));
        const Qout = Math.pow(Math.max(xd,0), alpha) * Math.pow(Math.max(yd,0), beta);

        lastPrimalC = Cout;
        lastDualQ = Qout;

        // Update outputs
        el.outPrimal.textContent = `x = ${fmt(xp)}, y = ${fmt(yp)}, C = ${fmt(Cout)}`;
        el.outDual.textContent   = `x = ${fmt(xd)}, y = ${fmt(yd)}, Q = ${fmt(Qout)}`;

        // Rebuild visuals
        clearGroup(dynamicPrimal);
        clearGroup(dynamicDual);

        // PRIMAL PLOT:
        // Isoquant curtain base: y = (Q / x^alpha)^(1/beta), vertical over C-axis
        const xMin = 0.1, xMax = LIMITS.X_MAX, yMax = LIMITS.Y_MAX;
        const samples = 120;
        const basePoints = [];
        for (let i=0;i<=samples;i++){
            const x = xMin + (xMax - xMin) * (i / samples);
            const denom = Math.pow(x, alpha);
            if (denom <= 0) continue;
            let y = Math.pow(Qin / denom, 1 / beta);
            if (!Number.isFinite(y) || y < 0) continue;
            if (y <= yMax) basePoints.push([x, y]);
        }
        const curtainIsoquant = buildVerticalCurtainPolyline(basePoints, LIMITS.Z_MAX, 0xff8c42, 0.85);
        if (curtainIsoquant) dynamicPrimal.add(curtainIsoquant);

        // Isocost plane z = w x + r y
        const plane = buildCostPlane(w, r, 18, 0xdddddd, 0.30);
        dynamicPrimal.add(plane);

        // Blue solution vector from origin to (xp, yp, Cout)
        const lineGeom = new THREE.BufferGeometry().setFromPoints([
            new THREE.Vector3(0,0,0),
            new THREE.Vector3(Math.min(xp, LIMITS.X_MAX), Math.min(yp, LIMITS.Y_MAX), Math.min(Cout, LIMITS.Z_MAX))
        ]);
        const lineMat = new THREE.LineBasicMaterial({color: 0x4aa3ff, linewidth: 3});
        const solLine = new THREE.Line(lineGeom, lineMat);
        dynamicPrimal.add(solLine);

        // Small marker at optimal base point projected to plane
        const markerGeom1 = new THREE.SphereGeometry(0.12, 16, 16);
        const markerMat1 = new THREE.MeshPhongMaterial({color: 0x4aa3ff, emissive: 0x0a2248});
        const marker1 = new THREE.Mesh(markerGeom1, markerMat1);
        marker1.position.set(Math.min(xp,LIMITS.X_MAX), Math.min(yp,LIMITS.Y_MAX), Math.min(Cout, LIMITS.Z_MAX));
        dynamicPrimal.add(marker1);

        // DUAL PLOT:
        // Production surface Q = x^alpha y^beta
        const prodSurface = buildProductionSurface(alpha, beta, LIMITS.X_MAX, LIMITS.Y_MAX, 0xff8c42);
        dynamicDual.add(prodSurface);

        // Budget curtain base: y = (C - w x) / r
        const baseBudget = [];
        const maxXBudget = Math.min(Cin / Math.max(w, 1e-6), LIMITS.X_MAX);
        for (let i=0;i<=samples;i++){
            const x = (maxXBudget) * (i / samples);
            let y = (Cin - w*x)/r;
            if (y >= 0 && y <= yMax) baseBudget.push([x, y]);
        }
        const curtainBudget = buildVerticalCurtainPolyline(baseBudget, LIMITS.Z_MAX, 0xaaaaaa, 0.35);
        if (curtainBudget) dynamicDual.add(curtainBudget);

        // Red intersection curve of surface with budget line
        const curvePoints = [];
        for (let i=0;i<=samples;i++){
            const x = (maxXBudget) * (i / samples);
            let y = (Cin - w*x)/r;
            if (y < 0 || y > yMax) continue;
            const z = Math.min(Math.pow(Math.max(x,0), alpha) * Math.pow(Math.max(y,0), beta), LIMITS.Z_MAX);
            curvePoints.push(new THREE.Vector3(x, y, z));
        }
        if (curvePoints.length >= 2){
            const curve = buildLine(curvePoints, 0xff4d4d);
            dynamicDual.add(curve);
        }

        // Optimal point marker on dual: (xd, yd, Qout)
        const markerGeom2 = new THREE.SphereGeometry(0.14, 20, 20);
        const markerMat2 = new THREE.MeshPhongMaterial({color: 0x999999, emissive: 0x202020});
        const marker2 = new THREE.Mesh(markerGeom2, markerMat2);
        marker2.position.set(Math.min(xd,LIMITS.X_MAX), Math.min(yd,LIMITS.Y_MAX), Math.min(Qout, LIMITS.Z_MAX));
        dynamicDual.add(marker2);
    }

    // Event bindings
    const sliders = [el.alpha, el.beta, el.w, el.r, el.Q, el.C];
    sliders.forEach(s => s.addEventListener('input', update));
    el.btnReset.addEventListener('click', ()=>{
        setSliderAttributes();
        // Reset labels to initial textual defaults as specified
        el.labAlpha.textContent = "0.5";
        el.labBeta.textContent  = "0.5";
        el.labW.textContent     = "1";
        el.labR.textContent     = "1";
        el.labQ.textContent     = "3";
        el.labC.textContent     = "4";
        update();
    });
    el.btnCFromPrimal.addEventListener('click', ()=>{
        el.C.value = Math.max(parseFloat(el.C.min), Math.min(parseFloat(el.C.max), lastPrimalC));
        update();
    });
    el.btnQFromDual.addEventListener('click', ()=>{
        el.Q.value = Math.max(parseFloat(el.Q.min), Math.min(parseFloat(el.Q.max), lastDualQ));
        update();
    });

    // Initial draw
    update();

    // Animate
    function animate(){
        requestAnimationFrame(animate);
        controlsPrimal.update();
        controlsDual.update();
        rendererPrimal.render(scenePrimal, cameraPrimal);
        rendererDual.render(sceneDual, cameraDual);
    }
    animate();
})();
</script>
</body>
</html>
