<script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>

<script>
/* ======== Global State and Constants ======== */
const components = [
    { name: 'n-butane',  A: 6.80165, B: 1005.157, C: -27.159, Cp: 132,  Hvap: 22400 },
    { name: 'n-pentane', A: 6.86177, B: 1184.249, C: -35.223, Cp: 161,  Hvap: 25800 },
    { name: 'n-heptane', A: 6.90237, B: 1358.59,  C: -51.303, Cp: 225,  Hvap: 31800 },
    { name: 'n-octane',  A: 6.92383, B: 1431.149, C: -59.501, Cp: 255,  Hvap: 34400 },
];

const z_feed = [0.25, 0.25, 0.25, 0.25]; // feed composition
const F_kmol_hr = 10;     // kmol/hr
const T_feed = 323.2;     // K
const P_feed = 1000;      // kPa

const state = {
    P_flash: 180,  // kPa
    Q_hr: 0,       // kJ/hr
    result: null
};

/* ======== Utility Functions ======== */
function clamp(v, lo, hi) { return Math.max(lo, Math.min(hi, v)); }
function sum(arr) { return arr.reduce((a, b) => a + b, 0); }
function normalize(arr) {
    const s = sum(arr);
    if (s === 0 || !isFinite(s)) return arr.map(_ => 0);
    return arr.map(v => v / s);
}
function psat_kPa(T, A, B, C) {
    // Antoine equation: P_sat (kPa) = 10^(A - B/(T + C))
    return Math.pow(10, (A - (B / (T + C))));
}

function formatNumber(val, digits = 3) {
    if (!isFinite(val)) return '—';
    return Number(val).toFixed(digits);
}

function rrFunction(V, K) {
    // Rachford-Rice residual for given V and K-values
    // f(V) = Σ z_i (K_i - 1) / (1 + V (K_i - 1))
    const eps = 1e-12;
    V = clamp(V, 0 + 1e-10, 1 - 1e-10);
    let total = 0;
    for (let i = 0; i < components.length; i++) {
        const Ki = K[i];
        const denom = 1 + V * (Ki - 1);
        // Prevent division by zero
        const safeDenom = Math.abs(denom) < eps ? (denom >= 0 ? eps : -eps) : denom;
        total += z_feed[i] * (Ki - 1) / safeDenom;
    }
    return total;
}

function solveV_givenT(T, P) {
    // Compute K-values at T, P
    const K = components.map(c => psat_kPa(T, c.A, c.B, c.C) / P);

    // Check for solution existence: f(0) and f(1)
    const f0 = z_feed.reduce((acc, z_i, i) => acc + z_i * (K[i] - 1), 0);
    const f1 = z_feed.reduce((acc, z_i, i) => acc + z_i * (K[i] - 1) / (K[i]), 0); // at V=1: denom = K_i
    let V;

    if (isNaN(f0) || isNaN(f1)) {
        // Fallback: treat as no vapor
        V = 0;
    } else if (f0 === 0) {
        V = 0;
    } else if (f1 === 0) {
        V = 1;
    } else if (f0 * f1 > 0) {
        // No root in (0,1): choose boundary based on sign
        // If sum(K-1) < 0 then V = 0 (all K < 1), else V = 1
        V = (f0 < 0) ? 0 : 1;
    } else {
        // Bisection on [0,1]
        let lo = 0, hi = 1;
        let flo = f0;
        let fhi = f1;
        for (let iter = 0; iter < 100; iter++) {
            const mid = 0.5 * (lo + hi);
            const fmid = rrFunction(mid, K);
            if (Math.abs(fmid) < 1e-10 || (hi - lo) < 1e-8) {
                V = mid;
                break;
            }
            if (flo * fmid > 0) {
                lo = mid;
                flo = fmid;
            } else {
                hi = mid;
                fhi = fmid;
            }
            V = mid;
        }
    }

    V = clamp(V, 0, 1);

    // Compute phase compositions
    const x = [];
    const y = [];
    for (let i = 0; i < components.length; i++) {
        const Ki = K[i];
        const denom = 1 + V * (Ki - 1);
        const xi = z_feed[i] / denom;
        const yi = Ki * xi;
        x.push(xi);
        y.push(yi);
    }
    // Normalize to avoid rounding drift
    const xN = normalize(x);
    const yN = normalize(y);

    return { V, K, x: xN, y: yN };
}

function energyResidual(T, P, Q_hr) {
    const Q_per_kmol = Q_hr / F_kmol_hr; // kJ/kmol
    const { V, x, y } = solveV_givenT(T, P);
    const sum_z_Cp = components.reduce((acc, c, i) => acc + z_feed[i] * c.Cp, 0); // kJ/kmol-K
    const sensible = sum_z_Cp * (T_feed - T); // kJ/kmol
    const latent = V * components.reduce((acc, c, i) => acc + y[i] * c.Hvap, 0); // kJ/kmol
    // Q/F + sensible (positive when cooling) - latent = 0
    return Q_per_kmol + sensible - latent;
}

function solveFlashTemperature(P, Q_hr) {
    // Robust bracketed bisection with expansion and graceful fallback
    let lo = 270, hi = 520; // K
    let glo = energyResidual(lo, P, Q_hr);
    let ghi = energyResidual(hi, P, Q_hr);

    // Expand bracket if needed
    const T_min = 200, T_max = 700;
    for (let tries = 0; tries < 12 && !(isFinite(glo) && isFinite(ghi) && (glo * ghi <= 0)); tries++) {
        if (!isFinite(glo)) lo = clamp(lo + 10, T_min, T_max);
        if (!isFinite(ghi)) hi = clamp(hi - 10, T_min, T_max);
        if (Math.abs(glo) < Math.abs(ghi)) {
            hi = clamp(hi + 30, T_min, T_max);
            ghi = energyResidual(hi, P, Q_hr);
        } else {
            lo = clamp(lo - 30, T_min, T_max);
            glo = energyResidual(lo, P, Q_hr);
        }
    }

    let T_star;
    if (isFinite(glo) && isFinite(ghi) && glo * ghi <= 0) {
        // Bisection
        for (let iter = 0; iter < 80; iter++) {
            const mid = 0.5 * (lo + hi);
            const gmid = energyResidual(mid, P, Q_hr);
            if (!isFinite(gmid)) {
                // Nudge slightly
                const eps = 1e-6;
                T_star = mid + eps;
                break;
            }
            if (Math.abs(gmid) < 1e-7 || (hi - lo) < 1e-6) {
                T_star = mid;
                break;
            }
            if (glo * gmid > 0) {
                lo = mid;
                glo = gmid;
            } else {
                hi = mid;
                ghi = gmid;
            }
            T_star = mid;
        }
    } else {
        // Fallback: scan for minimal |g|
        let bestT = lo;
        let bestG = Math.abs(glo);
        const N = 40;
        for (let i = 0; i <= N; i++) {
            const T = lo + (i / N) * (hi - lo);
            const g = Math.abs(energyResidual(T, P, Q_hr));
            if (g < bestG) {
                bestG = g; bestT = T;
            }
        }
        T_star = bestT;
    }
    return T_star;
}

function calculateFlash(P_flash, Q_hr) {
    const T_flash = solveFlashTemperature(P_flash, Q_hr);
    const { V, K, x, y } = solveV_givenT(T_flash, P_flash);
    return {
        T_flash,
        V_frac: V,
        x, y, K,
        inputs: { P_flash, Q_hr }
    };
}

/* ======== Controls Initialization ======== */
function initControls() {
    const sp = document.getElementById('slider-pressure');
    const sh = document.getElementById('slider-heat');
    const pv = document.getElementById('pressure-value');
    const hv = document.getElementById('heat-value');

    // Set attributes as specified
    sp.min = 100; sp.max = 1000; sp.step = 1; sp.value = 180;
    sh.min = 0; sh.max = 10000; sh.step = 100; sh.value = 0;

    // Initial display
    pv.textContent = `${sp.value} kPa`;
    hv.textContent = `${sh.value} kJ/hr`;

    // Update state and recalc
    function handleInput() {
        state.P_flash = Number(sp.value);
        state.Q_hr = Number(sh.value);
        pv.textContent = `${state.P_flash} kPa`;
        hv.textContent = `${state.Q_hr} kJ/hr`;
        state.result = calculateFlash(state.P_flash, state.Q_hr);
    }

    sp.addEventListener('input', handleInput);
    sh.addEventListener('input', handleInput);

    // Initial calc
    handleInput();
}

