}

function drawArrowHead(x, y, angle, size, col) {
    push();
    translate(x, y);
    rotate(angle);
    stroke(col);
    fill(col);
    const s = size || 8;
    triangle(0, 0, -s, s * 0.6, -s, -s * 0.6);
    pop();
}

function drawDashedLine(x1, y1, x2, y2, dash = 6, gap = 4, col = '#FF0000') {
    const dx = x2 - x1, dy = y2 - y1;
    const dist = Math.hypot(dx, dy);
    const steps = Math.floor(dist / (dash + gap));
    const ux = dx / dist, uy = dy / dist;
    stroke(col);
    for (let i = 0; i < steps; i++) {
        const sx = x1 + (dash + gap) * i * ux;
        const sy = y1 + (dash + gap) * i * uy;
        const ex = sx + dash * ux;
        const ey = sy + dash * uy;
        line(sx, sy, ex, ey);
    }
}

