      transparent: true,
      opacity: 0.25,
      blending: THREE.AdditiveBlending,
      depthWrite: false
    });

    // Procedural texture generation (simple layered trigs + noise)
    function makeProceduralGreyTexture(size = 512, seed = 1) {
      const c = document.createElement('canvas');
      c.width = c.height = size;
      const ctx = c.getContext('2d');
      const img = ctx.createImageData(size, size);
      const data = img.data;
      function rand(n) { // deterministic-ish
        const x = Math.sin(n * 127.1 + seed * 311.7) * 43758.5453;
        return x - Math.floor(x);
      }
      for (let y = 0; y < size; y++) {
        for (let x = 0; x < size; x++) {
          const nx = x / size, ny = y / size;
          // Layered pseudo-noise
          let v = 0;
          v += 0.55 * (Math.sin((nx * 8.0 + ny * 6.0) * 2.0 * Math.PI) * 0.5 + 0.5);
          v += 0.25 * (Math.sin((nx * 30.0 + 1.7) * 2.0 * Math.PI) * Math.cos((ny * 28.0 + 2.3) * 2.0 * Math.PI) * 0.5 + 0.5);
          v += 0.15 * rand(x + y * size + seed * 123.45);
          v += 0.05 * rand(x * 13.37 + y * 7.11 + seed * 0.314);
          // crater-like dark speckles
          const dx = nx - 0.5, dy = ny - 0.5;
          v *= 0.88 + 0.12 * Math.sin((dx*dx + dy*dy) * 28.0);
          const g = Math.max(0, Math.min(255, Math.floor(v * 255)));
          const i = (y * size + x) * 4;
          data[i] = data[i+1] = data[i+2] = g;
          data[i+3] = 255;
        }
      }
      ctx.putImageData(img, 0, 0);
      return new THREE.CanvasTexture(c);
    }

    // Moon terrain (plane) and Moon sphere for space view
    const moonTexture = makeProceduralGreyTexture(512, 3);
    moonTexture.wrapS = moonTexture.wrapT = THREE.RepeatWrapping;
    moonTexture.repeat.set(8, 8);

    const moonSurfaceMat = new THREE.MeshStandardMaterial({
      map: moonTexture,
      roughness: 0.95,
      metalness: 0.0,
      color: 0xaeb5bf
    });
    const moonSurfaceGeom = new THREE.PlaneGeometry(5000, 5000, 1, 1);
    const moonSurface = new THREE.Mesh(moonSurfaceGeom, moonSurfaceMat);
    moonSurface.rotation.x = -Math.PI/2; // flat ground
    moonSurface.position.y = -0.2;
    moonSurface.receiveShadow = true;
    scene.add(moonSurface);

    const moonSphereMat = new THREE.MeshStandardMaterial({
      map: makeProceduralGreyTexture(512, 7),
      roughness: 1.0,
      metalness: 0.0,
      color: 0xb9c2cf
    });
    const moonSphereGeom = new THREE.SphereGeometry(MOON_RADIUS, 48, 32);
    const moonSphere = new THREE.Mesh(moonSphereGeom, moonSphereMat);
    moonSphere.castShadow = false;
    moonSphere.receiveShadow = true;
    scene.add(moonSphere);

    // Earth group (orbit tilt)
    const earthOrbit = new THREE.Group();
    scene.add(earthOrbit);

    const earthGeom = new THREE.SphereGeometry(EARTH_RADIUS_ART, 48, 32);
    const earth = new THREE.Mesh(earthGeom, earthPhongMaterial);
    earth.castShadow = true;
    earth.receiveShadow = true;

    const earthAtmoGeom = new THREE.SphereGeometry(EARTH_RADIUS_ART * 1.06, 32, 24);
    const earthAtmo = new THREE.Mesh(earthAtmoGeom, atmosphereMaterial);
    earthAtmo.renderOrder = 1;

    // Group to keep earth and atmosphere together (for scaling)
    const earthGroup = new THREE.Group();
    earthGroup.add(earth);
    earthGroup.add(earthAtmo);
    earthOrbit.add(earthGroup);

    // Background "constellations" group (points + lines)
    const constellations = new THREE.Group();
    scene.add(constellations);

    function addConstellation(name, starSphericalList, color = 0xfff2aa) {
      // starSphericalList: [{r, phi, theta}] using physics convention:
      // r radius, theta polar angle (0..π from +Y), phi azimuth (0..2π around Y).
      const points = [];
      for (const s of starSphericalList) {
        const r = s.r, theta = s.theta, phi = s.phi;
        const x = r * Math.sin(theta) * Math.sin(phi);
        const y = r * Math.cos(theta);
        const z = r * Math.sin(theta) * Math.cos(phi);
        points.push(new THREE.Vector3(x, y, z));
      }
      // Points
      const g = new THREE.BufferGeometry().setFromPoints(points);
      const pmat = new THREE.PointsMaterial({ color, size: 2, sizeAttenuation: true });
      const p = new THREE.Points(g, pmat);
      constellations.add(p);
      // Lines connecting consecutive stars
      if (points.length >= 2) {
        const linePts = [];
        for (let i = 0; i < points.length - 1; i++) {
          linePts.push(points[i], points[i+1]);
        }
        const lg = new THREE.BufferGeometry().setFromPoints(linePts);
        const lmat = new THREE.LineBasicMaterial({ color, transparent: true, opacity: 0.55 });
        const lines = new THREE.LineSegments(lg, lmat);
        constellations.add(lines);
      }
    }

    // Create a few stylized zodiac constellations on a celestial sphere
    const CELESTIAL_RADIUS = 1200;
    function deg2rad(d){ return d * Math.PI / 180; }
    // Helper to go from RA/Dec to theta/phi (approx, treating Dec as latitude from equator)
    function raDecToThetaPhi(raHours, decDeg) {
      // Convert RA hours to degrees (0..360), Dec -90..+90
      const raDeg = raHours * 15.0;
      const phi = deg2rad(raDeg);            // azimuth around Y
      const theta = deg2rad(90 - decDeg);    // polar from +Y
      return { theta, phi };
    }
    // Minimal points for Aries, Taurus, Gemini (stylized)
    const zodiacData = {
      Aries: [ [2.5, 20], [2.9, 24], [3.2, 19], [3.5, 23] ],
      Taurus: [ [4.5, 16], [4.3, 15], [4.8, 12], [5.1, 10], [5.6, 9] ],
      Gemini: [ [7.2, 23], [7.5, 25], [7.7, 22], [7.9, 20], [8.2, 19] ],
      Cancer: [ [8.8, 19], [9.0, 21], [9.3, 20] ],
      Leo: [ [10.3, 12], [10.8, 13], [11.1, 15], [11.3, 18] ]
    };
    for (const [name, pts] of Object.entries(zodiacData)) {
      const sph = pts.map(([raH, decD]) => {
        const {theta, phi} = raDecToThetaPhi(raH, decD);
        return { r: CELESTIAL_RADIUS, theta, phi };
      });
      addConstellation(name, sph, 0xfff2aa);
    }
    constellations.visible = true;

    // Utility: set renderer/camera size
    function resizeRenderer() {
      const container = document.getElementById('visualization-container');
      const w = container.clientWidth;
      const h = container.clientHeight;
      if (w > 0 && h > 0) {
        renderer.setSize(w, h, false);
        camera.aspect = w / h;
        camera.updateProjectionMatrix();
      }
    }
    window.addEventListener('resize', resizeRenderer);

    // Camera configuration
    function setView(mode) {
      params.view = mode;
      // Update buttons
      btnLand.classList.toggle('active', mode === 'land');
      btnSpace.classList.toggle('active', mode === 'space');

      if (mode === 'land') {
        // Position camera near "surface", ~45° latitude
        const lat = 45 * Math.PI / 180;
        const lon = 0;
        const r = MOON_RADIUS + 0.4;
        const x = r * Math.cos(lat) * Math.cos(lon);
        const y = r * Math.sin(lat);
        const z = r * Math.cos(lat) * Math.sin(lon);
        camera.position.set(x, y, z);
        camera.up.set(0,1,0);

        // Show ground plane, hide Moon sphere
        moonSurface.visible = true;
        moonSphere.visible = false;
      } else {
        // Space view: pull back to see system
        camera.position.set(0, 40, 180);
        camera.up.set(0,1,0);

        moonSurface.visible = false;
        moonSphere.visible = true;
      }
      renderOnce();
    }

    // Earth scale toggle
    function updateEarthScale() {
      const targetRadius = params.realisticSize ? EARTH_RADIUS_REAL : EARTH_RADIUS_ART;
      const scale = targetRadius / EARTH_RADIUS_ART;
      earthGroup.scale.set(scale, scale, scale);
    }

    // Update star field visibility
    function updateConstellationVisibility() {
      constellations.visible = params.showConstellations;
    }

    // Update spans formatting to two decimals
    function updateValueSpans() {
      sliderTimeValue.textContent = Number(sliderTime.value).toFixed(2);
      sliderNodalValue.textContent = Number(sliderNodal.value).toFixed(2);
      // For the Sun-Earth angle, ensure numeric when changed by input/animation
      sliderSunEarthValue.textContent = Number(sliderSunEarth.value).toFixed(2);
      sliderZodiacValue.textContent = Number(sliderZodiac.value).toFixed(2);
    }

    // Map params to scene each frame
    function updateSceneFromParams() {
      // Set orbit tilt
      earthOrbit.rotation.x = params.nodalAngle;

      // Compute Earth position in its orbit (selenocentric)
      // Earth orbital angle based on time (days). Assume 27.3 days ~ 2π.
      const earthAngle = (params.time / 27.3) * Math.PI * 2.0;
      const ex = Math.cos(earthAngle) * EARTH_ORBIT_RADIUS;
      const ez = Math.sin(earthAngle) * EARTH_ORBIT_RADIUS;
      // Before tilt, Earth orbit lies in XZ plane at y=0. Child of earthOrbit is rotated by nodalAngle around X.
      earthGroup.position.set(ex, 0, ez);

      // Sun direction: use fixed angle set by sunEarthAngle on the same orbital plane, independent of time (so phases change with time)
      const sunAngle = params.sunEarthAngle;
      const sunVec = new THREE.Vector3(Math.cos(sunAngle), 0, Math.sin(sunAngle));
      // Apply same tilt as earth orbit to keep relative inclination
      const tiltX = params.nodalAngle;
      const cosT = Math.cos(tiltX), sinT = Math.sin(tiltX);
      // rotate around X: y' = y*cos - z*sin, z' = y*sin + z*cos
      const sy = sunVec.y * cosT - sunVec.z * sinT;
      const sz = sunVec.y * sinT + sunVec.z * cosT;
      const sx = sunVec.x;
      const sunDir = new THREE.Vector3(sx, sy, sz).normalize();

      // Position directional light far away along sunDir
      sunLight.position.copy(sunDir.clone().multiplyScalar(SUN_DISTANCE));
      sunLight.target.position.set(0, 0, 0);
      scene.add(sunLight.target); // ensure target in scene

      // Rotate background constellations around vertical axis (Y) by zodiac parameter
      constellations.rotation.y = params.zodiac;

      // Eclipse detection: Earth between Sun and Moon when Earth direction aligns with Sun direction
      const earthDir = earthGroup.position.clone().normalize();
      const angle = earthDir.angleTo(sunDir);
      const ECLIPSE_THRESHOLD = 0.06; // radians (~3.4°)
      const inEclipse = angle < ECLIPSE_THRESHOLD;

      // Switch Earth material/atmosphere for eclipse effect
      if (inEclipse) {
        earth.material = earthEclipseMaterial;
        earthAtmo.material = atmosphereEclipseMaterial;
        earthAtmo.scale.setScalar(1.12);
      } else {
        earth.material = earthPhongMaterial;
        earthAtmo.material = atmosphereMaterial;
        earthAtmo.scale.setScalar(1.06);
      }

      // In 'space' view, make sure Moon sphere is lit and visible; in 'land', aim at Earth for convenience
      if (params.view === 'land') {
        camera.lookAt(earthGroup.getWorldPosition(new THREE.Vector3()));
      } else {
        camera.lookAt(0, 0, 0);
      }
    }

    function renderOnce() {
      updateSceneFromParams();
      renderer.render(scene, camera);
    }

    // Initial states from controls
    function syncParamsFromControls() {
      params.time = parseFloat(sliderTime.value);
      params.nodalAngle = parseFloat(sliderNodal.value);
      params.sunEarthAngle = parseFloat(sliderSunEarth.value);
      params.zodiac = parseFloat(sliderZodiac.value);
      params.showConstellations = checkboxConstellations.checked;
      params.realisticSize = checkboxRealistic.checked;
    }

    // UI event bindings
    btnLand.addEventListener('click', () => setView('land'));
    btnSpace.addEventListener('click', () => setView('space'));

    sliderTime.addEventListener('input', () => {
      params.time = parseFloat(sliderTime.value);
      sliderTimeValue.textContent = params.time.toFixed(2);
      renderOnce();
    });
    sliderNodal.addEventListener('input', () => {
      params.nodalAngle = parseFloat(sliderNodal.value);
      sliderNodalValue.textContent = params.nodalAngle.toFixed(2);
      renderOnce();
    });
    sliderSunEarth.addEventListener('input', () => {
      params.sunEarthAngle = parseFloat(sliderSunEarth.value);
      // Once changed, display numeric (overwriting MathJax string)
      sliderSunEarthValue.textContent = params.sunEarthAngle.toFixed(2);
      renderOnce();
    });
    sliderZodiac.addEventListener('input', () => {
      params.zodiac = parseFloat(sliderZodiac.value);
      sliderZodiacValue.textContent = params.zodiac.toFixed(2);
      renderOnce();
    });

    // Play button toggles
    function togglePlay(flagKey, btnEl) {
      isAnimating[flagKey] = !isAnimating[flagKey];
      btnEl.classList.toggle('playing', isAnimating[flagKey]);
      btnEl.textContent = isAnimating[flagKey] ? '||' : '+';
    }
    btnTimePlay.addEventListener('click', () => togglePlay('time', btnTimePlay));
    btnNodalPlay.addEventListener('click', () => togglePlay('nodalAngle', btnNodalPlay));
    btnSunEarthPlay.addEventListener('click', () => togglePlay('sunEarthAngle', btnSunEarthPlay));
    btnZodiacPlay.addEventListener('click', () => togglePlay('zodiac', btnZodiacPlay));

    checkboxConstellations.addEventListener('change', () => {
      params.showConstellations = checkboxConstellations.checked;
      updateConstellationVisibility();
      renderOnce();
    });
    checkboxRealistic.addEventListener('change', () => {
      params.realisticSize = checkboxRealistic.checked;
      updateEarthScale();
      renderOnce();
    });

    // Initialize sizes, view, and scene
    function init() {
      resizeRenderer();
      setView('land'); // default active
      syncParamsFromControls();
      updateConstellationVisibility();
      updateEarthScale();
      renderOnce();
    }

    // Main animation loop
    let lastT = performance.now();
    function animate(now) {
      requestAnimationFrame(animate);
      const dt = Math.min(0.05, (now - lastT) / 1000); // clamp dt
      lastT = now;

      // Animate sliders if toggled
      if (isAnimating.time) {
        // Advance ~ 1.5 days per second
        let v = parseFloat(sliderTime.value);
        v += dt * 1.5;
        if (v > parseFloat(sliderTime.max)) v = parseFloat(sliderTime.min);
        sliderTime.value = v;
        params.time = v;
        sliderTimeValue.textContent = v.toFixed(2);
      }
      if (isAnimating.nodalAngle) {
        let v = parseFloat(sliderNodal.value);
        v = (v + dt * 0.6) % (2 * Math.PI);
        sliderNodal.value = v.toFixed(3);
        params.nodalAngle = v;
        sliderNodalValue.textContent = v.toFixed(2);
      }
      if (isAnimating.sunEarthAngle) {
        let v = parseFloat(sliderSunEarth.value);
        v = (v + dt * 0.8) % (2 * Math.PI);
        sliderSunEarth.value = v.toFixed(3);
        params.sunEarthAngle = v;
        sliderSunEarthValue.textContent = v.toFixed(2);
      }
      if (isAnimating.zodiac) {
        let v = parseFloat(sliderZodiac.value);
        v = (v + dt * 0.5) % (2 * Math.PI);
        sliderZodiac.value = v.toFixed(3);
        params.zodiac = v;
        sliderZodiacValue.textContent = v.toFixed(2);
      }

      updateSceneFromParams();
      renderer.render(scene, camera);
    }

    // Kick off
    init();
    requestAnimationFrame(animate);
  </script>
</body>
</html>
