<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8" />
  <meta name="viewport" content="width=device-width, initial-scale=1" />
  <title>Best Effort Global Warming Trajectories</title>
  <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
  <style>
    body {
      font-family: Arial, Helvetica, sans-serif;
      margin: 0;
      padding: 20px;
      background: #f9f9f9;
      color: #222;
      display: flex;
      flex-direction: column;
      gap: 16px;
    }
    #controls-container {
      display: flex;
      flex-direction: column;
      gap: 12px;
      background: #fff;
      border: 1px solid #e3e3e3;
      border-radius: 8px;
      padding: 12px 14px;
      box-shadow: 0 1px 2px rgba(0,0,0,0.04);
    }
    .control-row {
      display: flex;
      align-items: center;
      gap: 12px;
    }
    .control-row label {
      flex: 0 0 400px;
      max-width: 400px;
    }
    .control-row input[type="range"] {
      flex: 1 1 auto;
      min-width: 240px;
    }
    .control-row span {
      flex: 0 0 72px;
      text-align: right;
      font-variant-numeric: tabular-nums;
      font-weight: 600;
      color: #333;
    }
    #plot-div {
      width: 100%;
      height: 560px;
      background: #fff;
      border: 1px solid #e3e3e3;
      border-radius: 8px;
      box-shadow: 0 1px 2px rgba(0,0,0,0.04);
    }
  </style>
</head>
<body>
  <h1>Best Effort Global Warming Trajectories</h1>
  <p>
    Adjust the sliders to explore a simple emissions-and-atmospheric-carbon model.
    Emissions follow an initial rise and then decline at a constant pace (“wedges”),
    while atmospheric carbon evolves via a one-box decay with user-set residence time.
  </p>

  <div id="controls-container">
    <div class="control-row">
      <label for="slider-residence-time">Atmospheric CO2 residence time (years)</label>
      <input type="range" id="slider-residence-time" min="50" max="500" step="1" value="200" />
      <span id="value-residence-time">200</span>
    </div>
    <div class="control-row">
      <label for="slider-transition-pace">Transition to constant reduction pace (years)</label>
      <input type="range" id="slider-transition-pace" min="0" max="100" step="1" value="25" />
      <span id="value-transition-pace">25</span>
    </div>
    <div class="control-row">
      <label for="slider-wedges">Number of constant pace wedges (above emission stabilization at t=0)</label>
      <input type="range" id="slider-wedges" min="0" max="10" step="0.01" value="4" />
      <span id="value-wedges">4.00</span>
    </div>
  </div>

  <div id="plot-div"></div>

  <script>
    // Constants for the model
    const C0 = 850;     // Initial atmospheric carbon (GtC)
    const E0 = 8.5;     // Initial annual emissions (GtC/yr)
    const C_eq = 586;   // Pre-industrial equilibrium (GtC)
    const E_floor = 1.5;// Emission floor (GtC/yr)
    const rise_rate = 0.02;   // Emission rise rate during transition (GtC/yr^2)
    const time_step = 0.5;    // years
    const max_time = 300;     // years

    // Elements
    const sliderResidence = document.getElementById('slider-residence-time');
    const sliderTransition = document.getElementById('slider-transition-pace');
    const sliderWedges = document.getElementById('slider-wedges');

    const valueResidence = document.getElementById('value-residence-time');
    const valueTransition = document.getElementById('value-transition-pace');
    const valueWedges = document.getElementById('value-wedges');

    // Update display and plot on input
    sliderResidence.addEventListener('input', () => {
      valueResidence.textContent = sliderResidence.value;
      recomputePlot();
    });
    sliderTransition.addEventListener('input', () => {
      valueTransition.textContent = sliderTransition.value;
      recomputePlot();
    });
    sliderWedges.addEventListener('input', () => {
      valueWedges.textContent = parseFloat(sliderWedges.value).toFixed(2);
      recomputePlot();
    });

    function computeTrajectories() {
      // Read current slider values
      const t_res = parseFloat(sliderResidence.value);
      const t_trans = parseFloat(sliderTransition.value);
      const N_wedges = parseFloat(sliderWedges.value);

      const E_peak = E0 + rise_rate * t_trans;
      const reduction_rate = N_wedges / 50; // GtC/yr per year

      const times = [];
      const emissionsTimes100 = [];
      const carbon = [];

      let C_prev = C0;

      const steps = Math.round(max_time / time_step);

      for (let i = 0; i <= steps; i++) {
        const t = i * time_step;

        // Emission rate E(t)
        let E_t;
        if (t < t_trans) {
          E_t = E0 + rise_rate * t;
        } else {
          const E_linear = E_peak - reduction_rate * (t - t_trans);
          E_t = Math.max(E_floor, E_linear);
        }

        // Atmospheric carbon C(t) via Euler integration
        let C_t;
        if (i === 0) {
          C_t = C_prev; // initial condition
        } else {
          const dC = (E_t - (C_prev - C_eq) / t_res) * time_step;
          C_t = C_prev + dC;
        }
        C_prev = C_t;

        times.push(t);
        emissionsTimes100.push(100 * E_t);
        carbon.push(C_t);
      }

      return { times, emissionsTimes100, carbon };
    }

    // Plot initialization and update
    function recomputePlot() {
      const { times, emissionsTimes100, carbon } = computeTrajectories();

      const traceCarbon = {
        type: 'scatter',
        mode: 'lines',
        x: times,
        y: carbon,
        line: { color: 'blue', width: 2 },
        name: 'Atmospheric Carbon'
      };

      const traceEmissions = {
        type: 'scatter',
        mode: 'lines',
        x: times,
        y: emissionsTimes100,
        line: { color: 'purple', width: 2 },
        name: 'Annual Emissions × 100'
      };

      const traceCeiling = {
        type: 'scatter',
        mode: 'lines',
        x: [0, 300],
        y: [1200, 1200],
        line: { color: 'black', width: 2, dash: 'dash' },
        name: 'Target Ceiling'
      };

      const layout = {
        title: 'Atmospheric carbon (GtC; blue) and annual emissions (GtC/yr × 100; purple) versus time',
        xaxis: {
          title: 'years',
          range: [0, 300],
          zeroline: true,
          showgrid: true,
          gridcolor: '#ddd'
        },
        yaxis: {
          title: 'GtC or GtC/yr × 100',
          range: [0, 1600],
          zeroline: true,
          showgrid: true,
          gridcolor: '#ddd'
        },
        showlegend: false,
        margin: { l: 60, r: 20, b: 40, t: 80 },
        paper_bgcolor: '#f9f9f9',
        plot_bgcolor: 'white'
      };

      Plotly.react('plot-div', [traceCarbon, traceEmissions, traceCeiling], layout, {responsive: true});
    }

    // Initialize on first load
    window.addEventListener('DOMContentLoaded', () => {
      // Ensure initial display values
      valueResidence.textContent = sliderResidence.value;
      valueTransition.textContent = sliderTransition.value;
      valueWedges.textContent = parseFloat(sliderWedges.value).toFixed(2);

      recomputePlot();
    });
  </script>
</body>
</html>
