  <script>
    // Physics constants
    const g = 9.8;
    const M = 1;
    const m = 1;
    const scale = 50; // px per meter
    const canvasWidth = 500, canvasHeight = 500;

    // DOM elements
    const sliderTheta0 = document.getElementById('slider-theta0');
    const sliderV0     = document.getElementById('slider-v0');
    const sliderLength = document.getElementById('slider-length');

    const valueTheta0  = document.getElementById('value-theta0');
    const valueV0      = document.getElementById('value-v0');
    const valueLength  = document.getElementById('value-length');

    const btnPlayPause = document.getElementById('btn-play-pause');
    const btnReset     = document.getElementById('btn-reset');

    // Simulation state
    let isPlaying = false;
    let t = 0;
    let theta = 0;
    let theta_dot = 0;
    let x = 0;
    let x_dot = 0;
    let l = 2;
    let v0_init = 0;

    // Utility: format radians as common fractions of π when close, else decimal
    function radToPiString(rad) {
      const epsilon = 0.02;
      const P = Math.PI;
      const table = [
        [-P, "-π"],
        [-5*P/6, "-5π/6"],
        [-3*P/4, "-3π/4"],
        [-2*P/3, "-2π/3"],
        [-P/2, "-π/2"],
        [-P/3, "-π/3"],
        [-P/4, "-π/4"],
        [-P/6, "-π/6"],
        [0, "0"],
        [P/6, "π/6"],
        [P/4, "π/4"],
        [P/3, "π/3"],
        [P/2, "π/2"],
        [2*P/3, "2π/3"],
        [3*P/4, "3π/4"],
        [5*P/6, "5π/6"],
        [P, "π"]
      ];
      for (const [val, label] of table) {
        if (Math.abs(rad - val) < epsilon) return label;
      }
      // default decimal with 2 places
      return rad.toFixed(2);
    }

    function updateDisplaysFromSliders() {
      valueTheta0.textContent = radToPiString(parseFloat(sliderTheta0.value));
      valueV0.textContent = parseFloat(sliderV0.value).toFixed(2).replace(/\.00$/, '');
      valueLength.textContent = parseFloat(sliderLength.value).toFixed(2).replace(/\.00$/, '');
    }

    function pauseAnimation() {
      isPlaying = false;
      btnPlayPause.textContent = "▶";
    }

    function resetState() {
      t = 0;
      theta = parseFloat(sliderTheta0.value);
      theta_dot = 0;
      x = 0;
      v0_init = parseFloat(sliderV0.value);
      x_dot = v0_init;
      l = parseFloat(sliderLength.value);
    }

    // Event listeners for sliders
    function onAnySliderInput() {
      updateDisplaysFromSliders();
      pauseAnimation();
      resetState();
    }
    sliderTheta0.addEventListener('input', onAnySliderInput);
    sliderV0.addEventListener('input', onAnySliderInput);
    sliderLength.addEventListener('input', onAnySliderInput);

    // Buttons
    btnPlayPause.addEventListener('click', () => {
      isPlaying = !isPlaying;
      btnPlayPause.textContent = isPlaying ? "||" : "▶";
    });

    btnReset.addEventListener('click', () => {
      pauseAnimation();
      resetState();
    });

    // Initialize displays to default slider states
    updateDisplaysFromSliders();

    // p5.js sketch (global mode)
    let cnv;
    function setup() {
      cnv = createCanvas(canvasWidth, canvasHeight);
      cnv.parent('visualization');
      resetState();
      frameRate(60);
    }

    function draw() {
      background('#F0F0F0');

      // Update physics only when playing
      if (isPlaying) {
        const dt = Math.min(0.05, deltaTime / 1000); // seconds
        const sinT = Math.sin(theta);
        const cosT = Math.cos(theta);

        // theta_ddot from given equation
        const denom = l * (M + m * sinT * sinT);
        const theta_ddot = (-m * l * sinT * cosT * theta_dot * theta_dot - g * (M + m) * sinT) / denom;

        // Integrate
        theta_dot += theta_ddot * dt;
        theta += theta_dot * dt;

        // Pivot velocity via conservation relation
        x_dot = v0_init - (m * l * Math.cos(theta) * theta_dot) / (M + m);
        x += x_dot * dt;

        t += dt;
      }

      // Convert to screen coordinates
      const px = width / 2 + x * scale;
      const py = height / 2;

      const bx = px + (l * scale) * Math.sin(theta);
      const by = py + (l * scale) * Math.cos(theta);

      // Draw connector (rod)
      stroke('black');
      strokeWeight(2);
      line(px, py, bx, by);

      // Draw pivot as red rectangle centered at (px, py)
      noStroke();
      fill('red');
      const pw = 34, ph = 12;
      rectMode(CENTER);
      rect(px, py, pw, ph, 2);

      // Draw bob as orange circle
      stroke('black');
      strokeWeight(2);
      fill('orange');
      const bobR = 16;
      circle(bx, by, bobR * 2);
    }
  </script>
