  <!-- three.js and controls -->
  <script src="https://unpkg.com/three@0.161.0/build/three.min.js"></script>
  <script src="https://unpkg.com/three@0.161.0/examples/js/controls/OrbitControls.js"></script>

  <script>
  (function(){
    // DOM elements
    const sliderStrike = document.getElementById('slider-strike');
    const sliderDip = document.getElementById('slider-dip');
    const sliderSlipAngle = document.getElementById('slider-slip-angle');
    const sliderSlipAmount = document.getElementById('slider-slip-amount');
    const valueStrike = document.getElementById('value-strike');
    const valueDip = document.getElementById('value-dip');
    const valueSlipAngle = document.getElementById('value-slip-angle');
    const valueSlipAmount = document.getElementById('value-slip-amount');
    const btnReset = document.getElementById('btn-reset');
    const checkboxBox = document.getElementById('checkbox-box');
    const canvas = document.getElementById('webgl-canvas');
    const canvasContainer = document.getElementById('canvas-container');

    // Defaults (Section 3)
    const DEFAULTS = {
      strike: 180,
      dip: 60,
      slipAngle: 90,
      slipAmount: 0.5
    };

    // ThreeJS basic setup
    const renderer = new THREE.WebGLRenderer({ canvas, antialias: true });
    renderer.setPixelRatio(Math.min(devicePixelRatio, 2));
    renderer.setSize(canvasContainer.clientWidth, canvasContainer.clientHeight);

    const scene = new THREE.Scene();
    scene.background = new THREE.Color(0xf8fafc);

    const camera = new THREE.PerspectiveCamera(45, canvasContainer.clientWidth/canvasContainer.clientHeight, 0.1, 100);
    camera.position.set(3.5, 2.0, 3.5);
    scene.add(camera);

    const controls = new THREE.OrbitControls(camera, renderer.domElement);
    controls.enableDamping = true;
    controls.target.set(0, 0, 0);

    // Lights
    const hemiLight = new THREE.HemisphereLight(0xffffff, 0x667788, 0.7);
    hemiLight.position.set(0, 1, 0);
    scene.add(hemiLight);

    const dirLight1 = new THREE.DirectionalLight(0xffffff, 0.6);
    dirLight1.position.set(3, 5, 2);
    scene.add(dirLight1);

    const dirLight2 = new THREE.DirectionalLight(0xffffff, 0.35);
    dirLight2.position.set(-4, 2, -3);
    scene.add(dirLight2);

    // Groups
    const rootGroup = new THREE.Group();
    scene.add(rootGroup);

    // Sphere (focal sphere)
    const sphereGeom = new THREE.SphereGeometry(1, 64, 32);
    const originalPositions = Float32Array.from(sphereGeom.attributes.position.array); // copy
    // per-vertex colors
    const colors = new Float32Array(sphereGeom.attributes.position.count * 3);
    sphereGeom.setAttribute('color', new THREE.BufferAttribute(colors, 3));

    const sphereMat = new THREE.MeshStandardMaterial({
      metalness: 0.05,
      roughness: 0.7,
      vertexColors: true,
      side: THREE.FrontSide
    });
    const sphereMesh = new THREE.Mesh(sphereGeom, sphereMat);
    rootGroup.add(sphereMesh);

    // Bounding box helper (initially hidden)
    const boxHelper = new THREE.BoxHelper(sphereMesh, 0x00aaff);
    boxHelper.visible = false;
    scene.add(boxHelper);

    // Compass rose (circle + ticks + labels), lies on XZ plane (y=0)
    const compassGroup = new THREE.Group();
    rootGroup.add(compassGroup);

    function buildCompassRose() {
      compassGroup.clear();

      const R = 1.4;

      // Circle (dashed)
      const circlePoints = [];
      for (let i=0; i<=128; i++){
        const t = (i/128) * Math.PI * 2;
        circlePoints.push(new THREE.Vector3(R*Math.cos(t), 0, R*Math.sin(t)));
      }
      const circleGeom = new THREE.BufferGeometry().setFromPoints(circlePoints);
      const circleMat = new THREE.LineDashedMaterial({
        color: 0x666666,
        linewidth: 1,
        dashSize: 0.08,
        gapSize: 0.05
      });
      const circleLine = new THREE.Line(circleGeom, circleMat);
      circleLine.computeLineDistances();
      compassGroup.add(circleLine);

      // Ticks every 30 degrees
      for (let deg = 0; deg < 360; deg += 30) {
        const rad = THREE.MathUtils.degToRad(deg);
        const dir = new THREE.Vector3(Math.cos(rad), 0, Math.sin(rad));
        const inner = dir.clone().multiplyScalar(R * 0.96);
        const outer = dir.clone().multiplyScalar(R * 1.02);
        const tickGeom = new THREE.BufferGeometry().setFromPoints([inner, outer]);
        const tickMat = new THREE.LineBasicMaterial({ color: 0x999999 });
        const tick = new THREE.Line(tickGeom, tickMat);
        compassGroup.add(tick);
      }

      // Cardinal labels N,E,S,W using sprites
      const labels = [
        { t: 'N', pos: new THREE.Vector3(0, 0, -R) },
        { t: 'E', pos: new THREE.Vector3(R, 0, 0) },
        { t: 'S', pos: new THREE.Vector3(0, 0, R) },
        { t: 'W', pos: new THREE.Vector3(-R, 0, 0) },
      ];
      labels.forEach(item => {
        const spr = makeTextSprite(item.t, { color: '#1e293b', fontSize: 70, padding: 12, background: 'rgba(255,255,255,0.7)', borderColor:'#cbd5e1' });
        spr.position.copy(item.pos);
        spr.position.y += 0.0; // stays on plane
        spr.scale.set(0.25, 0.25, 0.25);
        compassGroup.add(spr);
      });
    }

    // Axis labels for P, T, U
    const labelP = makeTextSprite('P', { color: '#111827', fontSize: 80, padding: 14, background: 'rgba(255,255,255,0.75)', borderColor:'#e5e7eb' });
    const labelT = makeTextSprite('T', { color: '#111827', fontSize: 80, padding: 14, background: 'rgba(255,255,255,0.75)', borderColor:'#e5e7eb' });
    const labelU = makeTextSprite('U', { color: '#111827', fontSize: 80, padding: 14, background: 'rgba(255,255,255,0.75)', borderColor:'#e5e7eb' });
    [labelP, labelT, labelU].forEach(l => {
      l.scale.set(0.22, 0.22, 0.22);
      rootGroup.add(l);
    });

    buildCompassRose();

    // Utility: create a sprite label using canvas
    function makeTextSprite(message, options = {}) {
      const {
        fontFace = 'Inter, system-ui, Arial, sans-serif',
        fontSize = 64,
        color = '#000',
        padding = 8,
        background = 'rgba(255,255,255,0.6)',
        borderColor = 'rgba(0,0,0,0.15)',
        borderRadius = 8
      } = options;

      const ctxCanvas = document.createElement('canvas');
      const ctx = ctxCanvas.getContext('2d');
      const px = fontSize;
      ctx.font = `bold ${px}px ${fontFace}`;
      const metrics = ctx.measureText(message);
      const textW = Math.ceil(metrics.width);
      const textH = Math.ceil(px);
      const pad = padding;
      ctxCanvas.width = textW + pad*2;
      ctxCanvas.height = textH + pad*2;

      // Re-get context after resize
      const ctx2 = ctxCanvas.getContext('2d');
      ctx2.font = `bold ${px}px ${fontFace}`;
      // background with rounded rect
      ctx2.fillStyle = background;
      ctx2.strokeStyle = borderColor;
      roundRect(ctx2, 0.5, 0.5, ctxCanvas.width-1, ctxCanvas.height-1, borderRadius, true, true);
      // text
      ctx2.fillStyle = color;
      ctx2.textBaseline = 'top';
      ctx2.fillText(message, pad, pad);

      const texture = new THREE.CanvasTexture(ctxCanvas);
      texture.anisotropy = renderer.capabilities.getMaxAnisotropy();
      const material = new THREE.SpriteMaterial({ map: texture, transparent: true });
      const sprite = new THREE.Sprite(material);
      // Scale is handled externally
      return sprite;
    }

    function roundRect(ctx, x, y, w, h, r, fill, stroke) {
      if (w < 2*r) r = w/2;
      if (h < 2*r) r = h/2;
      ctx.beginPath();
      ctx.moveTo(x+r, y);
      ctx.arcTo(x+w, y, x+w, y+h, r);
      ctx.arcTo(x+w, y+h, x, y+h, r);
      ctx.arcTo(x, y+h, x, y, r);
      ctx.arcTo(x, y, x+w, y, r);
      ctx.closePath();
      if (fill) ctx.fill();
      if (stroke) ctx.stroke();
    }

    // Coordinate mappings:
    // Seismological NED -> ThreeJS (E, Up, South)
    function nedToThree(vN, vE, vD) {
      return new THREE.Vector3(vE, -vD, -vN);
    }
    // ThreeJS (E,Up,South) -> NED
    function threeToNed(vx, vy, vz) {
      // N = -South = -z
      // E = x
      // D = -Up = -y
      return new THREE.Vector3(-vz, vx, -vy);
    }

    // Compute fault geometry and axes (P, T, U)
    function computeFaultAxes(phiDeg, deltaDeg, lambdaDeg) {
      const phi = THREE.MathUtils.degToRad(phiDeg);
      const delt = THREE.MathUtils.degToRad(deltaDeg);
      const lam  = THREE.MathUtils.degToRad(lambdaDeg);

      // In NED: x=N, y=E, z=D
      const cphi = Math.cos(phi), sphi = Math.sin(phi);
      const cdel = Math.cos(delt), sdel = Math.sin(delt);
      const clam = Math.cos(lam),  slam = Math.sin(lam);

      // Strike direction unit vector (horizontal): azimuth phi, clockwise from North
      const s_hat = new THREE.Vector3(cphi, sphi, 0);

      // Down-dip unit vector in plane
      const d_hat = new THREE.Vector3(-sphi*cdel, cphi*cdel, sdel);

      // Normal to plane (right-hand: s × d)
      const n = new THREE.Vector3().copy(s_hat).cross(d_hat).normalize();

      // Slip vector u = cosλ * s + sinλ * d
      const u = new THREE.Vector3().copy(s_hat).multiplyScalar(clam)
                    .add(new THREE.Vector3().copy(d_hat).multiplyScalar(slam));
      // s_hat and d_hat are orthonormal -> u is unit-length already
      // But numeric safety:
      u.normalize();

      // P and T axes
      const P = new THREE.Vector3().copy(n).add(u).normalize();
      const T = new THREE.Vector3().copy(u).sub(n).normalize();

      return { n, u, P, T }; // all in NED
    }

    // Update text displays
    function updateValueDisplays() {
      valueStrike.value = `${Number(sliderStrike.value).toFixed(0)}°`;
      valueDip.value = `${Number(sliderDip.value).toFixed(0)}°`;
      valueSlipAngle.value = `${Number(sliderSlipAngle.value).toFixed(0)}°`;
      valueSlipAmount.value = Number(sliderSlipAmount.value).toFixed(2);
    }

    // Coloring + Deformation
    const deformationScale = 0.35; // tweakable

    function recolorAndDeform() {
      const strike = Number(sliderStrike.value);
      const dip = Number(sliderDip.value);
      const rake = Number(sliderSlipAngle.value);
      const sAmt = Number(sliderSlipAmount.value);

      const { P, T, u } = computeFaultAxes(strike, dip, rake); // NED

      // set PTU label positions (on sphere surface, mapped to three.js coords)
      const PTUscale = 1.08;
      const P3 = nedToThree(P.x, P.y, P.z).normalize().multiplyScalar(PTUscale);
      const T3 = nedToThree(T.x, T.y, T.z).normalize().multiplyScalar(PTUscale);
      const U3 = nedToThree(u.x, u.y, u.z).normalize().multiplyScalar(PTUscale);
      labelP.position.copy(P3);
      labelT.position.copy(T3);
      labelU.position.copy(U3);

      const posAttr = sphereGeom.attributes.position;
      const colAttr = sphereGeom.attributes.color;
      const posArr = posAttr.array;
      const origArr = originalPositions;
      const colArr = colAttr.array;

      // For each vertex: compute sign g = (v·T)^2 - (v·P)^2 using v in NED
      const vThree = new THREE.Vector3();
      const vNed = new THREE.Vector3();
      const vNedUnit = new THREE.Vector3();
      for (let i=0; i<posAttr.count; i++) {
        const ix = i*3;
        // original position (so deformation is radial from original sphere)
        const ox = origArr[ix+0];
        const oy = origArr[ix+1];
        const oz = origArr[ix+2];

        vThree.set(ox, oy, oz);
        // to NED
        vNed.copy(threeToNed(vThree.x, vThree.y, vThree.z));
        vNedUnit.copy(vNed).normalize();

        const dotT = vNedUnit.dot(T);
        const dotP = vNedUnit.dot(P);
        const g = (dotT*dotT) - (dotP*dotP); // dilatational minus compressional
        // Deform radially
        const scale = 1 + deformationScale * sAmt * g;
        posArr[ix+0] = ox * scale;
        posArr[ix+1] = oy * scale;
        posArr[ix+2] = oz * scale;

        // Color per vertex: orange for dilatational (g >= 0), white for compressional
        const isDilat = g >= 0;
        let r, gC, b;
        if (isDilat) {
          r = 1.0; gC = 0.65; b = 0.0; // orange
        } else {
          r = 1.0; gC = 1.0; b = 1.0; // white
        }
        colArr[ix+0] = r;
        colArr[ix+1] = gC;
        colArr[ix+2] = b;
      }

      posAttr.needsUpdate = true;
      colAttr.needsUpdate = true;
      sphereGeom.computeVertexNormals();

      // Update BoxHelper if visible
      if (boxHelper.visible) {
        boxHelper.update();
      }
    }

    function onResize() {
      const w = canvasContainer.clientWidth;
      const h = canvasContainer.clientHeight;
      renderer.setSize(w, h, false);
      camera.aspect = w / h;
      camera.updateProjectionMatrix();
    }
    window.addEventListener('resize', onResize);

    // UI events
    const onAnyInput = () => {
      updateValueDisplays();
      recolorAndDeform();
    };

    sliderStrike.addEventListener('input', onAnyInput);
    sliderDip.addEventListener('input', onAnyInput);
    sliderSlipAngle.addEventListener('input', onAnyInput);
    sliderSlipAmount.addEventListener('input', onAnyInput);

    checkboxBox.addEventListener('change', () => {
      boxHelper.visible = checkboxBox.checked;
      if (boxHelper.visible) boxHelper.update();
    });

    btnReset.addEventListener('click', () => {
      sliderStrike.value = DEFAULTS.strike;
      sliderDip.value = DEFAULTS.dip;
      sliderSlipAngle.value = DEFAULTS.slipAngle;
      sliderSlipAmount.value = DEFAULTS.slipAmount;
      checkboxBox.checked = false;
      boxHelper.visible = false;
      updateValueDisplays();
      // Reset geometry to original positions and recolor/deform with default
      sphereGeom.attributes.position.array.set(originalPositions);
      sphereGeom.attributes.position.needsUpdate = true;
      recolorAndDeform();
    });

    // Initial draw
    updateValueDisplays();
    recolorAndDeform();

    // Animate
    function animate() {
      requestAnimationFrame(animate);
      controls.update();
      renderer.render(scene, camera);
    }
    animate();

    // Fit scene on load
    onResize();

  })();
  </script>
</body>
</html>
