  <script>
    // DOM elements and initial state
    const sliderN = document.getElementById('slider-db-size');
    const sliderK = document.getElementById('slider-iterations');
    const displayN = document.getElementById('display-db-size');
    const displayK = document.getElementById('display-iterations');
    const probDisplay = document.getElementById('probability-display');

    // Ensure displays update in real time as sliders move
    function updateDisplays() {
      displayN.textContent = sliderN.value;
      displayK.textContent = sliderK.value;
    }
    sliderN.addEventListener('input', updateDisplays);
    sliderK.addEventListener('input', updateDisplays);

    // Constants for visualization
    const targetIndex = 6; // fixed target as per plan

    // p5 sketch
    let canvasHeight = 640; // 4 sections -> 160px each by default
    let canvas;

    function setup() {
      const container = document.getElementById('canvas-container');
      const w = Math.max(600, container.clientWidth - 0); // responsive width
      canvas = createCanvas(w, canvasHeight);
      canvas.parent('canvas-container');
      textFont('Arial, Helvetica, sans-serif');
      textSize(14);
      textAlign(CENTER, CENTER);
      noStroke();
    }

    function windowResized() {
      const container = document.getElementById('canvas-container');
      const w = Math.max(600, container.clientWidth - 0);
      resizeCanvas(w, canvasHeight);
    }

    // Grover core operations
    function initialState(N) {
      const amp = 1 / Math.sqrt(N);
      return Array(N).fill(amp);
    }

    function inversionOracle(arr, tgt) {
      const out = arr.slice();
      out[tgt] = -out[tgt];
      return out;
    }

    function diffusion(arr) {
      const N = arr.length;
      const mean = arr.reduce((s, a) => s + a, 0) / N;
      return arr.map(a => 2 * mean - a);
    }

    function applyGroverKTimes(N, k, tgt) {
      let state = initialState(N);
      for (let i = 0; i < k; i++) {
        state = inversionOracle(state, tgt);
        state = diffusion(state);
      }
      return state;
    }

    function groverSuccessProbability(N, k) {
      const theta = Math.asin(1 / Math.sqrt(N));
      const s = Math.sin((2 * k + 1) * theta);
      const P = s * s;
      return P;
    }

    // Drawing helpers
    function drawSection(amplitudes, sectionIndex, title, drawParens) {
      const N = amplitudes.length;
      const sections = 4;
      const sectionHeight = height / sections;
      const top = sectionIndex * sectionHeight;
      const baselineY = top + sectionHeight * 0.55; // slightly below center to leave room for title
      const titleY = top + 18;

      // Background for section (subtle)
      noStroke();
      fill(248);
      rect(0, top, width, sectionHeight);

      // Title
      fill(34);
      text(title, width / 2, titleY);

      // Baseline
      stroke(200);
      strokeWeight(1);
      line(40, baselineY, width - 40, baselineY);

      // Plotting bounds and scales
      const marginLeft = 60;
      const marginRight = 60;
      const plotWidth = width - marginLeft - marginRight;
      const dx = plotWidth / N;
      const barW = Math.max(1, Math.min(20, dx * 0.7));
      const ampScale = sectionHeight * 0.35; // scale amplitudes within section

      // Bars
      noStroke();
      for (let i = 0; i < N; i++) {
        const amp = amplitudes[i];
        const h = Math.abs(amp) * ampScale;
        const cx = marginLeft + dx * (i + 0.5);
        const x = cx - barW / 2;
        const y = amp >= 0 ? (baselineY - h) : baselineY;

        fill(i === targetIndex ? '#FFA500' : '#808080');
        rect(x, y, barW, h);
      }

      // Parentheses for first plot using arcs
      if (drawParens) {
        const parenH = sectionHeight * 0.75;
        const cxLeft = marginLeft - 20;
        const cxRight = width - marginRight + 20;
        const cy = (top + sectionHeight * 0.55);
        noFill();
        stroke(120);
        strokeWeight(2);
        // Left "("
        arc(cxLeft, cy, 24, parenH, Math.PI / 2, Math.PI * 1.5);
        // Right ")"
        arc(cxRight, cy, 24, parenH, -Math.PI / 2, Math.PI / 2);
      }
    }

    // Only update probability text if values changed
    let lastN = null;
    let lastK = null;

    function draw() {
      background(255);

      // Read N and k
      const N = parseInt(sliderN.value, 10);
      const k = parseInt(sliderK.value, 10);

      // Ensure displays match sliders
      if (displayN.textContent !== String(N) || displayK.textContent !== String(k)) {
        updateDisplays();
      }

      // Compute states for four stages
      const stage1 = initialState(N);
      const stage2 = inversionOracle(stage1, targetIndex);
      const stage3 = diffusion(stage2);
      const stage4 = applyGroverKTimes(N, k, targetIndex);

      // Draw four sections
      drawSection(stage1, 0, 'original amplitudes', true);
      drawSection(stage2, 1, 'after first inversion', false);
      drawSection(stage3, 2, 'after first diffusion', false);
      drawSection(stage4, 3, 'after k iterations', false);

      // Update probability display when needed
      if (lastN !== N || lastK !== k) {
        lastN = N;
        lastK = k;
        const P = groverSuccessProbability(N, k);
        const percent = (P * 100).toFixed(1);
        probDisplay.textContent = `${percent}% success probability`;
      }
    }
  </script>
</body>
</html>
