<script>
(function() {
  // Global app state
  const appState = {
    month: (new Date()).getMonth(),
    view: '2d',
    p5Instance: null,
    three: null, // { renderer, scene, camera, controls, objects, rafId, dispose }
  };

  // DOM references
  const slider = document.getElementById('slider-month');
  const btn2d = document.getElementById('btn-2d');
  const btn3d = document.getElementById('btn-3d');
  const vizArea = document.getElementById('visualization-area');

  // Initialize slider properties
  slider.min = 0;
  slider.max = 11;
  slider.step = 1;
  slider.value = appState.month;

  // Helper to set active button styling
  function setActiveButtons() {
    if (appState.view === '2d') {
      btn2d.classList.add('active');
      btn3d.classList.remove('active');
    } else {
      btn3d.classList.add('active');
      btn2d.classList.remove('active');
    }
  }

  // Utility: ensure a canvas with id="main-canvas" exists inside visualization-area
  function ensureCanvas() {
    let c = document.getElementById('main-canvas');
    if (!c) {
      c = document.createElement('canvas');
      c.id = 'main-canvas';
      c.style.position = 'absolute';
      c.style.inset = '0';
      c.style.width = '100%';
      c.style.height = '100%';
      c.style.background = '#000000';
      vizArea.appendChild(c);
    }
    return c;
  }

  // Utility: remove the canvas (used when switching rendering context)
  function removeCanvas() {
    const c = document.getElementById('main-canvas');
    if (c && c.parentNode) c.parentNode.removeChild(c);
  }

  // Compute Earth's axial tilt (degrees) for 2D view
  function tiltDegreesForMonth(month) {
    return 23.5 * Math.cos(2 * Math.PI * month / 12);
  }

  // ---------------------------
  // 2D VIEW (p5.js) MANAGEMENT
  // ---------------------------
  function start2D() {
    stop3D(); // ensure 3D renderer is stopped
    removeCanvas(); // p5 will create its own canvas and assign the id
    const sketch = (p) => {
      let earthR, mpR, bowR;
      let tailLength;

      p.setup = function() {
        const parent = vizArea;
        const w = parent.clientWidth;
        const h = parent.clientHeight;
        const cnv = p.createCanvas(w, h);
        cnv.id('main-canvas');
        cnv.parent('visualization-area');
        p.noLoop();
        p.angleMode(p.DEGREES);
        p.pixelDensity(window.devicePixelRatio || 1);
        p.smooth();
        computeScales();
        p.redraw();
      };

      p.windowResized = function() {
        const parent = vizArea;
        p.resizeCanvas(parent.clientWidth, parent.clientHeight);
        computeScales();
        p.redraw();
      };

      function computeScales() {
        const s = Math.min(p.width, p.height) / 2;
        earthR = Math.max(12, s * 0.07);
        mpR = s * 0.55;   // magnetopause dayside "radius"
        bowR = s * 0.70;  // bow shock radius
        tailLength = s * 0.9;
      }

      function drawSolarWind() {
        p.push();
        p.noFill();
        p.stroke('#990000');
        p.strokeWeight(2);
        const margin = 18;
        const leftX = margin;
        const rightX = p.width * 0.45;
        const gap = 20;
        for (let y = margin + 10; y < p.height - margin; y += gap) {
          p.line(leftX, y, rightX, y);
          // Arrowhead at right
          const ah = 6, aw = 10;
          p.fill('#990000');
          p.noStroke();
          p.triangle(rightX, y,
                     rightX - aw, y - ah,
                     rightX - aw, y + ah);
          p.stroke('#990000');
        }
        p.pop();
      }

      function drawEarthAndFeatures() {
        // Earth
        p.push();
        p.noStroke();
        p.fill('#4169E1');
        p.circle(0, 0, earthR * 2);
        // Simple landmasses
        p.fill('#228B22');
        p.push();
        p.rotate(30);
        p.ellipse(earthR * 0.25, -earthR * 0.2, earthR * 0.9, earthR * 0.45);
        p.pop();

        p.push();
        p.rotate(-20);
        p.ellipse(-earthR * 0.3, earthR * 0.1, earthR * 0.6, earthR * 0.35);
        p.pop();
        p.pop();

        // Polar cusps (triangles pointing sunward = left in unrotated frame)
        p.push();
        p.noStroke();
        p.fill('#F0E68C'); // khaki
        // Top cusp
        p.triangle(-earthR * 1.3, -earthR * 1.1,
                    0, -earthR * 0.6,
                   -earthR * 0.4, -earthR * 1.8);
        // Bottom cusp
        p.triangle(-earthR * 1.3,  earthR * 1.1,
                    0,  earthR * 0.6,
                   -earthR * 0.4,  earthR * 1.8);
        p.pop();

        // Van Allen Belts (nested crescents on the night side = right)
        p.push();
        p.noStroke();
        p.fill('#006400'); // dark green
        p.arc(earthR * 1.1, 0, earthR * 4.4, earthR * 2.6, -25, 25, p.PIE);
        p.fill('#32CD32'); // lime green
        p.arc(earthR * 1.2, 0, earthR * 3.4, earthR * 1.8, -25, 25, p.PIE);
        p.pop();
      }

      function drawFieldLines() {
        p.push();
        p.noFill();
        p.stroke('#808080');
        p.strokeWeight(1.2);

        const lines = [earthR * 0.8, earthR * 1.1, earthR * 1.6, earthR * 2.2, earthR * 3.0];
        // Closed loops around Earth
        for (let L of lines.slice(0, 3)) {
          p.beginShape();
          for (let a = -80; a <= 80; a += 2) {
            let rad = Math.max(earthR * 0.6, L / Math.pow(Math.cos(p.radians(a)) || 0.0001, 2));
            rad = Math.min(rad, mpR * 0.9);
            const x = rad * Math.cos(p.radians(a));
            const y = rad * Math.sin(p.radians(a));
            p.vertex(x, y);
          }
          for (let a = 80; a >= -80; a -= 2) {
            let rad = Math.max(earthR * 0.6, L / Math.pow(Math.cos(p.radians(a)) || 0.0001, 2));
            rad = Math.min(rad, mpR * 0.9);
            const x = -rad * Math.cos(p.radians(a));
            const y = rad * Math.sin(p.radians(a));
            p.vertex(x, y);
          }
          p.endShape(p.CLOSE);
        }

        // Open field lines forming magnetotail (extending to right)
        for (let i = -2; i <= 2; i++) {
          p.beginShape();
          for (let a = -70; a <= 70; a += 2) {
            let rad = (earthR * (2.5 + Math.abs(i) * 0.6)) / Math.pow(Math.cos(p.radians(a)) || 0.0001, 2);
            rad = Math.min(rad, mpR * 0.95);
            const x = rad * Math.cos(p.radians(a));
            const y = rad * Math.sin(p.radians(a)) + i * earthR * 0.5;
            p.vertex(x, y);
          }
          // Extend straight into tail
          p.vertex(tailLength, i * earthR * 0.5);
          p.endShape();
        }
        p.pop();
      }

      function drawMagnetotailRegion() {
        // Filled semi-transparent dark blue region on night side (right)
        p.push();
        p.noStroke();
        p.fill(0, 0, 139, 70);
        p.beginShape();
        p.vertex(earthR * 1.5, -earthR * 2.2);
        p.bezierVertex(mpR * 0.7, -earthR * 2.6, mpR * 0.9, -earthR * 1.6, tailLength, -earthR * 0.8);
        p.vertex(tailLength,  earthR * 0.8);
        p.bezierVertex(mpR * 0.9, earthR * 1.6, mpR * 0.7, earthR * 2.6, earthR * 1.5, earthR * 2.2);
        p.endShape(p.CLOSE);
        p.pop();
      }

      function drawBowShockAndMagnetopause() {
        p.push();
        p.noFill();
        p.strokeWeight(2.2);

        // Bow Shock (orange)
        p.stroke('#FFA500');
        p.beginShape();
        for (let a = -70; a <= 70; a += 2) {
          const k = 0.65; // controls nose sharpness
          const r = bowR * (1 - 0.35 * Math.cos(p.radians(a)));
          const x = -r * Math.cos(p.radians(a)) * k;
          const y = r * Math.sin(p.radians(a)) * 0.9;
          p.vertex(x, y);
        }
        p.endShape();

        // Magnetopause (cyan)
        p.stroke('#00FFFF');
        p.beginShape();
        for (let a = -70; a <= 70; a += 2) {
          const k = 0.75;
          const r = mpR * (1 - 0.28 * Math.cos(p.radians(a)));
          const x = -r * Math.cos(p.radians(a)) * k;
          const y = r * Math.sin(p.radians(a)) * 0.9;
          p.vertex(x, y);
        }
        p.endShape();
        p.pop();
      }

      p.draw = function() {
        p.background(0);
        // Solar wind (static in screen coordinates)
        drawSolarWind();

        // Rotate the entire Earth-magnetosphere system by seasonal tilt
        p.push();
        p.translate(p.width / 2, p.height / 2);
        const tilt = tiltDegreesForMonth(appState.month);
        p.rotate(tilt);

        drawMagnetotailRegion();
        drawBowShockAndMagnetopause();
        drawFieldLines();
        drawEarthAndFeatures();

        p.pop();
      };
    };

    appState.p5Instance = new p5(sketch);
  }

  function stop2D() {
    if (appState.p5Instance) {
      try { appState.p5Instance.remove(); } catch (e) {}
      appState.p5Instance = null;
    }
  }

  // -----------------------------
  // 3D VIEW (three.js) MANAGEMENT
  // -----------------------------
  function start3D() {
    stop2D();
    const canvas = ensureCanvas();

    const state = {
      renderer: null,
      scene: null,
      camera: null,
      controls: null,
      rafId: null,
      objects: {
        sun: null,
        earthGroup: null,
        earth: null,
        axis: null,
        magnetosphere: null,
        orbitLine: null
      },
      ellipse: {
        a: 120,
        e: 0.25,
        b: 0, // computed
        c: 0, // computed
        center: new THREE.Vector2() // computed
      }
    };

    const dpr = Math.min(window.devicePixelRatio || 1, 2);

    // Renderer
    const renderer = new THREE.WebGLRenderer({
      canvas: canvas,
      antialias: true
    });
    renderer.setPixelRatio(dpr);
    renderer.setSize(vizArea.clientWidth, vizArea.clientHeight, false);
    renderer.setClearColor(0x000000, 1);

    // Scene and Camera
    const scene = new THREE.Scene();
    scene.background = new THREE.Color(0x000000);

    const camera = new THREE.PerspectiveCamera(50, vizArea.clientWidth / vizArea.clientHeight, 0.1, 5000);
    camera.position.set(0, 150, 280);
    camera.lookAt(0, 0, 0);

    // Controls
    const controls = new THREE.OrbitControls(camera, renderer.domElement);
    controls.enableDamping = true;
    controls.dampingFactor = 0.06;
    controls.minDistance = 50;
    controls.maxDistance = 1500;

    // Lights
    const ambient = new THREE.AmbientLight(0xffffff, 0.5);
    scene.add(ambient);
    const sunLight = new THREE.PointLight(0xffffff, 2.0, 0, 2);
    sunLight.position.set(0, 0, 0);
    scene.add(sunLight);

    // Sun
    const sunGeo = new THREE.SphereGeometry(18, 32, 32);
    const sunMat = new THREE.MeshBasicMaterial({ color: 0xFFFF00 });
    const sun = new THREE.Mesh(sunGeo, sunMat);
    sun.position.set(0, 0, 0);
    scene.add(sun);

    // Solar wind arrows radiating from Sun
    const windArrows = new THREE.Group();
    const red = 0xFF0000;
    const dirs = [];
    for (let i = 0; i < 12; i++) {
      const t = i * (Math.PI * 2 / 12);
      dirs.push(new THREE.Vector3(Math.cos(t), 0, Math.sin(t)));
    }
    for (let i = 0; i < 6; i++) {
      const t = i * (Math.PI * 2 / 6);
      dirs.push(new THREE.Vector3(0, Math.sin(t), Math.cos(t)).normalize());
    }
    dirs.forEach(d => {
      const ah = new THREE.ArrowHelper(d.clone().normalize(), new THREE.Vector3(0,0,0), 50, red, 6, 3);
      windArrows.add(ah);
    });
    scene.add(windArrows);

    // Earth's orbit (ellipse with Sun at a focus)
    const a = state.ellipse.a;
    const e = state.ellipse.e;
    const b = a * Math.sqrt(1 - e * e);
    const c = a * e;
    state.ellipse.b = b;
    state.ellipse.c = c;
    state.ellipse.center.set(-c, 0); // Place Sun at the right focus (origin)

    const ellipseCurve = new THREE.EllipseCurve(
      state.ellipse.center.x, state.ellipse.center.y,
      a, b, 0, Math.PI * 2, false, 0
    );
    const points2D = ellipseCurve.getPoints(256);
    const pts3D = points2D.map(p => new THREE.Vector3(p.x, p.y, 0));
    const orbitGeom = new THREE.BufferGeometry().setFromPoints(pts3D);
    const orbitMat = new THREE.LineBasicMaterial({ color: 0xFFFFFF });
    const orbitLine = new THREE.LineLoop(orbitGeom, orbitMat);
    scene.add(orbitLine);

    // Earth group (earth + axis + magnetosphere)
    const earthGroup = new THREE.Group();
    scene.add(earthGroup);

    // Earth sphere
    const earthGeo = new THREE.SphereGeometry(6, 32, 32);
    const earthMat = new THREE.MeshStandardMaterial({ color: 0x4169E1, metalness: 0.1, roughness: 0.8 });
    const earth = new THREE.Mesh(earthGeo, earthMat);
    earthGroup.add(earth);

    // Earth's axis (tilted 23.5 degrees from orbit normal, fixed in world)
    const tiltRad = THREE.MathUtils.degToRad(23.5);
    const axisDir = new THREE.Vector3(Math.sin(tiltRad), 0, Math.cos(tiltRad)).normalize();
    const axis = new THREE.ArrowHelper(axisDir.clone(), new THREE.Vector3(0, 0, 0), 18, 0x00FF00, 4, 2);
    earthGroup.add(axis);

    // Magnetosphere (LatheGeometry revolved profile, transparent)
    function createMagnetosphereMesh() {
      // Profile points (radius, y) where y is along local Y (we will rotate so axis aligns with +X in world)
      const pts = [];
      // Dayside nose near Earth (small radius)
      pts.push(new THREE.Vector2(0.0, -4.5));  // near the axis
      pts.push(new THREE.Vector2(1.5, -3.0));
      pts.push(new THREE.Vector2(2.5, -1.8));
      pts.push(new THREE.Vector2(3.2, -0.8));
      pts.push(new THREE.Vector2(3.5,  0.0));
      // Transition into tail (radius decreases slowly while y increases)
      pts.push(new THREE.Vector2(3.2,  6.0));
      pts.push(new THREE.Vector2(2.7, 12.0));
      pts.push(new THREE.Vector2(2.1, 22.0));
      pts.push(new THREE.Vector2(1.6, 35.0));
      pts.push(new THREE.Vector2(1.0, 50.0));
      pts.push(new THREE.Vector2(0.4, 70.0));
      pts.push(new THREE.Vector2(0.0, 90.0));  // taper
      const latheGeom = new THREE.LatheGeometry(pts, 60);
      // Rotate so lathe axis (+Y) aligns with +X (tail axis we will point away from Sun)
      latheGeom.rotateZ(-Math.PI / 2);

      const latheMat = new THREE.MeshStandardMaterial({
        color: 0x0000FF,
        transparent: true,
        opacity: 0.4,
        roughness: 0.8,
        metalness: 0.0,
        side: THREE.DoubleSide
      });
      const mesh = new THREE.Mesh(latheGeom, latheMat);
      mesh.position.set(0, 0, 0);
      return mesh;
    }

    const magnetosphere = createMagnetosphereMesh();
    earthGroup.add(magnetosphere);

    // Save references
    state.renderer = renderer;
    state.scene = scene;
    state.camera = camera;
    state.controls = controls;
    state.objects.sun = sun;
    state.objects.earthGroup = earthGroup;
    state.objects.earth = earth;
    state.objects.axis = axis;
    state.objects.magnetosphere = magnetosphere;
    state.objects.orbitLine = orbitLine;

    // Update Earth position and magnetosphere orientation for current month
    function updateForMonth(month) {
      const theta = 2 * Math.PI * (month / 12);
      const x = state.ellipse.center.x + a * Math.cos(theta);
      const y = state.ellipse.center.y + b * Math.sin(theta);
      earthGroup.position.set(x, y, 0);

      // Axis direction remains fixed in world coordinates; ArrowHelper uses parent's local space.
      axis.setDirection(axisDir.clone());

      // Magnetosphere tail points away from Sun: align local +X with vector (Earth - Sun)
      const tailDir = earthGroup.position.clone().sub(new THREE.Vector3(0, 0, 0)).normalize();
      const q = new THREE.Quaternion().setFromUnitVectors(new THREE.Vector3(1, 0, 0), tailDir);
      magnetosphere.setRotationFromQuaternion(q);
    }

    updateForMonth(appState.month);

    // Animation loop
    function animate() {
      state.rafId = requestAnimationFrame(animate);
      controls.update();
      renderer.render(scene, camera);
    }
    animate();

    // Resize handler
    function onResize() {
      const w = vizArea.clientWidth;
      const h = vizArea.clientHeight;
      renderer.setPixelRatio(Math.min(window.devicePixelRatio || 1, 2));
      renderer.setSize(w, h, false);
      camera.aspect = w / h;
      camera.updateProjectionMatrix();
    }
    window.addEventListener('resize', onResize);

    // Expose stop/dispose
    state.dispose = function() {
      cancelAnimationFrame(state.rafId);
      window.removeEventListener('resize', onResize);
      controls.dispose();
      orbitGeom.dispose();
      orbitMat.dispose();
      sunGeo.dispose();
      sunMat.dispose();
      earthGeo.dispose();
      earthMat.dispose();
      if (magnetosphere.geometry) magnetosphere.geometry.dispose();
      if (magnetosphere.material) magnetosphere.material.dispose();
      renderer.dispose();
      // Clear canvas
      const gl = renderer.getContext();
      if (gl) {
        gl.getExtension('WEBGL_lose_context')?.loseContext();
      }
      // Optionally keep canvas for 2D or remove; we keep it and clear visually:
      const ctx2d = canvas.getContext('2d');
      if (ctx2d) {
        ctx2d.save();
        ctx2d.setTransform(1,0,0,1,0,0);
        ctx2d.clearRect(0,0,canvas.width,canvas.height);
        ctx2d.restore();
      }
    };

    // Method to update month externally
    state.updateMonth = updateForMonth;

    appState.three = state;
  }

  function stop3D() {
    if (appState.three) {
      try { appState.three.dispose(); } catch (e) {}
      appState.three = null;
    }
  }

  // ---------------------------
  // Event Handlers
  // ---------------------------
  function onMonthChange(val) {
    const m = Math.max(0, Math.min(11, parseInt(val, 10) || 0));
    appState.month = m;
    if (appState.view === '2d' && appState.p5Instance) {
      appState.p5Instance.redraw();
    } else if (appState.view === '3d' && appState.three) {
      appState.three.updateMonth(appState.month);
    }
  }

  slider.addEventListener('change', (e) => onMonthChange(e.target.value));
  slider.addEventListener('input', (e) => onMonthChange(e.target.value)); // live update while sliding

  btn2d.addEventListener('click', () => {
    if (appState.view !== '2d') {
      appState.view = '2d';
      setActiveButtons();
      start2D();
      // Ensure current month is reflected
      if (appState.p5Instance) appState.p5Instance.redraw();
    }
  });

  btn3d.addEventListener('click', () => {
    if (appState.view !== '3d') {
      appState.view = '3d';
      setActiveButtons();
      start3D();
      if (appState.three) appState.three.updateMonth(appState.month);
    }
  });

  // ---------------------------
  // Initial Load
  // ---------------------------
  setActiveButtons();
  // Start in 2D with slider-month set to current month
  start2D();
})();
</script>
</body>
</html>
