  <script>
    // Lanczos approximation for the Gamma function Γ(z)
    function gamma(z) {
      // Coefficients for g=7, n=9
      const p = [
        0.99999999999980993,
        676.5203681218851,
        -1259.1392167224028,
        771.32342877765313,
        -176.61502916214059,
        12.507343278686905,
        -0.13857109526572012,
        9.9843695780195716e-6,
        1.5056327351493116e-7
      ];
      if (z < 0.5) {
        // Reflection formula
        return Math.PI / (Math.sin(Math.PI * z) * gamma(1 - z));
      } else {
        z -= 1;
        let x = p[0];
        for (let i = 1; i < p.length; i++) {
          x += p[i] / (z + i);
        }
        const t = z + 7.5; // g + 0.5 with g=7
        return Math.sqrt(2 * Math.PI) * Math.pow(t, z + 0.5) * Math.exp(-t) * x;
      }
    }

    // Gamma PDF f(x; α, β) with scale β
    function gammaPDF(x, alpha, beta) {
      if (x < 0) return 0;
      if (x === 0) {
        if (alpha > 1) return 0;
        if (Math.abs(alpha - 1) < 1e-14) return 1 / beta;
        // For alpha < 1, the density diverges at 0; handle via small epsilon in x-grid
        return 0;
      }
      const coeff = 1 / (Math.pow(beta, alpha) * gamma(alpha));
      return Math.pow(x, alpha - 1) * Math.exp(-x / beta) * coeff;
    }

    // Generate a fixed x-grid (start slightly above 0 to avoid singularity when α<1)
    const X_MIN = 1e-4;
    const X_MAX = 25;
    const DX = 0.1;
    const xs = (() => {
      const arr = [];
      for (let x = X_MIN; x <= X_MAX + 1e-12; x += DX) {
        arr.push(x);
      }
      return arr;
    })();

    // Compute PDF and CDF via trapezoidal integration
    function computePDFandCDF(alpha, beta) {
      const pdf = xs.map(x => gammaPDF(x, alpha, beta));
      const cdf = new Array(xs.length).fill(0);
      let area = 0;
      for (let i = 1; i < xs.length; i++) {
        const dx = xs[i] - xs[i - 1];
        const trap = 0.5 * (pdf[i] + pdf[i - 1]) * dx;
        area += trap;
        cdf[i] = area;
      }
      // Normalize CDF to [0,1]
      if (area > 0) {
        for (let i = 0; i < cdf.length; i++) {
          cdf[i] /= area;
        }
      }
      return { pdf, cdf };
    }

    // Update MGF formula with current α and β
    function updateMGF(alpha, beta) {
      const aStr = alpha.toFixed(3);
      const bStr = beta.toFixed(3);
      const container = document.getElementById('mgf-formula');
      container.innerHTML = `$$ (1 - \\beta t)^{-\\alpha} = \\frac{1}{(1 - ${bStr} t)^{${aStr}}} $$`;
      if (window.MathJax && MathJax.typeset) {
        MathJax.typeset();
      }
    }

    // Create Plotly data and layout
    function createPlot(alpha, beta) {
      const mean = alpha * beta;
      const variance = alpha * beta * beta;
      const { pdf, cdf } = computePDFandCDF(alpha, beta);
      const yMean = gammaPDF(mean, alpha, beta);

      const data = [
        {
          type: 'scatter',
          mode: 'lines',
