  <script>
    (function() {
      // DOM elements
      const container = document.getElementById('canvas-container');
      const sliderA = document.getElementById('slider-a');
      const inputA  = document.getElementById('input-a');
      const sliderB = document.getElementById('slider-b');
      const inputB  = document.getElementById('input-b');
      const sliderC = document.getElementById('slider-c');
      const inputC  = document.getElementById('input-c');

      // Three.js essentials
      let scene, camera, renderer, controls, animationId;
      let solidMesh = null;
      let wireMesh = null;

      // Constants
      const FOV = 45;
      const NEAR = 0.1;
      const FAR = 1000;
      const INITIAL_CAMERA_POS = new THREE.Vector3(5, 5, 5);
      const range = 2.0; // as specified

      init();
      createAxes();
      updateParaboloid();
      animate();

      function init() {
        // Scene
        scene = new THREE.Scene();
        scene.background = new THREE.Color(0x000000);

        // Camera
        const width = container.clientWidth || window.innerWidth;
        const height = container.clientHeight || (window.innerHeight * 0.8);
        camera = new THREE.PerspectiveCamera(FOV, width / height, NEAR, FAR);
        camera.position.copy(INITIAL_CAMERA_POS);
        camera.lookAt(0, 0, 0);

        // Renderer
        renderer = new THREE.WebGLRenderer({ antialias: true });
        renderer.setPixelRatio(window.devicePixelRatio || 1);
        renderer.setSize(width, height);
        renderer.setClearColor(0x000000, 1);
        container.appendChild(renderer.domElement);

        // Controls
        controls = new THREE.OrbitControls(camera, renderer.domElement);
        controls.enableDamping = true;
        controls.dampingFactor = 0.08;
        controls.enablePan = true;
        controls.enableZoom = true;

        // Lights
        const ambient = new THREE.AmbientLight(0x404040, 1.2);
        scene.add(ambient);

        const dirLight = new THREE.DirectionalLight(0xffffff, 1.0);
        dirLight.position.set(5, 10, 7);
        scene.add(dirLight);

        // Events
        window.addEventListener('resize', onWindowResize);

        // Link controls
        linkSliderAndInput(sliderA, inputA, onParamsChanged);
        linkSliderAndInput(sliderB, inputB, onParamsChanged);
        linkSliderAndInput(sliderC, inputC, onParamsChanged);
      }

      function onWindowResize() {
        const width = container.clientWidth;
        const height = container.clientHeight;
        camera.aspect = width / height;
        camera.updateProjectionMatrix();
        renderer.setSize(width, height);
      }

      function animate() {
        animationId = requestAnimationFrame(animate);
        controls.update();
        renderer.render(scene, camera);
      }

      function createAxes() {
        const axisColor = 0x888888;
        const material = new THREE.LineBasicMaterial({ color: axisColor });

        // X axis
        let geometry = new THREE.BufferGeometry().setFromPoints([
          new THREE.Vector3(-5, 0, 0),
          new THREE.Vector3(5, 0, 0)
        ]);
        const xAxis = new THREE.Line(geometry, material);
        scene.add(xAxis);

        // Y axis
        geometry = new THREE.BufferGeometry().setFromPoints([
          new THREE.Vector3(0, -5, 0),
          new THREE.Vector3(0, 5, 0)
        ]);
        const yAxis = new THREE.Line(geometry, material);
        scene.add(yAxis);

        // Z axis
        geometry = new THREE.BufferGeometry().setFromPoints([
          new THREE.Vector3(0, 0, -5),
          new THREE.Vector3(0, 0, 5)
        ]);
        const zAxis = new THREE.Line(geometry, material);
        scene.add(zAxis);
      }

      function onParamsChanged() {
        updateParaboloid();
      }

      function getParams() {
        const a = clamp(parseFloat(sliderA.value), parseFloat(sliderA.min), parseFloat(sliderA.max));
        const b = clamp(parseFloat(sliderB.value), parseFloat(sliderB.min), parseFloat(sliderB.max));
        const c = clamp(parseFloat(sliderC.value), parseFloat(sliderC.min), parseFloat(sliderC.max));
        return { a, b, c };
      }

      function updateParaboloid() {
        const { a, b, c } = getParams();

        // Remove old meshes
        if (solidMesh) {
          scene.remove(solidMesh);
        }
        if (wireMesh) {
          scene.remove(wireMesh);
        }
        disposeMesh(solidMesh);
        disposeMesh(wireMesh);
        solidMesh = null;
        wireMesh = null;

        // Parametric function
        const paraboloidFunc = function(u, v, target) {
          const theta = u * 2.0 * Math.PI;
          const r = v * range;
          const x = a * r * Math.cos(theta);
          const y = b * r * Math.sin(theta);
          const z = c * (r * r);
          target.set(x, y, z);
        };

        // Geometry
        const slices = 50; // number of segments around
        const stacks = 50; // number of segments along radius
        const geometry = new THREE.ParametricGeometry(paraboloidFunc, slices, stacks);

        // Vertex coloring based on height (z)
        applyHeightVertexColors(geometry, c);

        // Solid mesh material
        const solidMaterial = new THREE.MeshPhongMaterial({
          side: THREE.DoubleSide,
          vertexColors: true,
          shininess: 40,
          specular: new THREE.Color(0x222222)
        });

        // Wireframe material
        const wireMaterial = new THREE.MeshBasicMaterial({
          color: 0xcccccc,
          wireframe: true
        });

        // Meshes
        solidMesh = new THREE.Mesh(geometry, solidMaterial);
        wireMesh = new THREE.Mesh(geometry, wireMaterial);

        scene.add(solidMesh);
        scene.add(wireMesh);
      }

      function applyHeightVertexColors(geometry, c) {
        const posAttr = geometry.getAttribute('position');
        const count = posAttr.count;
        const colors = new Float32Array(count * 3);

        // Colors: z=0 -> #FFD700 (255,215,0), z=max -> #FF4500 (255,69,0)
        const c1 = { r: 255/255, g: 215/255, b: 0/255 };
        const c2 = { r: 255/255, g: 69/255,  b: 0/255 };

        const zMax = c * range * range;
        for (let i = 0; i < count; i++) {
          const z = posAttr.getZ(i);
          const t = zMax > 0 ? Math.max(0, Math.min(1, z / zMax)) : 0;

          const r = c1.r + (c2.r - c1.r) * t;
          const g = c1.g + (c2.g - c1.g) * t;
          const b = c1.b + (c2.b - c1.b) * t;

          colors[i * 3 + 0] = r;
          colors[i * 3 + 1] = g;
          colors[i * 3 + 2] = b;
        }

        geometry.setAttribute('color', new THREE.Float32BufferAttribute(colors, 3));
        geometry.attributes.color.needsUpdate = true;
      }

      function disposeMesh(mesh) {
        if (!mesh) return;
        if (mesh.geometry) mesh.geometry.dispose();
        if (Array.isArray(mesh.material)) {
          mesh.material.forEach(m => m.dispose && m.dispose());
        } else if (mesh.material) {
          mesh.material.dispose && mesh.material.dispose();
        }
      }

      function clamp(val, min, max) {
        return Math.max(min, Math.min(max, val));
      }

      function linkSliderAndInput(slider, input, onChange) {
        // Reflect slider -> input
        slider.addEventListener('input', () => {
          input.value = slider.value;
          onChange();
        });

        // Reflect input -> slider
        input.addEventListener('input', () => {
          // Ensure value respects min/max
          let v = parseFloat(input.value);
          if (isNaN(v)) return;
          v = clamp(v, parseFloat(input.min), parseFloat(input.max));
          // Align to step of slider/input
          const step = parseFloat(input.step) || 0.1;
          v = Math.round(v / step) * step;
          input.value = v.toFixed(step < 1 ? (String(step).split('.')[1] || '').length : 0);
          slider.value = input.value;
          onChange();
        });
      }
    })();
  </script>
