  // Helper to format half-integers like 1.5 -> "3/2"
  function formatHalf(val) {
    const isHalf = Math.abs(val * 2 - Math.round(val * 2)) < 1e-9 ? true : false;
    const twice = Math.round(val * 2);
    if (twice % 2 !== 0) {
      // half-integer
      const num = twice;
      const sign = num < 0 ? "-" : "";
      return sign + Math.abs(num) + "/2";
    } else {
      return String(val);
    }
  }

  function createFixedButtons(container, values, selectedVal, onClick) {
    container.innerHTML = "";
    values.forEach(v => {
      const btn = document.createElement("button");
      btn.textContent = (Math.abs(v * 2 - Math.round(v * 2)) < 1e-9 && (Math.round(v*2) % 2 !== 0))
        ? formatHalf(v)
        : (Number.isInteger(v) ? String(v) : String(v));
      btn.dataset.value = String(v);
      if (v === selectedVal) btn.classList.add("selected");
      btn.addEventListener("click", () => onClick(v, btn));
      container.appendChild(btn);
    });
  }

  function createDynamicButtons(container, values, selectedVal, onClick) {
    container.innerHTML = "";
    values.forEach(v => {
      const btn = document.createElement("button");
      btn.textContent = (Math.abs(v * 2 - Math.round(v * 2)) < 1e-9 && (Math.round(v*2) % 2 !== 0))
        ? formatHalf(v)
        : (Number.isInteger(v) ? String(v) : v.toFixed(1));
      btn.dataset.value = String(v);
      if (v === selectedVal) btn.classList.add("selected");
      btn.addEventListener("click", () => onClick(v, btn));
      container.appendChild(btn);
    });
  }

  // Generate m values from -j to +j in steps of 1 (j may be half-integer)
  function mValuesFor(j) {
    const kmax = Math.round(2 * j); // j is multiple of 0.5
    const vals = [];
    for (let k = -kmax; k <= kmax; k += 2) {
      vals.push(k / 2);
    }
    return vals;
  }

  function defaultMFor(j) {
    // smallest non-negative allowed value
    const vals = mValuesFor(j);
    const nonNeg = vals.filter(x => x >= 0);
    if (nonNeg.length > 0) {
      return Math.min(...nonNeg);
    }
    // fallback to the largest (shouldn't happen)
    return vals[vals.length - 1];
  }

  function jValuesFor(j1, j2) {
    const jmin = Math.abs(j1 - j2);
    const jmax = j1 + j2;
    const vals = [];
    // step by 1 but respect half-integer parity
    const startTwice = Math.round(2 * jmin);
    const endTwice = Math.round(2 * jmax);
    for (let k = startTwice; k <= endTwice; k += 2) {
      vals.push(k / 2);
    }
    return vals;
  }

  const state = {
    j1: 1.5,
    m1: 0.5,
    j2: 1.0,
    m2: 0.0,
    j: 2.5,
    isAnimating: false,
    phi_z: 0,
    phi_j: 0
  };

  // DOM references
  const j1Div = document.getElementById("j1-selector");
  const m1Div = document.getElementById("m1-selector");
  const j2Div = document.getElementById("j2-selector");
  const m2Div = document.getElementById("m2-selector");
  const jDiv  = document.getElementById("j-selector");
  const btnPlayPause = document.getElementById("btn-play-pause");
  const btnReset = document.getElementById("btn-reset");

  function setButtonSelection(container, btn) {
    Array.from(container.querySelectorAll("button")).forEach(b => b.classList.remove("selected"));
    if (btn) btn.classList.add("selected");
  }

  function setupSelectors() {
    // j1 fixed buttons
    createFixedButtons(j1Div, [0.5, 1.0, 1.5, 2.0], state.j1, (val, btn) => {
      state.j1 = val;
      setButtonSelection(j1Div, btn);
      // update dependent m1, j
      const mVals = mValuesFor(state.j1);
      state.m1 = defaultMFor(state.j1);
      createDynamicButtons(m1Div, mVals, state.m1, onM1Clicked);

      const jVals = jValuesFor(state.j1, state.j2);
      state.j = Math.max(...jVals); // default largest
      createDynamicButtons(jDiv, jVals, state.j, onJClicked);

      resetAnimation();
      recalcScene();
      renderOnce();
    });

    // m1 dynamic
    createDynamicButtons(m1Div, mValuesFor(state.j1), state.m1, onM1Clicked);

    // j2 fixed buttons
    createFixedButtons(j2Div, [0.5, 1.0, 1.5, 2.0], state.j2, (val, btn) => {
      state.j2 = val;
      setButtonSelection(j2Div, btn);
      // update dependent m2, j
      const mVals = mValuesFor(state.j2);
      state.m2 = defaultMFor(state.j2);
      createDynamicButtons(m2Div, mVals, state.m2, onM2Clicked);

      const jVals = jValuesFor(state.j1, state.j2);
      state.j = Math.max(...jVals); // default largest
      createDynamicButtons(jDiv, jVals, state.j, onJClicked);

      resetAnimation();
      recalcScene();
      renderOnce();
    });

    // m2 dynamic
    createDynamicButtons(m2Div, mValuesFor(state.j2), state.m2, onM2Clicked);

    // j dynamic
    const jValsInit = jValuesFor(state.j1, state.j2);
    createDynamicButtons(jDiv, jValsInit, state.j, onJClicked);
  }

  function onM1Clicked(val, btn) {
    state.m1 = val;
    setButtonSelection(m1Div, btn);
    resetAnimation();
    recalcScene();
    renderOnce();
  }
  function onM2Clicked(val, btn) {
    state.m2 = val;
    setButtonSelection(m2Div, btn);
    resetAnimation();
    recalcScene();
    renderOnce();
  }
  function onJClicked(val, btn) {
    state.j = val;
    setButtonSelection(jDiv, btn);
    resetAnimation();
    recalcScene();
    renderOnce();
  }

  btnPlayPause.addEventListener("click", () => {
    state.isAnimating = !state.isAnimating;
    btnPlayPause.textContent = state.isAnimating ? "❚❚" : "▶";
  });
  btnReset.addEventListener("click", () => {
    resetAnimation();
    updateVectors();
    renderOnce();
  });

  function resetAnimation() {
    state.isAnimating = false;
    state.phi_z = 0;
    state.phi_j = 0;
    btnPlayPause.textContent = "▶";
  }

  // =========================
  // 3D Setup (three.js)
  // =========================

  const canvas = document.getElementById("main-canvas");
  const renderer = new THREE.WebGLRenderer({ canvas, antialias: true });
  renderer.setPixelRatio(Math.min(window.devicePixelRatio || 1, 2));
  renderer.setClearColor(0xffffff, 1);

  const scene = new THREE.Scene();

  const camera = new THREE.PerspectiveCamera(45, 1, 0.1, 1000);
  camera.up.set(0, 0, 1); // Z is up
  camera.position.set(6, -7, 5);

  const controls = new OrbitControls(camera, renderer.domElement);
  controls.enableDamping = true;
  controls.dampingFactor = 0.08;
  controls.target.set(0, 0, 0);

  // Lighting
  const ambient = new THREE.AmbientLight(0xffffff, 0.7);
  scene.add(ambient);
  const dirLight = new THREE.DirectionalLight(0xffffff, 0.6);
  dirLight.position.set(5, -5, 8);
  scene.add(dirLight);

  // Optional: a faint axis helper
  const axes = new THREE.AxesHelper(3.5);
  axes.material.depthTest = false;
  axes.renderOrder = 1;
  scene.add(axes);

  // Dynamic objects
  let arrowJ = null;
  let arrowJ1 = null;
  let arrowJ2 = null;
  let coneJ = null;
  let coneJ1 = null;
  let coneJ2 = null;

  function ensureArrows() {
    if (!arrowJ) {
      arrowJ = new THREE.ArrowHelper(new THREE.Vector3(0,0,1), new THREE.Vector3(0,0,0), 1, 0x111111, 0.25, 0.12);
      scene.add(arrowJ);
    }
    if (!arrowJ1) {
      arrowJ1 = new THREE.ArrowHelper(new THREE.Vector3(1,0,0), new THREE.Vector3(0,0,0), 1, 0xcc3333, 0.25, 0.12);
      scene.add(arrowJ1);
    }
    if (!arrowJ2) {
      arrowJ2 = new THREE.ArrowHelper(new THREE.Vector3(0,1,0), new THREE.Vector3(0,0,0), 1, 0x3366cc, 0.25, 0.12);
      scene.add(arrowJ2);
    }
  }

  function disposeCone(mesh) {
    if (!mesh) return;
    scene.remove(mesh);
    if (mesh.geometry) mesh.geometry.dispose();
    if (mesh.material) mesh.material.dispose();
  }

  function makeCone(axis, height, radius, colorHex) {
    const geo = new THREE.ConeGeometry(radius, height, 96, 1, true);
    const mat = new THREE.MeshPhongMaterial({
      color: colorHex,
      transparent: true,
      opacity: getComputedStyle(document.documentElement).getPropertyValue('--cone-alpha') ? parseFloat(getComputedStyle(document.documentElement).getPropertyValue('--cone-alpha')) : 0.25,
      side: THREE.DoubleSide
    });
    const cone = new THREE.Mesh(geo, mat);
    // Orient: default cone axis is +Y. Rotate it so +Y aligns with 'axis'.
    const axisNorm = axis.clone().normalize();
    const q = new THREE.Quaternion().setFromUnitVectors(new THREE.Vector3(0,1,0), axisNorm);
    cone.quaternion.copy(q);
    // Place apex at origin (apex is at local +Y * (height/2))
    const apexLocal = new THREE.Vector3(0, height/2, 0).applyQuaternion(q);
    cone.position.copy(apexLocal).multiplyScalar(-1);
    return cone;
  }

  function setRendererSize() {
    const container = document.getElementById("canvas-container");
    const width = container.clientWidth;
    const height = container.clientHeight;
    renderer.setSize(width, height, false);
    camera.aspect = width / height;
    camera.updateProjectionMatrix();
  }
  window.addEventListener("resize", setRendererSize);
  setRendererSize();

  // =========================
  // Physics / Geometry
  // =========================

  function L_of(q) {
    return Math.sqrt(q * (q + 1));
  }

  function clamp(x, a, b) { return Math.max(a, Math.min(b, x)); }

  function recalcScene() {
    ensureArrows();

    // Compute magnitudes
    const L1 = L_of(state.j1);
    const L2 = L_of(state.j2);
    const LJ = L_of(state.j);

    // Determine model: coupled if j >= j1 and j >= j2
    const isCoupled = (state.j >= state.j1) && (state.j >= state.j2);

    // Cones are static per state: rebuild them
    disposeCone(coneJ);
    disposeCone(coneJ1);
    disposeCone(coneJ2);
    coneJ = null; coneJ1 = null; coneJ2 = null;

    if (isCoupled && LJ > 1e-8) {
      // Total m
      const mTot = state.m1 + state.m2;
      const cosThetaJ = clamp(LJ === 0 ? 1 : (mTot / LJ), -1, 1);
      const thetaJ = Math.acos(cosThetaJ);
      // J cone: axis Z, height = LJ, radius = LJ * tan(thetaJ)
      const rJ = Math.tan(thetaJ) * LJ;
      if (isFinite(rJ) && rJ >= 0) {
        coneJ = makeCone(new THREE.Vector3(0,0,1), LJ, rJ, 0x111111);
        scene.add(coneJ);
      }

      // J1 and J2 cones around J: angles from law of cosines
      const cosT1 = clamp((LJ*LJ + L1*L1 - L2*L2) / (2 * LJ * L1), -1, 1);
      const cosT2 = clamp((LJ*LJ + L2*L2 - L1*L1) / (2 * LJ * L2), -1, 1);
      const theta1 = Math.acos(cosT1);
      const theta2 = Math.acos(cosT2);
      const r1 = Math.tan(theta1) * L1;
      const r2 = Math.tan(theta2) * L2;

      // For cones around J axis we will build them when drawing vectors
      // but since J axis precesses, cones should be aligned with current J direction each frame.
      // However the requirement says "cones are static... but must be re-created and re-oriented whenever quantum numbers change".
      // We'll align them now with the current J direction (phi_z = current state.phi_z).

      // Precompute J direction for current phi_z
      const dirJ = jDirectionVector(LJ, mTot, state.phi_z).normalize();
      const axisJ = dirJ.clone(); // unit

      if (isFinite(r1) && r1 >= 0) {
        coneJ1 = makeCone(axisJ, L1, r1, 0xcc3333);
        scene.add(coneJ1);
      }
      if (isFinite(r2) && r2 >= 0) {
        coneJ2 = makeCone(axisJ, L2, r2, 0x3366cc);
        scene.add(coneJ2);
      }

      // Set visibility: all arrows visible in coupled model
      arrowJ.visible  = true;
      arrowJ1.visible = true;
      arrowJ2.visible = true;
    } else {
      // Uncoupled: draw only J1 and J2 cones around Z
      const cosT1u = clamp(L1 === 0 ? 1 : (state.m1 / L1), -1, 1);
      const cosT2u = clamp(L2 === 0 ? 1 : (state.m2 / L2), -1, 1);
      const theta1u = Math.acos(cosT1u);
      const theta2u = Math.acos(cosT2u);
      const r1u = Math.tan(theta1u) * L1;
      const r2u = Math.tan(theta2u) * L2;

      if (isFinite(r1u) && r1u >= 0) {
        coneJ1 = makeCone(new THREE.Vector3(0,0,1), L1, r1u, 0xcc3333);
        scene.add(coneJ1);
      }
      if (isFinite(r2u) && r2u >= 0) {
        coneJ2 = makeCone(new THREE.Vector3(0,0,1), L2, r2u, 0x3366cc);
        scene.add(coneJ2);
      }

      // Hide J arrow and cone (not drawn in uncoupled model)
      arrowJ.visible = false;
      arrowJ1.visible = true;
      arrowJ2.visible = true;
    }

    updateVectors();
  }

  function jDirectionVector(LJ, mTot, phiZ) {
    // Direction of J precessing around Z with fixed polar angle thetaJ
    if (LJ < 1e-8) return new THREE.Vector3(0,0,1); // arbitrary
    const cosThetaJ = clamp(mTot / LJ, -1, 1);
    const thetaJ = Math.acos(cosThetaJ);
    const s = Math.sin(thetaJ), c = Math.cos(thetaJ);
    const dir = new THREE.Vector3(s * Math.cos(phiZ), s * Math.sin(phiZ), c);
    return dir;
  }

  function updateVectors() {
    const L1 = L_of(state.j1);
    const L2 = L_of(state.j2);
    const LJ = L_of(state.j);
    const mTot = state.m1 + state.m2;

    const isCoupled = (state.j >= state.j1) && (state.j >= state.j2) && (LJ > 1e-8);

    // Update cones orientation if coupled (cones around J must follow J axis on state changes)
    if (isCoupled) {
      const cosThetaJ = clamp(LJ === 0 ? 1 : (mTot / LJ), -1, 1);
      const thetaJ = Math.acos(cosThetaJ);
      const rJ = Math.tan(thetaJ) * LJ;
      const dirJ = jDirectionVector(LJ, mTot, state.phi_z).normalize();

      if (coneJ) {
        // Align J cone to Z (already aligned) - J cone is around Z; no update needed.
      }
      // Re-align J1/J2 cones around current J direction
      if (coneJ1) {
        // Rebuild orientation without recreating geometry: set quaternion/position from axis
        const h = L1;
        const axis = dirJ.clone().normalize();
        const q = new THREE.Quaternion().setFromUnitVectors(new THREE.Vector3(0,1,0), axis);
        coneJ1.quaternion.copy(q);
        const apexLocal = new THREE.Vector3(0, h/2, 0).applyQuaternion(q);
        coneJ1.position.copy(apexLocal).multiplyScalar(-1);
      }
      if (coneJ2) {
        const h = L2;
        const axis = dirJ.clone().normalize();
        const q = new THREE.Quaternion().setFromUnitVectors(new THREE.Vector3(0,1,0), axis);
        coneJ2.quaternion.copy(q);
        const apexLocal = new THREE.Vector3(0, h/2, 0).applyQuaternion(q);
        coneJ2.position.copy(apexLocal).multiplyScalar(-1);
      }
    }

    // Update arrow directions and lengths
    if (isCoupled) {
      // J vector
      const dirJ = jDirectionVector(LJ, mTot, state.phi_z).normalize();
      arrowJ.visible = true;
      arrowJ.setDirection(dirJ);
      arrowJ.setLength(LJ, Math.min(0.28 * LJ, 0.5), Math.min(0.12 * LJ, 0.25));

      // Build local frame (eX', eY', eZ'=dirJ)
      const eZp = dirJ.clone().normalize();
      let tmp = new THREE.Vector3(0,0,1).cross(eZp);
      if (tmp.length() < 1e-6) tmp = new THREE.Vector3(1,0,0).cross(eZp); // fallback if parallel
      const eXp = tmp.normalize();
      const eYp = eZp.clone().cross(eXp).normalize();

      // Angles from law of cosines
      const cosT1 = clamp((LJ*LJ + L1*L1 - L2*L2) / (2 * LJ * L1), -1, 1);
      const cosT2 = clamp((LJ*LJ + L2*L2 - L1*L1) / (2 * LJ * L2), -1, 1);
      const sinT1 = Math.sqrt(Math.max(0, 1 - cosT1*cosT1));
      const sinT2 = Math.sqrt(Math.max(0, 1 - cosT2*cosT2));
      const a1 = L1 * cosT1;
      const a2 = L2 * cosT2;
      // Common transverse magnitude t should be equal for closure
      const t = L1 * sinT1; // should equal L2 * sinT2

      const cosPhi = Math.cos(state.phi_j);
      const sinPhi = Math.sin(state.phi_j);
      const transverse = eXp.clone().multiplyScalar(cosPhi).add(eYp.clone().multiplyScalar(sinPhi));

      const v1 = eZp.clone().multiplyScalar(a1).add(transverse.clone().multiplyScalar(t));
      const v2 = eZp.clone().multiplyScalar(a2).add(transverse.clone().multiplyScalar(-t));
      const vJ = v1.clone().add(v2); // should match dirJ*LJ

      // Update arrows
      arrowJ1.visible = true;
      arrowJ2.visible = true;
      arrowJ1.setDirection(v1.clone().normalize());
      arrowJ1.setLength(v1.length(), Math.min(0.28 * L1, 0.5), Math.min(0.12 * L1, 0.25));
      arrowJ2.setDirection(v2.clone().normalize());
      arrowJ2.setLength(v2.length(), Math.min(0.28 * L2, 0.5), Math.min(0.12 * L2, 0.25));
    } else {
      // Uncoupled: each Ji precesses around Z independently with fixed polar angle
      // J not drawn
      arrowJ.visible = false;

      // J1
      const cosT1 = clamp(L1 === 0 ? 1 : (state.m1 / L1), -1, 1);
      const theta1 = Math.acos(cosT1);
      const s1 = Math.sin(theta1), c1 = Math.cos(theta1);
      const dir1 = new THREE.Vector3(s1 * Math.cos(state.phi_z), s1 * Math.sin(state.phi_z), c1).normalize();
      arrowJ1.visible = true;
      arrowJ1.setDirection(dir1);
      arrowJ1.setLength(L1, Math.min(0.28 * L1, 0.5), Math.min(0.12 * L1, 0.25));

      // J2
      const cosT2 = clamp(L2 === 0 ? 1 : (state.m2 / L2), -1, 1);
      const theta2 = Math.acos(cosT2);
      const s2 = Math.sin(theta2), c2 = Math.cos(theta2);
      const dir2 = new THREE.Vector3(s2 * Math.cos(state.phi_z), s2 * Math.sin(state.phi_z), c2).normalize();
      arrowJ2.visible = true;
      arrowJ2.setDirection(dir2);
      arrowJ2.setLength(L2, Math.min(0.28 * L2, 0.5), Math.min(0.12 * L2, 0.25));
    }
  }

  // =========================
  // Animation Loop
  // =========================

  function animate() {
    requestAnimationFrame(animate);
    if (state.isAnimating) {
      state.phi_z += 0.01; // precession around Z
      state.phi_j += 0.03; // precession around J (coupled model)
    }
    updateVectors();
    controls.update();
    renderer.render(scene, camera);
  }

  function renderOnce() {
    controls.update();
    renderer.render(scene, camera);
  }

  // =========================
  // Init
  // =========================

  setupSelectors();
  recalcScene();
  renderOnce();
  animate();
</script>
</body>
</html>
