        let t = (a += 0x6D2B79F5);
        t = Math.imul(t ^ (t >>> 15), t | 1);
        t ^= t + Math.imul(t ^ (t >>> 7), t | 61);
        return ((t ^ (t >>> 14)) >>> 0) / 4294967296;
      };
    }

    const SCATTERED_FIXED_SEED = 123456789;
    let rng = mulberry32(SCATTERED_FIXED_SEED);

    function resetRng(seed = SCATTERED_FIXED_SEED) {
      rng = mulberry32(seed);
    }

    function random() {
      return rng();
    }

    function createEmptyGrid(r, c) {
      const g = new Array(r);
      for (let y = 0; y < r; y++) {
        g[y] = new Array(c).fill(EMPTY);
      }
      return g;
    }

    function cloneGrid(g) {
      return g.map(row => row.slice());
    }

    function ensureStartOpen(g) {
      if (g[start.y] && g[start.y][start.x] !== undefined) {
        g[start.y][start.x] = EMPTY;
      }
    }

    // Pattern generators
    function generateNoneGrid() {
      const g = createEmptyGrid(rows, cols);
      ensureStartOpen(g);
      return g;
    }

    function generateBoxGrid() {
      const g = createEmptyGrid(rows, cols);
      for (let y = 0; y < rows; y++) {
        for (let x = 0; x < cols; x++) {
          if (y === 0 || y === rows - 1 || x === 0 || x === cols - 1) {
            g[y][x] = OBSTACLE;
          }
        }
      }
      ensureStartOpen(g);
      return g;
    }

    function generateScatteredGridWithMinReachable(targetMin = 61) {
      // Use fixed seed to keep the same scattered pattern every time.
      // We'll try a sequence of probabilities near ~25% until reachable >= targetMin.
      resetRng(SCATTERED_FIXED_SEED);
      const probs = [0.25, 0.22, 0.20, 0.18, 0.16, 0.14, 0.12, 0.10, 0.08, 0.05];
      for (let pIdx = 0; pIdx < probs.length; pIdx++) {
        // regenerate RNG so each probability test is deterministic across runs
        resetRng(SCATTERED_FIXED_SEED + pIdx * 101);
        const g = createEmptyGrid(rows, cols);
        for (let y = 0; y < rows; y++) {
          for (let x = 0; x < cols; x++) {
            g[y][x] = (random() < probs[pIdx]) ? OBSTACLE : EMPTY;
          }
        }
        ensureStartOpen(g);
        const reachable = countReachable(g, start.x, start.y);
        if (reachable >= targetMin) {
          return g;
        }
      }
      // Fallback: if none met the target, return the least obstructed last one
      resetRng(SCATTERED_FIXED_SEED + 999);
      const g = createEmptyGrid(rows, cols);
      for (let y = 0; y < rows; y++) {
        for (let x = 0; x < cols; x++) {
          g[y][x] = (random() < 0.08) ? OBSTACLE : EMPTY;
        }
      }
      ensureStartOpen(g);
      return g;
    }

    function countReachable(g, sx, sy) {
      if (g[sy][sx] === OBSTACLE) return 0;
      const visited = create2DBool(rows, cols, false);
      const q = [{ x: sx, y: sy }];
      visited[sy][sx] = true;
      const dirs = [
        { dx: 0, dy: -1 }, // N
        { dx: -1, dy: 0 }, // W
        { dx: 0, dy: 1 },  // S
        { dx: 1, dy: 0 },  // E
      ];
      let count = 0;
      while (q.length > 0) {
        const { x, y } = q.shift();
        count++;
        for (const d of dirs) {
          const nx = x + d.dx, ny = y + d.dy;
          if (nx >= 0 && nx < cols && ny >= 0 && ny < rows) {
            if (!visited[ny][nx] && g[ny][nx] !== OBSTACLE) {
              visited[ny][nx] = true;
              q.push({ x: nx, y: ny });
            }
          }
        }
      }
      return count;
    }

    function create2DBool(r, c, val) {
      const a = new Array(r);
      for (let y = 0; y < r; y++) {
        a[y] = new Array(c).fill(val);
      }
      return a;
    }

    // Flood Fill algorithms
    function floodFillRecursiveHistory(g, sx, sy) {
      const history = [];
      const visited = create2DBool(rows, cols, false);
      const dirs = [
        { dx: 0, dy: -1 }, // N
        { dx: -1, dy: 0 }, // W
        { dx: 0, dy: 1 },  // S
        { dx: 1, dy: 0 }   // E
      ];
      function dfs(x, y) {
        if (x < 0 || x >= cols || y < 0 || y >= rows) return;
        if (visited[y][x]) return;
        if (g[y][x] === OBSTACLE) return;
        visited[y][x] = true;
        history.push({ x, y });
        // Order: N, W, S, E
        for (const d of dirs) {
          dfs(x + d.dx, y + d.dy);
        }
      }
      dfs(sx, sy);
      return history;
    }

    function floodFillIterativeHistory(g, sx, sy) {
      const history = [];
      const visited = create2DBool(rows, cols, false);
      const q = [];
      if (g[sy][sx] === OBSTACLE) return history;
      q.push({ x: sx, y: sy });
      visited[sy][sx] = true;
      const dirs = [
        { dx: 0, dy: -1 }, // N
        { dx: -1, dy: 0 }, // W
        { dx: 0, dy: 1 },  // S
        { dx: 1, dy: 0 }   // E
      ];
      while (q.length > 0) {
        const { x, y } = q.shift();
        history.push({ x, y });
        for (const d of dirs) {
          const nx = x + d.dx, ny = y + d.dy;
          if (nx >= 0 && nx < cols && ny >= 0 && ny < rows) {
            if (!visited[ny][nx] && g[ny][nx] !== OBSTACLE) {
              visited[ny][nx] = true;
              q.push({ x: nx, y: ny });
            }
          }
        }
      }
      return history;
    }

    function recomputeHistories() {
      recursiveHistory = floodFillRecursiveHistory(grid, start.x, start.y);
      iterativeHistory = floodFillIterativeHistory(grid, start.x, start.y);
    }

    function setSliderMaxAndValue(initialValue = 0) {
      const maxSteps = Math.max(recursiveHistory.length, iterativeHistory.length);
      stepsSlider.max = String(maxSteps);
      stepsSlider.min = "0";
      stepsSlider.step = "1";
      const val = Math.max(0, Math.min(initialValue, maxSteps));
      stepsSlider.value = String(val);
      stepsValue.textContent = `+ ${val}`;
    }

    function setActiveButton(activeId) {
      [btnNone, btnBox, btnScattered].forEach(btn => btn.classList.remove('active'));
      const map = {
        'none': btnNone,
        'box': btnBox,
        'scattered': btnScattered
      };
      if (map[activeId]) {
        map[activeId].classList.add('active');
      }
    }

    function initializePattern(pattern, options = {}) {
      // pattern: 'scattered' | 'box' | 'none'
      if (pattern === 'scattered') {
        grid = generateScatteredGridWithMinReachable(61);
      } else if (pattern === 'box') {
        grid = generateBoxGrid();
      } else {
        grid = generateNoneGrid();
      }
      recomputeHistories();
      if (options.setInitial61) {
        // On initial load, ensure we can set to 61
        setSliderMaxAndValue(61);
      } else {
        setSliderMaxAndValue(0);
      }
      // Redraw canvases to reflect new state
      recSketch.redraw();
      itrSketch.redraw();
    }

    // p5 sketches (instance mode)
    let recSketch, itrSketch;

    function makeSketch(which) {
      // which: 'recursive' | 'iterative'
      return function (p) {
        p.setup = function () {
          const canvas = p.createCanvas(canvasSize, canvasSize);
          // Mount inside proper container
          if (which === 'recursive') {
            canvas.parent(document.getElementById('canvas-recursive'));
          } else {
            canvas.parent(document.getElementById('canvas-iterative'));
          }
          p.noLoop();
        };

        p.draw = function () {
          drawGrid(p, which);
        };
      };
    }

    function drawGrid(p, which) {
      p.background(255);
      p.stroke(0);
      p.strokeWeight(1);

      // Draw base cells (empty/obstacle)
      for (let y = 0; y < rows; y++) {
        for (let x = 0; x < cols; x++) {
          if (grid[y][x] === OBSTACLE) {
            p.fill(0);
          } else {
            p.fill(255);
          }
          p.rect(x * cellSize, y * cellSize, cellSize, cellSize);
        }
      }

      // Overlay filled cells up to step N
      const N = parseInt(stepsSlider.value, 10) || 0;
      const hist = (which === 'recursive') ? recursiveHistory : iterativeHistory;
      const upto = Math.min(N, hist.length);
      p.fill(0, 0, 255);
      p.stroke(0);

      for (let i = 0; i < upto; i++) {
        const { x, y } = hist[i];
        // Draw filled cell (blue)
        p.rect(x * cellSize, y * cellSize, cellSize, cellSize);
      }
    }

    // Event listeners
    stepsSlider.addEventListener('input', () => {
      const val = parseInt(stepsSlider.value, 10) || 0;
      stepsValue.textContent = `+ ${val}`;
      recSketch.redraw();
      itrSketch.redraw();
    });

    btnNone.addEventListener('click', () => {
      setActiveButton('none');
      initializePattern('none', { setInitial61: false });
    });

    btnBox.addEventListener('click', () => {
      setActiveButton('box');
      initializePattern('box', { setInitial61: false });
    });

    btnScattered.addEventListener('click', () => {
      setActiveButton('scattered');
      initializePattern('scattered', { setInitial61: false });
    });

    // Initial load
    // Prepare initial scattered grid and histories before creating sketches (to avoid an early draw with empty grid)
    grid = generateScatteredGridWithMinReachable(61);
    recomputeHistories();

    // Create p5 instances
    recSketch = new p5(makeSketch('recursive'));
    itrSketch = new p5(makeSketch('iterative'));

    // Set initial slider to 61 (as required) and update canvases
    setSliderMaxAndValue(61);
    recSketch.redraw();
    itrSketch.redraw();
  </script>
</body>
</html>
