    <div class="row">
      <label for="slider-dt">Δt</label>
      <input type="range" id="slider-dt" min="0.05" max="0.4" step="0.01" value="0.15">
    </div>
  </div>

  <div id="canvas-container">
    <div class="canvas-wrap" id="canvas-holder"></div>
    <div class="note">Adjust the sliders to recompute the static stroboscopic trajectory. The canvas updates only when you move a slider.</div>
  </div>

  <!-- CDN for p5.js -->
  <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>

  <script>
    // Slider references
    let sliderK, sliderTime, sliderVx, sliderDt;

    // p5 globals
    let cnv;
    const CANVAS_W = 700;
    const CANVAS_H = 400;
    const PADDING = 40;

    function setup() {
      cnv = createCanvas(CANVAS_W, CANVAS_H);
      cnv.parent("canvas-holder");
      noLoop();

      // Get slider elements
      sliderK = document.getElementById("slider-k");
      sliderTime = document.getElementById("slider-time");
      sliderVx = document.getElementById("slider-vx");
      sliderDt = document.getElementById("slider-dt");

      // Attach input listeners to trigger redraw
      [sliderK, sliderTime, sliderVx, sliderDt].forEach(el => {
        el.addEventListener("input", () => {
          redraw();
        });
      });

      // Initial draw
      redraw();
    }

    function draw() {
      // Read current slider values
      const k = parseFloat(sliderK.value);
      const totalTime = parseFloat(sliderTime.value);
      const vx = parseFloat(sliderVx.value);
      const dtStrobe = parseFloat(sliderDt.value);

      // Constants
      const g = 9.8;    // m/s^2
      const y0 = 10;    // m
      const vy0 = 0;    // m/s
      const x0 = 0;     // m

      // Compute trajectory bounce-by-bounce
      const pathPoints = [];
      const strobePoints = [];

      let currentTime = 0;
      let currentX = x0;
      let currentY = y0;
      let currentVy = vy0;
      let nextStrobeTime = 0;

      const arcDt = 0.01; // resolution for continuous path
      const maxBounces = 1000; // safety guard

      for (let bounce = 0; bounce < maxBounces && currentTime < totalTime - 1e-9; bounce++) {
        // Time of flight until next ground contact from current state
        const a = -0.5 * g;
        const b = currentVy;
        const c = currentY;
        const D = b*b - 4*a*c;
        if (D < 0) break; // Shouldn't happen, guard

        // Positive time root
        let t_flight = (-b - Math.sqrt(D)) / (2*a);
        if (!isFinite(t_flight) || t_flight <= 0) break;

        // Determine how much time remains for this arc
        const timeLeft = totalTime - currentTime;
        const t_arc = Math.min(t_flight, timeLeft);

        // Generate continuous path points for this arc
        let t = 0;
        while (t <= t_arc + 1e-9) {
          const x = currentX + vx * t;
          const y = currentY + currentVy * t - 0.5 * g * t * t;
          pathPoints.push({ x, y });
          t += arcDt;
        }

        // Generate stroboscopic points that fall within this arc
        const arcEndTime = currentTime + t_arc;
        while (nextStrobeTime <= arcEndTime + 1e-9) {
          const tau = nextStrobeTime - currentTime;
          if (tau >= -1e-9) {
            const sx = currentX + vx * tau;
            const sy = currentY + currentVy * tau - 0.5 * g * tau * tau;
            strobePoints.push({ x: sx, y: Math.max(0, sy) });
          }
          nextStrobeTime += dtStrobe;
        }

        // If arc ended before ground (ran out of total time), we're done
        if (t_arc < t_flight - 1e-9) {
          currentTime = arcEndTime;
          break;
        }

        // Otherwise, update state for next bounce
        currentTime += t_flight;
        currentX += vx * t_flight;
        const vy_impact = currentVy - g * t_flight; // downward (negative)
        const vy_new = Math.sqrt(k) * Math.abs(vy_impact); // upward
        currentY = 0;
        currentVy = vy_new;
      }

      // Prepare canvas
      background(0, 0, 0);

      // Calculate dynamic scaling based on ranges
      const plotWidth = width - 2 * PADDING;
      const plotHeight = height - 2 * PADDING;

      const rangeX = Math.max(vx * totalTime, 1); // meters
      const rangeY = Math.max(y0, 1); // meters (highest point is initial height)
      const scaleX = plotWidth / rangeX;
      const scaleY = plotHeight / rangeY;

      // Transform to Cartesian with origin bottom-left
      push();
      translate(PADDING, height - PADDING);
      scale(1, -1);

      drawAxes(plotWidth, plotHeight, scaleX, scaleY, rangeX, rangeY);

      // Draw trajectory path (red line)
      stroke(255, 0, 0);
      strokeWeight(2);
      noFill();
      beginShape();
      for (let i = 0; i < pathPoints.length; i++) {
        const px = pathPoints[i].x * scaleX;
        const py = pathPoints[i].y * scaleY;
        vertex(px, py);
      }
      endShape();

      // Draw strobe points (yellow dots)
      noStroke();
      fill(255, 255, 0);
      for (let i = 0; i < strobePoints.length; i++) {
        const px = strobePoints[i].x * scaleX;
        const py = strobePoints[i].y * scaleY;
        ellipse(px, py, 8, 8);
      }

      pop();
    }

    function drawAxes(plotWidth, plotHeight, scaleX, scaleY, rangeX, rangeY) {
      // Axes
      stroke(100, 100, 150);
      strokeWeight(1.5);
      // x-axis
      line(0, 0, plotWidth, 0);
      // y-axis
      line(0, 0, 0, plotHeight);

      // Tick marks
      const tickLen = 6; // px
      const xStep = niceStep(rangeX);
      const yStep = niceStep(rangeY);

      // x-axis ticks
      for (let x = 0; x <= rangeX + 1e-9; x += xStep) {
        const px = x * scaleX;
        line(px, -tickLen, px, tickLen);
      }

      // y-axis ticks
      for (let y = 0; y <= rangeY + 1e-9; y += yStep) {
        const py = y * scaleY;
        line(-tickLen, py, tickLen, py);
      }
    }

    function niceStep(range) {
      const candidates = [0.5, 1, 2, 5, 10, 20, 50, 100, 200, 500];
      for (let i = 0; i < candidates.length; i++) {
        const step = candidates[i];
        if (range / step <= 10) return step;
      }
      return candidates[candidates.length - 1];
    }
  </script>
</body>
</html>
