<body>
  <header>
    <h1>Angle Bisectors on the Circumcircle</h1>
    <p>
      Extend the angle bisectors of the triangle ABC to meet the circumcircle at A', B' and C'. Then AA' ⟂ B'C', BB' ⟂ A'C', and CC' ⟂ A'B'.
    </p>
  </header>

  <div id="canvas-container">
    <!-- p5.js will create the canvas and assign id="p5-canvas" here -->
  </div>

  <!-- Include p5.js via CDN -->
  <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.9.2/p5.min.js" integrity="sha512-swoyBgl9z7gENbqY3SctR4P6R2nKzmu7DPdnRUpqhyGFqgzQ7a7CrHBrqrt/Ief7iIfkPAh0gJuAqg00wI1srA==" crossorigin="anonymous" referrerpolicy="no-referrer"></script>

  <script>
    (function() {
      const cfg = {
        W: 500,
        H: 500,
        bg: '#F0F0F0',
        circle: {
          r: 200,
          stroke: '#6A5ACD',
          strokeWeight: 4
        },
        triABC: {
          stroke: '#6A5ACD',
          strokeWeight: 4
        },
        triAprime: {
          stroke: '#FFA500',
          strokeWeight: 4
        },
        point: {
          d: 12,
          r: 6,
          fill: '#FFA500',
          stroke: '#000000',
          strokeWeight: 1,
          pickRadius: 15
        },
        label: {
          size: 16,
          fill: '#000000',
          offset: 16
        }
      };

      let cx, cy; // center of circumcircle
      let angleA, angleB, angleC; // in radians
      let dragging = null; // 'A' | 'B' | 'C' | null

      let canvasRef = null;

      function deg2rad(d) { return d * Math.PI / 180; }
      function normAngle(a) {
        // Normalize angle to (-PI, PI]
        while (a <= -Math.PI) a += 2 * Math.PI;
        while (a > Math.PI) a -= 2 * Math.PI;
        return a;
      }

      function angleFromCenter(px, py) {
        return Math.atan2(py - cy, px - cx);
      }

      function posFromAngle(theta) {
        return { x: cx + cfg.circle.r * Math.cos(theta), y: cy + cfg.circle.r * Math.sin(theta) };
      }

      // Vector-averaged midpoint angle of two angles (robust to wrapping).
      function midpointAngle(a1, a2) {
        const s = Math.sin(a1) + Math.sin(a2);
        const c = Math.cos(a1) + Math.cos(a2);
        if (Math.abs(s) < 1e-9 && Math.abs(c) < 1e-9) {
          // Opposite directions; fall back to average with wrap-safe adjustment
          // Bring a2 within pi of a1
          let a2b = a2;
          let d = normAngle(a2b - a1);
          a2b = a1 + d;
          return normAngle((a1 + a2b) / 2);
        }
        return Math.atan2(s, c);
      }

      // Drawing helpers
      function drawPoint(p, withCrosshair) {
        stroke(cfg.point.stroke);
        strokeWeight(cfg.point.strokeWeight);
        fill(cfg.point.fill);
        circle(p.x, p.y, cfg.point.d);

        if (withCrosshair) {
          // crosshair size slightly smaller than radius
          const ch = 4;
          stroke(0);
          strokeWeight(1.2);
          line(p.x - ch, p.y, p.x + ch, p.y);
          line(p.x, p.y - ch, p.x, p.y + ch);
        }
      }

      function drawLabel(label, p) {
        noStroke();
        fill(cfg.label.fill);
        textSize(cfg.label.size);
        textAlign(CENTER, CENTER);

        // Radial outward offset from center
        const vx = p.x - cx;
        const vy = p.y - cy;
        const len = Math.hypot(vx, vy) || 1;
        const ox = (vx / len) * cfg.label.offset;
        const oy = (vy / len) * cfg.label.offset;

        text(label, p.x + ox, p.y + oy);
      }

      function drawSegment(p1, p2, strokeCol, sw) {
        stroke(strokeCol);
        strokeWeight(sw);
        line(p1.x, p1.y, p2.x, p2.y);
      }

      function setupSketch(p) {
        p.setup = function() {
          canvasRef = p.createCanvas(cfg.W, cfg.H);
          canvasRef.id('p5-canvas');
          canvasRef.parent('canvas-container');

          p.pixelDensity(1); // for consistent stroke sizes
          cx = cfg.W / 2;
          cy = cfg.H / 2;

          // Initial angles
          angleA = deg2rad(210); // 7π/6
          angleB = deg2rad(330); // 11π/6
          angleC = deg2rad(90);  // π/2

          p.noLoop();
        };

        p.draw = function() {
          p.clear();
          p.background(cfg.bg);
          p.strokeCap(p.ROUND);

          // Compute key points on circumcircle
          const A = posFromAngle(angleA);
          const B = posFromAngle(angleB);
          const C = posFromAngle(angleC);

          // Compute arc midpoints via vector averaging
          const angleAprime = midpointAngle(angleB, angleC);
          const angleBprime = midpointAngle(angleA, angleC);
          const angleCprime = midpointAngle(angleA, angleB);

          const Aprime = posFromAngle(angleAprime);
          const Bprime = posFromAngle(angleBprime);
          const Cprime = posFromAngle(angleCprime);

          // Draw circumcircle
          p.noFill();
          p.stroke(cfg.circle.stroke);
          p.strokeWeight(cfg.circle.strokeWeight);
          p.circle(cx, cy, cfg.circle.r * 2);

          // Draw triangle ABC
          drawSegment(A, B, cfg.triABC.stroke, cfg.triABC.strokeWeight);
          drawSegment(B, C, cfg.triABC.stroke, cfg.triABC.strokeWeight);
          drawSegment(C, A, cfg.triABC.stroke, cfg.triABC.strokeWeight);

          // Draw extended bisectors AA', BB', CC'
          drawSegment(A, Aprime, cfg.triAprime.stroke, cfg.triAprime.strokeWeight);
          drawSegment(B, Bprime, cfg.triAprime.stroke, cfg.triAprime.strokeWeight);
          drawSegment(C, Cprime, cfg.triAprime.stroke, cfg.triAprime.strokeWeight);

          // Draw triangle A'B'C'
          drawSegment(Aprime, Bprime, cfg.triAprime.stroke, cfg.triAprime.strokeWeight);
          drawSegment(Bprime, Cprime, cfg.triAprime.stroke, cfg.triAprime.strokeWeight);
          drawSegment(Cprime, Aprime, cfg.triAprime.stroke, cfg.triAprime.strokeWeight);

          // Draw points
          drawPoint(A, true);
          drawPoint(B, true);
          drawPoint(C, true);

          drawPoint(Aprime, false);
          drawPoint(Bprime, false);
          drawPoint(Cprime, false);

          // Labels
          drawLabel("A", A);
          drawLabel("B", B);
          drawLabel("C", C);
          drawLabel("A'", Aprime);
          drawLabel("B'", Bprime);
          drawLabel("C'", Cprime);
        };

        // Interaction
        function pickPoint(mx, my) {
          const A = posFromAngle(angleA);
          const B = posFromAngle(angleB);
          const C = posFromAngle(angleC);

          function near(pt) {
            return Math.hypot(mx - pt.x, my - pt.y) <= cfg.point.pickRadius;
          }

          if (near(A)) return 'A';
          if (near(B)) return 'B';
          if (near(C)) return 'C';
          return null;
        }

        p.mousePressed = function() {
          const mx = p.mouseX, my = p.mouseY;
          dragging = pickPoint(mx, my);
        };

        p.mouseDragged = function() {
          if (!dragging) return;
          const mx = p.mouseX, my = p.mouseY;
          const theta = angleFromCenter(mx, my);

          if (dragging === 'A') angleA = normAngle(theta);
          if (dragging === 'B') angleB = normAngle(theta);
          if (dragging === 'C') angleC = normAngle(theta);

          p.redraw();
        };

        p.mouseReleased = function() {
          dragging = null;
        };

        // Touch support (basic)
        p.touchStarted = function() {
          if (p.touches && p.touches.length > 0) {
            const t = p.touches[0];
            dragging = pickPoint(t.x, t.y);
          }
        };
        p.touchMoved = function() {
          if (!dragging) return false;
          if (p.touches && p.touches.length > 0) {
            const t = p.touches[0];
            const theta = angleFromCenter(t.x, t.y);
            if (dragging === 'A') angleA = normAngle(theta);
            if (dragging === 'B') angleB = normAngle(theta);
            if (dragging === 'C') angleC = normAngle(theta);
            p.redraw();
          }
          return false; // prevent scrolling
        };
        p.touchEnded = function() {
          dragging = null;
        };

        // Also redraw on window resize of devicePixelRatio changes or font load
        // but keep canvas fixed size as required.
      }

      new p5(setupSketch);
    })();
  </script>
</body>
