  <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.7.0/p5.min.js"></script>
  <script>
    // Global state
    let cnv;
    let hoveredDescription = null;
    let sliderEl;

    // Descriptions for tooltip
    const DESCRIPTIONS = {
      photosynthesis: "Plants use sunlight, water, and carbon dioxide to create food, releasing oxygen.",
      cellular: "Animals and plants break down sugars for energy, releasing carbon dioxide.",
      waste: "Decomposers break down dead organic matter, returning carbon to the soil and atmosphere.",
      fossils: "Over millions of years, buried organic matter can turn into fossil fuels like coal and oil.",
      extraction: "Humans extract fossil fuels from the ground for energy.",
      emissions: "Burning fossil fuels releases large amounts of carbon dioxide into the atmosphere.",
      diffusion: "Carbon dioxide dissolves into and is released from bodies of water like oceans."
    };

    function setup() {
      cnv = createCanvas(600, 500);
      cnv.parent('canvas-container');
      sliderEl = document.getElementById('slider-steps');
      textFont('Arial, Helvetica, sans-serif');
    }

    function draw() {
      hoveredDescription = null; // reset hover state each frame
      drawStaticScene();

      const step = parseInt(sliderEl ? sliderEl.value : 0, 10) || 0;

      // Dynamic processes based on slider
      if (step >= 1) drawCellularRespiration();
      if (step >= 2) drawWasteAndDecay();
      if (step >= 3) drawFossilsAndFuels();
      if (step >= 4) drawPhotosynthesis();
      if (step >= 5) drawFossilFuelEmissions();
      if (step >= 6) drawFossilFuelExtraction();
      if (step >= 7) drawCO2Diffusion();

      // Draw tooltip last so it's on top
      if (hoveredDescription) {
        drawTooltip(hoveredDescription);
      }
    }

    // --------- Static Scene -------------
    function drawStaticScene() {
      // Sky
      noStroke();
      background('#4169E1');

      // Sun (top right)
      fill('#FFD700');
      circle(540, 60, 70);

      // Cloud (upper-middle)
      fill(255);
      drawCloud(250, 80, 1.0);

      // Grass (middle part)
      fill('#228B22');
      rect(0, 260, width, 140);

      // Soil (bottom)
      fill('#8B4513');
      rect(0, 400, width, 100);

      // Water (bottom right, overlapping grass and soil)
