      // Trees
      drawTree(120, 220);  // left tree
      drawTree(290, 220);  // near where photosynthesis arrow lands (tree canopy around y ~ 180)

      // Sheep (near grass)
      drawSheep(200, 260);

      // Factory (right side)
      drawFactory(430, 300);

      // Fossil deposits in soil
      drawFossilDeposits();
    }

    function drawCloud(x, y, scaleAmt) {
      push();
      translate(x, y);
      scale(scaleAmt);
      noStroke();
      ellipse(0, 0, 80, 50);
      ellipse(30, -10, 70, 45);
      ellipse(-30, -10, 70, 45);
      ellipse(10, -20, 60, 40);
      pop();
