    function drawWater() {
      push();
      noStroke();
      fill('#0000CD');
      beginShape();
      vertex(500, 350);
      vertex(560, 350);
      vertex(600, 380);
      vertex(600, 470);
      vertex(520, 470);
      vertex(500, 450);
      endShape(CLOSE);
      pop();
    }

    function drawTree(baseX, baseY) {
      // trunk
      push();
      noStroke();
      fill('#8B4513');
      rect(baseX + 10, baseY, 20, 70);
      // foliage
      fill('#006400');
      ellipse(baseX + 20, baseY - 30, 90, 80);
      pop();
    }

    function drawSheep(cx, cy) {
      push();
      noStroke();
      // body
      fill(255);
      ellipse(cx, cy, 70, 45);
      ellipse(cx - 20, cy - 15, 35, 30);
      ellipse(cx + 20, cy - 15, 35, 30);
      // head
      fill(240);
      ellipse(cx + 35, cy - 10, 25, 20);
      // ear
      ellipse(cx + 43, cy - 18, 10, 6);
      // eye
      fill(0);
      circle(cx + 40, cy - 12, 3);
      // legs
      stroke(60);
      strokeWeight(3);
      line(cx - 20, cy + 20, cx - 20, cy + 35);
      line(cx, cy + 20, cx, cy + 35);
      line(cx + 20, cy + 20, cx + 20, cy + 35);
      pop();
    }

    function drawFactory(baseX, baseY) {
      // building
      push();
      noStroke();
      fill('#696969');
      rect(baseX, baseY - 80, 120, 80);

      // roof detail
      fill('#808080');
      rect(baseX + 10, baseY - 100, 40, 20);

      // smokestack
      fill('#A9A9A9');
      rect(baseX + 50, baseY - 120, 25, 120);

      // stack top cap
      fill('#C0C0C0');
      rect(baseX + 48, baseY - 125, 29, 8);
      pop();
    }

    function drawFossilDeposits() {
      push();
      noStroke();
      fill(0);
      for (let i = 0; i < 12; i++) {
        const x = 30 + i * 45 + (i % 3) * 5;
        const y = 420 + (i % 4) * 15;
        circle(x, y, 6 + (i % 3));
      }
      pop();
    }

    // --------- Utilities -------------
    function drawArrow(x1, y1, x2, y2) {
      push();
      stroke(0);
      strokeWeight(2);
      line(x1, y1, x2, y2);

      // Arrowhead
      const ang = Math.atan2(y2 - y1, x2 - x1);
      translate(x2, y2);
      rotate(ang);
      noStroke();
      fill(0);
      const size = 10;
      triangle(0, 0, -size, 4, -size, -4);
      pop();
    }

    function drawLabelAndCheckHover(x, y, labelText, description) {
      push();
      textSize(12);
      textLeading(14);
      textAlign(LEFT, TOP);
      fill(0);
      noStroke();

      const lines = labelText.split('\n');
      let maxW = 0;
      for (let i = 0; i < lines.length; i++) {
        maxW = Math.max(maxW, textWidth(lines[i]));
      }
      const lineH = 14;
      const boxW = maxW;
      const boxH = lines.length * lineH;

      // Draw label text
      for (let i = 0; i < lines.length; i++) {
        text(lines[i], x, y + i * lineH);
      }

      // Hover detection
      if (mouseX >= x && mouseX <= x + boxW && mouseY >= y && mouseY <= y + boxH) {
        hoveredDescription = description;
      }
      pop();
    }

    function wrapTextToWidth(txt, maxWidth) {
      push();
      textSize(12);
      textLeading(14);
      textAlign(LEFT, TOP);
      const words = txt.split(' ');
      const lines = [];
      let current = '';
      for (let w = 0; w < words.length; w++) {
        const testLine = current ? current + ' ' + words[w] : words[w];
        if (textWidth(testLine) <= maxWidth) {
          current = testLine;
        } else {
          if (current) lines.push(current);
          current = words[w];
        }
      }
      if (current) lines.push(current);
      pop();
      return lines;
    }

    function drawTooltip(textStr) {
      const padding = 8;
      const maxWidth = 260;
      const lineH = 14;

      const lines = wrapTextToWidth(textStr, maxWidth);
      // Compute actual used width
      let usedW = 0;
      push();
      textSize(12);
      textLeading(14);
      textAlign(LEFT, TOP);
      for (let i = 0; i < lines.length; i++) {
        usedW = Math.max(usedW, textWidth(lines[i]));
      }
      pop();

      const boxW = usedW + padding * 2;
      const boxH = lines.length * lineH + padding * 2;

      let tx = mouseX + 15;
      let ty = mouseY + 15;
      if (tx + boxW > width - 10) tx = width - boxW - 10;
      if (ty + boxH > height - 10) ty = height - boxH - 10;

      push();
      noStroke();
      fill(0, 0, 0, 150);
      rect(tx, ty, boxW, boxH, 6);

      fill(255);
      textSize(12);
      textLeading(14);
      textAlign(LEFT, TOP);
      for (let i = 0; i < lines.length; i++) {
        text(lines[i], tx + padding, ty + padding + i * lineH);
      }
      pop();
    }

    // --------- Processes -------------
    function drawPhotosynthesis() {
      // Arrow from sky (350, 80) to tree (300, 180). Label at (360, 120).
      drawArrow(350, 80, 300, 180);
      drawLabelAndCheckHover(
        360, 120,
        "Photosynthesis",
        DESCRIPTIONS.photosynthesis
      );
    }

    function drawCellularRespiration() {
      // Arrow from sheep (200, 240) to sky (250, 180). Label at (150, 180).
      drawArrow(200, 240, 250, 180);
      drawLabelAndCheckHover(
        150, 180,
        "Cellular Respiration",
        DESCRIPTIONS.cellular
      );
    }

    function drawWasteAndDecay() {
      // Arrow from ground below sheep (220, 280) down to (220, 350). Label at (240, 320).
      drawArrow(220, 280, 220, 350);
      drawLabelAndCheckHover(
        240, 320,
        "Organisms' Waste\nand Decay",
        DESCRIPTIONS.waste
      );
    }

    function drawFossilsAndFuels() {
      // Arrow from waste area (220, 370) down to soil (220, 440). Label at (240, 440).
      drawArrow(220, 370, 220, 440);
      drawLabelAndCheckHover(
        240, 440,
        "Fossils and\nFossil Fuels",
        DESCRIPTIONS.fossils
      );
    }

    function drawFossilFuelEmissions() {
      // Arrow from factory (480, 200) to sky (430, 120). Label at (440, 150).
      drawArrow(480, 200, 430, 120);
      drawLabelAndCheckHover(
        440, 150,
        "Fossil Fuel\nEmissions",
        DESCRIPTIONS.emissions
      );
    }

    function drawFossilFuelExtraction() {
      // Arrow from soil (450, 420) to factory base (480, 280). Label at (400, 350).
      drawArrow(450, 420, 480, 280);
      drawLabelAndCheckHover(
        400, 350,
        "Fossil Fuel\nExtraction",
        DESCRIPTIONS.extraction
      );
    }

    function drawCO2Diffusion() {
      // Two arrows between water and sky.
      // One from (520, 250) to (550, 400).
      // Another from (570, 400) to (540, 250).
      drawArrow(520, 250, 550, 400);
      drawArrow(570, 400, 540, 250);
      drawLabelAndCheckHover(
        480, 420,
        "CO2 Diffusion",
        DESCRIPTIONS.diffusion
      );
    }
  </script>
