    // p5.js sketch
    const sketch = (p) => {
      let W, H;
      // World bounds (square) to maintain aspect ratio
      const xmin = -2.2, xmax = 2.2, ymin = -2.2, ymax = 2.2;
      let scale = 1, offX = 0, offY = 0;

      function fitWorldToScreen() {
        const container = document.getElementById('canvas-container');
        W = container.clientWidth;
        H = container.clientHeight;
        p.resizeCanvas(W, H, true);

        // Compute uniform scale and centered offsets
        const sx = W / (xmax - xmin);
        const sy = H / (ymax - ymin);
        scale = Math.min(sx, sy) * 0.9; // 10% margin
        const worldPixW = (xmax - xmin) * scale;
        const worldPixH = (ymax - ymin) * scale;
        offX = (W - worldPixW) / 2 - xmin * scale;
        offY = (H - worldPixH) / 2 + ymax * scale;
      }

      function Sx(x) { return offX + x * scale; }
      function Sy(y) { return offY - y * scale; }

      function drawAxes() {
        p.push();
        p.stroke(0);
        p.strokeWeight(1);
        p.fill(0);

        // Axes lines
        // x-axis
        p.line(Sx(xmin), Sy(0), Sx(xmax), Sy(0));
        // y-axis
        p.line(Sx(0), Sy(ymin), Sx(0), Sy(ymax));

        // Ticks and labels -2..2
        p.textSize(12);
        p.textAlign(p.CENTER, p.TOP);
        p.fill('#333');
        for (let x = -2; x <= 2; x++) {
          const X = Sx(x);
          const Y0 = Sy(0) - 5, Y1 = Sy(0) + 5;
          p.stroke('#999');
          p.line(X, Y0, X, Y1);
          if (x !== 0) p.text(x.toString(), X, Sy(0) + 8);
        }
        p.textAlign(p.RIGHT, p.CENTER);
        for (let y = -2; y <= 2; y++) {
          const Y = Sy(y);
          const X0 = Sx(0) - 5, X1 = Sx(0) + 5;
          p.stroke('#999');
          p.line(X0, Y, X1, Y);
          if (y !== 0) p.text(y.toString(), Sx(0) - 8, Y - 6);
        }

        // Axis labels
        p.textSize(14);
        p.textAlign(p.RIGHT, p.TOP);
        p.text("x(t)", Sx(xmax) - 6, Sy(0) + 8);
        p.textAlign(p.LEFT, p.TOP);
        p.push();
        p.translate(Sx(0) + 8, Sy(ymax) + 6);
        p.text("y(t)", 0, 0);
        p.pop();

        p.pop();
      }

      function drawCurve() {
        p.push();
        p.noFill();
        p.stroke('#1d4ed8'); // blue
        p.strokeWeight(2);
        p.beginShape();
        const steps = 1000;
        for (let i = 0; i <= steps; i++) {
          const tt = (i / steps) * Math.PI * 2;
          const x = Math.cos(tt);
          const y = Math.sin(2 * tt);
          p.vertex(Sx(x), Sy(y));
        }
        p.endShape();
        p.pop();
      }

      function drawPoint(x, y) {
        p.push();
        p.fill(0);
        p.noStroke();
        p.circle(Sx(x), Sy(y), 6);
        p.pop();
      }

      function drawArrow(x0, y0, x1, y1, col = '#000', weight = 2, dashed = false) {
        p.push();
        p.stroke(col);
        p.strokeWeight(weight);
        p.noFill();
        if (dashed) p.drawingContext.setLineDash([6, 6]);
        p.line(Sx(x0), Sy(y0), Sx(x1), Sy(y1));
        if (dashed) p.drawingContext.setLineDash([]);

        // Arrowhead
        const dx = x1 - x0, dy = y1 - y0;
        const len = Math.hypot(dx, dy) || 1e-6;
        const ux = dx / len, uy = dy / len;
        const headLen = Math.min(0.15, 0.15 * len); // world units
        const leftx = x1 - ux * headLen + (-uy) * headLen * 0.6;
        const lefty = y1 - uy * headLen + (ux) * headLen * 0.6;
        const rightx = x1 - ux * headLen - (-uy) * headLen * 0.6;
        const righty = y1 - uy * headLen - (ux) * headLen * 0.6;

        p.fill(col);
        p.noStroke();
        p.triangle(Sx(x1), Sy(y1), Sx(leftx), Sy(lefty), Sx(rightx), Sy(righty));
        p.pop();
      }

      function drawDashedCircle(cx, cy, r, col = '#9aa0a6') {
        if (!isFinite(r) || r <= 0) return;
        p.push();
        p.noFill();
        p.stroke(col);
        p.strokeWeight(1.5);
        p.drawingContext.setLineDash([5, 6]);
        const d = r * 2 * scale; // pixel diameter (uniform scale)
        p.circle(Sx(cx), Sy(cy), d);
        p.drawingContext.setLineDash([]);
        p.pop();
      }

      function labelAt(x, y, textStr, col = '#000') {
        p.push();
        p.fill(col);
        p.noStroke();
        p.textSize(14);
        p.textAlign(p.LEFT, p.CENTER);
        p.text(textStr, Sx(x) + 4, Sy(y));
        p.pop();
      }

      function rotate90CCW(v) { return { x: -v.y, y: v.x }; }

      p.setup = () => {
        const container = document.getElementById('canvas-container');
        const rect = container.getBoundingClientRect();
        const cnv = p.createCanvas(rect.width, rect.height);
        cnv.parent('canvas-container');
        window.p5Instance = p;
        p.noLoop();
        fitWorldToScreen();
        p.redraw();
      };

      p.windowResized = () => {
        fitWorldToScreen();
        p.redraw();
      };

      p.draw = () => {
        p.background('#ffffff');
        drawAxes();
        drawCurve();

        // Kinematics at current time t
        const t = state.t;
        const r = { x: Math.cos(t), y: Math.sin(2 * t) };
        const v = { x: -Math.sin(t), y: 2 * Math.cos(2 * t) };
        const a = { x: -Math.cos(t), y: -4 * Math.sin(2 * t) };

        const vMag = Math.hypot(v.x, v.y);
        const aMag = Math.hypot(a.x, a.y);

        // Draw current point
        drawPoint(r.x, r.y);

        // Position vector (from origin to point)
        if (state.showPosition) {
          drawArrow(0, 0, r.x, r.y, '#1b7f3b', 2.5, false);
        }

        // Basis vectors at the point depending on coordinate system
        const basisLen = 0.7; // world units length for basis display
        p.push();
        const dashed = true;
        let labelColor = '#222';
        p.stroke('#000');
        p.strokeWeight(2);

        if (state.coords === 'rectangular') {
          // i-hat and j-hat
          drawArrow(r.x, r.y, r.x + basisLen, r.y, '#222', 2, dashed);
          drawArrow(r.x, r.y, r.x, r.y + basisLen, '#222', 2, dashed);
          labelAt(r.x + basisLen, r.y, 'î', labelColor);
          labelAt(r.x, r.y + basisLen, 'ĵ', labelColor);
        } else if (state.coords === 'polar') {
          // e_r and e_theta
          const rMag = Math.hypot(r.x, r.y) || 1e-6;
          const er = { x: r.x / rMag, y: r.y / rMag };
          const et = rotate90CCW(er); // CCW rotate
          drawArrow(r.x, r.y, r.x + er.x * basisLen, r.y + er.y * basisLen, '#222', 2, dashed);
          drawArrow(r.x, r.y, r.x + et.x * basisLen, r.y + et.y * basisLen, '#222', 2, dashed);
          labelAt(r.x + er.x * basisLen, r.y + er.y * basisLen, 'ê_r', labelColor);
          labelAt(r.x + et.x * basisLen, r.y + et.y * basisLen, 'ê_θ', labelColor);
        } else if (state.coords === 'normal-tangential') {
          // e_T and e_N
          const eT = { x: v.x / vMag, y: v.y / vMag };
          const nLeft = rotate90CCW(eT);
          const cross = v.x * a.y - v.y * a.x; // scalar z of v x a
          const sgn = Math.sign(cross) || 1;
          const eN = { x: nLeft.x * sgn, y: nLeft.y * sgn };

          drawArrow(r.x, r.y, r.x + eT.x * basisLen, r.y + eT.y * basisLen, '#222', 2, dashed);
          drawArrow(r.x, r.y, r.x + eN.x * basisLen, r.y + eN.y * basisLen, '#222', 2, dashed);
          labelAt(r.x + eT.x * basisLen, r.y + eT.y * basisLen, 'ê_T', labelColor);
          labelAt(r.x + eN.x * basisLen, r.y + eN.y * basisLen, 'ê_N', labelColor);
        }
        p.pop();

        // Velocity vector from P(t)
        if (state.showVelocity) {
          const kV = 0.4; // scale factor for display
          const vEnd = { x: r.x + v.x * kV, y: r.y + v.y * kV };
          drawArrow(r.x, r.y, vEnd.x, vEnd.y, '#8b0000', 2.5, false); // dark red
        }

        // Acceleration vector from P(t)
        if (state.showAcceleration) {
          const kA = 0.15; // scale factor for display
          const aEnd = { x: r.x + a.x * kA, y: r.y + a.y * kA };
          drawArrow(r.x, r.y, aEnd.x, aEnd.y, '#a000a0', 2.5, false); // magenta/purple
        }

        // Instantaneous (osculating) circle
        if (state.showCircle) {
          const cross = v.x * a.y - v.y * a.x;
          const rho = Math.pow(vMag, 3) / Math.abs(cross || 1e-9);
          // eN pointing toward center of curvature
          const eT = { x: v.x / vMag, y: v.y / vMag };
          const nLeft = rotate90CCW(eT);
          const sgn = Math.sign(cross) || 1;
          const eN = { x: nLeft.x * sgn, y: nLeft.y * sgn };
          const C = { x: r.x + eN.x * rho, y: r.y + eN.y * rho };

          drawDashedCircle(C.x, C.y, rho, '#9aa0a6');
          // Optional: draw center point
          p.push();
          p.noStroke();
          p.fill('#666');
          p.circle(Sx(C.x), Sy(C.y), 4);
          p.pop();
        }
      };
    };

    new p5(sketch);
  </script>
</body>
</html>
