    const gridContainer = document.getElementById('grid-container');
    const btnManhattan = document.getElementById('btn-manhattan');
    const btnEuclidean = document.getElementById('btn-euclidean');
    const btnChebyshev = document.getElementById('btn-chebyshev');

    // Build the grid structure (63 cells with nested span)
    const cellElements = []; // 2D array of elements for fast updates
    (function buildGrid() {
      for (let r = 0; r < ROWS; r++) {
        const rowElems = [];
        for (let c = 0; c < COLS; c++) {
          const cell = document.createElement('div');
          cell.className = 'cell cell-off';
          cell.dataset.row = String(r);
          cell.dataset.col = String(c);

          const valueSpan = document.createElement('span');
          valueSpan.className = 'cell-value';
          valueSpan.textContent = ''; // filled on render

          cell.appendChild(valueSpan);
          gridContainer.appendChild(cell);

          // Click handler to toggle state and redraw
          cell.addEventListener('click', () => {
            const row = parseInt(cell.dataset.row, 10);
            const col = parseInt(cell.dataset.col, 10);
            gridState[row][col] = !gridState[row][col];
            computeAndRender();
          });

          rowElems.push(cell);
        }
        cellElements.push(rowElems);
      }
    })();

    // Button interactions
    btnManhattan.addEventListener('click', () => setDistanceFunction('manhattan'));
    btnEuclidean.addEventListener('click', () => setDistanceFunction('euclidean'));
    btnChebyshev.addEventListener('click', () => setDistanceFunction('chebyshev'));

    function setDistanceFunction(metric) {
      if (currentDistanceFunction === metric) return;
      currentDistanceFunction = metric;
      updateActiveButtons();
      computeAndRender();
    }

    function updateActiveButtons() {
      btnManhattan.classList.toggle('active', currentDistanceFunction === 'manhattan');
      btnEuclidean.classList.toggle('active', currentDistanceFunction === 'euclidean');
      btnChebyshev.classList.toggle('active', currentDistanceFunction === 'chebyshev');
    }

    // Compute minimal distance to nearest "on" cell for each grid cell
    function computeDistances() {
      const sources = [];
      for (let r = 0; r < ROWS; r++) {
        for (let c = 0; c < COLS; c++) {
          if (gridState[r][c]) sources.push([r, c]);
        }
      }

      const distances = Array.from({ length: ROWS }, () => Array(COLS).fill(Infinity));

      if (sources.length === 0) {
        // No sources: keep Infinity distances
        return distances;
      }

      for (let r = 0; r < ROWS; r++) {
        for (let c = 0; c < COLS; c++) {
          let minD = Infinity;
          for (const [sr, sc] of sources) {
            const dr = r - sr;
            const dc = c - sc;
            let d;
            switch (currentDistanceFunction) {
              case 'manhattan':
                d = Math.abs(dr) + Math.abs(dc);
                break;
              case 'euclidean': // squared Euclidean
                d = dr * dr + dc * dc;
                break;
              case 'chebyshev':
                d = Math.max(Math.abs(dr), Math.abs(dc));
                break;
              default:
                d = Math.abs(dr) + Math.abs(dc);
            }
            if (d < minD) minD = d;
            if (minD === 0) break; // cannot get smaller than 0
          }
          distances[r][c] = minD;
        }
      }

      return distances;
    }

    // Render grid based on gridState and distances
    function render(distances) {
      for (let r = 0; r < ROWS; r++) {
        for (let c = 0; c < COLS; c++) {
          const cell = cellElements[r][c];
          const valueSpan = cell.firstElementChild;

          // Set classes for on/off
          if (gridState[r][c]) {
            cell.classList.add('cell-on');
            cell.classList.remove('cell-off');
          } else {
            cell.classList.add('cell-off');
            cell.classList.remove('cell-on');
          }

          // Display value (∞ if no sources)
          const d = distances[r][c];
          if (!isFinite(d)) {
            valueSpan.textContent = '∞';
          } else {
            valueSpan.textContent = String(d);
          }
        }
      }
    }

    function computeAndRender() {
      const dists = computeDistances();
      render(dists);
    }

    // Initial paint
    updateActiveButtons();
    computeAndRender();
  </script>
</body>
</html>
