<script>
(function(){
    // Utility formatting
    const fmtInt = (x) => new Intl.NumberFormat('en-US', {maximumFractionDigits:0}).format(Math.round(x));
    const fmtR2  = (x) => (isFinite(x) ? x : 0).toFixed(3);

    // Elements
    const btnModelEP   = document.getElementById('btn-model-ep');
    const btnModelAR   = document.getElementById('btn-model-ar');
    const btnTmaxLow   = document.getElementById('btn-tmax-low');
    const btnTmaxHigh  = document.getElementById('btn-tmax-high');

    const sN     = document.getElementById('slider-n');
    const sTmin  = document.getElementById('slider-tmin');
    const sTmax  = document.getElementById('slider-tmax');
    const sTref  = document.getElementById('slider-tref');
    const sParam = document.getElementById('slider-param');

    const lN     = document.getElementById('label-n');
    const lTmin  = document.getElementById('label-tmin');
    const lTmax  = document.getElementById('label-tmax');
    const lTref  = document.getElementById('label-tref');
    const lPname = document.getElementById('label-param-name');
    const lPval  = document.getElementById('label-param-value');

    const fitResults = document.getElementById('fit-results');

    // Button group behavior
    function setActive(btn, siblings) {
        siblings.forEach(b => b.classList.remove('active'));
        btn.classList.add('active');
    }

    // Model utilities
    function isEPActive() { return btnModelEP.classList.contains('active'); }
    function isARActive() { return btnModelAR.classList.contains('active'); }
    function isTmaxLowActive() { return btnTmaxLow.classList.contains('active'); }

    function clamp(val, min, max){ return Math.max(min, Math.min(max, val)); }

    // Compute y for a given model
    function yEP(Tk, TrefK, a){ // Eyring–Polanyi normalized
        return (Tk / TrefK) * Math.exp(a * (1/TrefK - 1/Tk));
    }
    function yAR(Tk, TrefK, a){ // Arrhenius normalized
        return Math.exp(a * (1/TrefK - 1/Tk));
    }

    // Generate evenly spaced array [start, end] inclusive
    function linspace(start, end, n){
        if (n <= 1) return [start];
        const arr = new Array(n);
        const step = (end - start) / (n - 1);
        for (let i=0;i<n;i++) arr[i] = start + i*step;
        return arr;
    }

    // Main update function
    function update(){
        // Handle dynamic max for T_max
        if (isTmaxLowActive()) {
            sTmax.max = "1200";
        } else {
            sTmax.max = "6000";
        }

        // Enforce constraints: Tmin < Tmax; Tref within [Tmin, Tmax]
        let Tmin = parseFloat(sTmin.value);
        let Tmax = parseFloat(sTmax.value);
        let Tref = parseFloat(sTref.value);

        // Adjust to be within slider bounds
        Tmin = clamp(Tmin, parseFloat(sTmin.min), parseFloat(sTmin.max));
        Tmax = clamp(Tmax, parseFloat(sTmax.min), parseFloat(sTmax.max));

        if (Tmin >= Tmax) {
            // Keep at least 1 degree between them
            if (Tmin >= parseFloat(sTmax.max)) {
                Tmin = parseFloat(sTmax.max) - 1;
            } else {
                Tmax = Tmin + 1;
            }
        }

        // Clamp Tref between Tmin and Tmax
        Tref = clamp(Tref, Tmin, Tmax);

        // Write back possibly adjusted values
        sTmin.value = Math.round(Tmin);
        sTmax.value = Math.round(Tmax);
        sTref.value = Math.round(Tref);

        // Dynamic label for parameter name based on model
        if (isEPActive()) {
            lPname.textContent = "\\(a_{EP}\\)";
        } else {
            lPname.textContent = "\\(a_{Ar}\\)";
        }
        if (window.MathJax && MathJax.typeset) MathJax.typeset([lPname]);

        // Update span labels
        lN.textContent = sN.value;
        lTmin.textContent = fmtInt(sTmin.value) + ".";
        lTmax.textContent = fmtInt(sTmax.value) + ".";
        lTref.textContent = fmtInt(sTref.value) + ".";
        lPval.textContent = fmtInt(sParam.value) + ".";

        // Data generation
        const n = parseInt(sN.value, 10);
        const aParam = parseFloat(sParam.value);
        const tempsC = linspace(Tmin, Tmax, n);
        const TrefK = Tref + 273.15;
        const tempsK = tempsC.map(t => t + 273.15);

        let yData = [];
        if (isEPActive()) {
            yData = tempsK.map(Tk => yEP(Tk, TrefK, aParam));
        } else {
            yData = tempsK.map(Tk => yAR(Tk, TrefK, aParam));
        }

        // Fitting using the other model via grid search
        const fitModel = isEPActive() ? 'AR' : 'EP';
        // Grid search parameters
        const aMin = 0, aMax = 10000, aStep = 1;
        let bestA = 0;
        let bestRSS = Infinity;

        for (let a=aMin; a<=aMax; a+=aStep) {
            let rss = 0;
            for (let i=0;i<tempsK.length;i++) {
                const Tk = tempsK[i];
                const yhat = (fitModel === 'AR')
                    ? yAR(Tk, TrefK, a)
                    : yEP(Tk, TrefK, a);
                const diff = yData[i] - yhat;
                rss += diff*diff;
            }
            if (rss < bestRSS) {
                bestRSS = rss;
                bestA = a;
            }
        }

        // r^2 calculation
        const meanY = yData.reduce((acc, v) => acc + v, 0) / yData.length;
        let ssTot = 0;
        for (let i=0;i<yData.length;i++){
            const d = yData[i] - meanY;
            ssTot += d*d;
        }
        const r2 = (ssTot === 0) ? 1 : (1 - bestRSS / ssTot);

        // Display fit results
        const paramLabel = (fitModel === 'AR') ? "a_{Ar}" : "a_{EP}";
        const fitText = `\\(${paramLabel} = ${fmtInt(bestA)}.\\) \\(r^2 = ${fmtR2(r2)}\\)`;
        fitResults.innerHTML = fitText;
        if (window.MathJax && MathJax.typeset) MathJax.typeset([fitResults]);

        // Draw plot
        drawPlot({
            tempsC, yData, Tref, TrefK,
            fitModel, aFit: bestA,
            Tmin, Tmax
        });
    }

    function drawPlot(state){
        const { tempsC, yData, TrefK, fitModel, aFit, Tmin, Tmax } = state;

        // Trace 1: data points
        const traceData = {
            x: tempsC,
            y: yData,
            type: 'scatter',
            mode: 'markers',
            name: 'generated data',
            marker: {
                size: 9,
                color: '#22c55e', // green fill
                line: { color: '#1f77b4', width: 1.6 } // blue border
            },
            hovertemplate: 'T = %{x:.0f} °C<br>y = %{y:.4f}<extra></extra>'
        };

        // Trace 2: fit curve across the range
        const xFit = linspace(Tmin, Tmax, 200);
        const xFitK = xFit.map(x => x + 273.15);
        const yFit = xFitK.map(Tk => {
            return (fitModel === 'AR')
                ? yAR(Tk, TrefK, aFit)
                : yEP(Tk, TrefK, aFit);
        });

        const traceFit = {
            x: xFit,
            y: yFit,
            type: 'scatter',
            mode: 'lines',
            name: (fitModel === 'AR') ? 'Arrhenius fit' : 'Eyring–Polanyi fit',
            line: { color: '#22c55e', width: 3 },
            hovertemplate: 'T = %{x:.0f} °C<br>fit y = %{y:.4f}<extra></extra>'
        };

        const title = (isEPActive())
            ? 'Arrhenius model fit to Eyring–Polanyi data'
            : 'Eyring–Polanyi model fit to Arrhenius data';

        const layout = {
            paper_bgcolor: 'rgba(0,0,0,0)',
            plot_bgcolor: 'rgba(0,0,0,0)',
            title: { text: title, font: { color: '#e5e7eb', size: 18 } },
            margin: { l: 60, r: 20, t: 40, b: 50 },
            xaxis: {
                title: { text: 'T °C', font: { color: '#e5e7eb' } },
                gridcolor: '#1f2432',
                zerolinecolor: '#1f2432',
                tickfont: { color: '#e5e7eb' }
            },
            yaxis: {
                title: { text: 'k(T) / k(T_{ref})', font: { color: '#e5e7eb' } },
                gridcolor: '#1f2432',
                zerolinecolor: '#1f2432',
                tickfont: { color: '#e5e7eb' }
            },
            legend: { orientation: 'h', x: 0, y: 1.05, font: { color: '#e5e7eb' } }
        };

        Plotly.newPlot('plot-container', [traceData, traceFit], layout, {displayModeBar: false, responsive: true});
    }

    // Event listeners
    btnModelEP.addEventListener('click', () => { setActive(btnModelEP, [btnModelAR]); update(); });
    btnModelAR.addEventListener('click', () => { setActive(btnModelAR, [btnModelEP]); update(); });

    btnTmaxLow.addEventListener('click', () => { setActive(btnTmaxLow, [btnTmaxHigh]); update(); });
    btnTmaxHigh.addEventListener('click', () => { setActive(btnTmaxHigh, [btnTmaxLow]); update(); });

    [sN, sTmin, sTmax, sTref, sParam].forEach(s => {
        s.addEventListener('input', update);
        s.addEventListener('change', update);
    });

    // Initial render
    window.addEventListener('load', () => {
        // Ensure MathJax initial labels render
        if (window.MathJax && MathJax.typeset) MathJax.typeset();
        update();
    });
})();
</script>
</body>
</html>
