          updateDisplay(n);
          clearActive();
          highlightButton(n);
          drawScene();
        });
      });
    }

    function clearActive() {
      const ids = [3,4,5,6,7,8,9,10];
      ids.forEach(val => {
        document.getElementById(`btn-n-${val}`).classList.remove('active');
      });
    }

    function highlightButton(val) {
      const btn = document.getElementById(`btn-n-${val}`);
      if (btn) btn.classList.add('active');
    }

    function updateDisplay(val) {
      const disp = document.getElementById('n-display');
      disp.textContent = String(val);
    }

    function drawScene() {
      background(COL_BG);
      stroke(COL_STROKE);
      strokeWeight(1);

      // Layout parameters
      const canvasWidth = width;
      const canvasHeight = height;

      // Left side: regular 2n-gon parameters
      // Use a radius that adapts a bit to canvas height and leaves space for right side
      const R = Math.min(150, Math.min(canvasHeight * 0.35, canvasWidth * 0.20));
      const cx = canvasWidth / 4;      // left half center x
      const cy = canvasHeight / 2;     // center y

      // Right side: rearranged rectangles parameters
      const startX = Math.floor(canvasWidth / 2) + 10;  // starting x for rectangles
      const startY = Math.floor(canvasHeight / 2);      // baseline center y

      // Draw left 2n-gon dissected into 2n isosceles triangles
      drawPolygonDissection(cx, cy, R, n);

      // Compute triangle dimensions for rectangles
      const angleStep = Math.PI / n;
      const s = 2 * R * Math.sin(angleStep / 2);  // base of each isosceles triangle
      const a = R * Math.cos(angleStep / 2);      // apothem (height from base to center)
      const rectW = s;
      const rectH = 2 * a;

      // Arrange full rectangles
      const countFullRects = Math.floor(n / 2);
      const gap = Math.max(12, Math.min(24, Math.round(canvasWidth * 0.02)));
      let x = startX;
      let y = startY - rectH / 2;

      for (let k = 1; k <= countFullRects; k++) {
        drawFullRectangle(x, y, rectW, rectH);
        x += rectW + gap;
      }

      // If n is odd, draw the half-rectangle
      if (n % 2 === 1) {
        drawHalfRectangle(x, y, rectW, rectH);
      }
    }

    function drawPolygonDissection(cx, cy, R, nVal) {
      const totalSides = 2 * nVal;
      const angleStep = Math.PI / nVal;

      // Precompute vertices on circumcircle
      const pts = [];
      for (let k = 0; k <= totalSides; k++) {
        const ang = k * angleStep - Math.PI / 2;  // rotate upright
        const px = cx + R * Math.cos(ang);
        const py = cy + R * Math.sin(ang);
        pts.push({x: px, y: py});
      }

      // Draw triangles from center using color sequence Yellow, Red, Blue
      for (let j = 1; j <= totalSides; j++) {
        const cidx = (j - 1) % 3;
        fill(triColors[cidx]);
        stroke(COL_STROKE);
        triangle(cx, cy, pts[j - 1].x, pts[j - 1].y, pts[j].x, pts[j].y);
      }

      // Outline the polygon
      noFill();
      beginShape();
      for (let k = 0; k < totalSides; k++) {
        vertex(pts[k].x, pts[k].y);
      }
      endShape(CLOSE);
    }

    // Draw a "full rectangle" composed of four wedges (triangles) that meet at the center:
    // Top: Yellow, Bottom: Red, Left: Blue, Right: Yellow
    // We overlay these wedge triangles; the rectangle outline and diagonals are drawn for clarity.
    function drawFullRectangle(x, y, w, h) {
      const cx = x + w / 2;
      const cy = y + h / 2;
      const tl = {x: x,     y: y};
      const tr = {x: x + w, y: y};
      const br = {x: x + w, y: y + h};
      const bl = {x: x,     y: y + h};

      // Left wedge (Blue): center -> top-left -> bottom-left
      fill(COL_BLUE); stroke(COL_STROKE);
      triangle(cx, cy, tl.x, tl.y, bl.x, bl.y);

      // Right wedge (Yellow): center -> bottom-right -> top-right
      fill(COL_YELLOW); stroke(COL_STROKE);
      triangle(cx, cy, br.x, br.y, tr.x, tr.y);

      // Top wedge (Yellow): center -> top-right -> top-left
      fill(COL_YELLOW); stroke(COL_STROKE);
      triangle(cx, cy, tr.x, tr.y, tl.x, tl.y);

      // Bottom wedge (Red): center -> bottom-left -> bottom-right
      fill(COL_RED); stroke(COL_STROKE);
      triangle(cx, cy, bl.x, bl.y, br.x, br.y);

      // Rectangle outline
      noFill(); stroke(COL_STROKE);
      rect(x, y, w, h);

      // Internal divisions (diagonals from corner to corner through the center)
      line(tl.x, tl.y, br.x, br.y);
      line(tr.x, tr.y, bl.x, bl.y);
    }

    // Draw a "half-rectangle" (for odd n) composed of two wedges meeting at center:
    // Top: Blue, Bottom: Red
    function drawHalfRectangle(x, y, w, h) {
      const cx = x + w / 2;
      const cy = y + h / 2;
      const tl = {x: x,     y: y};
      const tr = {x: x + w, y: y};
      const br = {x: x + w, y: y + h};
      const bl = {x: x,     y: y + h};

      // Top wedge (Blue)
      fill(COL_BLUE); stroke(COL_STROKE);
      triangle(cx, cy, tr.x, tr.y, tl.x, tl.y);

      // Bottom wedge (Red)
      fill(COL_RED); stroke(COL_STROKE);
      triangle(cx, cy, bl.x, bl.y, br.x, br.y);

      // Outline rectangle
      noFill(); stroke(COL_STROKE);
      rect(x, y, w, h);

      // Outline triangle edges to emphasize the two pieces
      // Center to top corners
      line(cx, cy, tl.x, tl.y);
      line(cx, cy, tr.x, tr.y);
      // Center to bottom corners
      line(cx, cy, bl.x, bl.y);
      line(cx, cy, br.x, br.y);
    }
  </script>
</body>
