<script>
(function() {
  // Global/shared state
  const state = {
    // Canvas sizes
    workspaceSize: 400,
    phaseSize: 400,
    // Robot parameters
    base: {x: 200, y: 200},
    link1: 75,
    link2: 50,
    linkRadius: 10,
    // Obstacles
    obstacles: [
      { x: 200, y: 100, radius: 15 },
      { x: 200, y: 300, radius: 15 }
    ],
    // Configurations (angles in radians, -PI to PI)
    startConfig: { q1: -1.5, q2: 0.5 },
    goalConfig:  { q1:  1.5, q2: 1.0 },
    // Drag flags
    isDraggingObstacle1: false,
    isDraggingObstacle2: false,
    isDraggingStart: false,
    isDraggingGoal: false,
    // Temporary configs during dragging
    tempStartConfig: null,
    tempGoalConfig: null,
    // Controls
    Q: 2,
    P: 1.0,
    // Phase space grid
    N: 0,
    cellSize: 0,
    occupancy: null, // Uint8Array (1=obstacle, 0=free)
    dist: null,      // Int32Array distances from goal (BFS)
    maxDist: 0,
    // Path
    pathIndices: [],     // indices (linear) from start to goal
    pathAngles: [],      // [{q1,q2},...]
    pathPixels: [],      // [{x,y},...]
    pathLength: 0,       // sum of Euclidean distances in angle space
    // Render helpers
    phaseGfx: null,      // p5.Graphics buffer for background field
    phaseFieldDirty: true
  };

  const TWO_PI = Math.PI * 2;

  // Utility: clamp
  const clamp = (v, lo, hi) => Math.max(lo, Math.min(hi, v));
  // Utility: normalize angle to [-PI, PI)
  function normalizeAngle(a) {
    a = (a + Math.PI) % (2*Math.PI);
    if (a < 0) a += 2*Math.PI;
    return a - Math.PI;
  }
  // Map angle in [-PI,PI) to index [0..N-1]
  function angleToIndex(a, N) {
    let norm = (a + Math.PI) / (2*Math.PI);
    // ensure inside [0,1)
    norm = ((norm % 1) + 1) % 1;
    let idx = Math.floor(norm * N);
    // in case norm==1.0 by floating imprecision
    if (idx === N) idx = 0;
    return idx;
  }
  // Map index to angle at cell center
  function indexToAngle(i, N) {
    return -Math.PI + (i + 0.5) * (2*Math.PI / N);
  }
  // Convert linear index to (ix,iy)
  function linToXY(k, N) {
    const iy = (k / N) | 0;
    const ix = k - iy * N;
    return {ix, iy};
  }
  function xyToLin(ix, iy, N) {
    return iy * N + ix;
  }

  // Forward kinematics for two-link arm
  function forwardKinematics(q1, q2) {
    const b = state.base;
    const L1 = state.link1;
    const L2 = state.link2;

    const jx = b.x + L1 * Math.cos(q1);
    const jy = b.y + L1 * Math.sin(q1);

    const efx = jx + L2 * Math.cos(q1 + q2);
    const efy = jy + L2 * Math.sin(q1 + q2);

    return {
      base: {x: b.x, y: b.y},
      joint: {x: jx, y: jy},
      end: {x: efx, y: efy}
    };
  }

  // Distance from point C to segment AB
  function pointToSegmentDistance(A, B, C) {
    const ABx = B.x - A.x, ABy = B.y - A.y;
    const ACx = C.x - A.x, ACy = C.y - A.y;
    const ab2 = ABx*ABx + ABy*ABy;
    let t = 0;
    if (ab2 > 0) {
      t = (ACx*ABx + ACy*ABy) / ab2;
      t = Math.max(0, Math.max(Math.min(1, t), 0));
    }
    const Dx = A.x + t*ABx;
    const Dy = A.y + t*ABy;
    const DxC = C.x - Dx, DyC = C.y - Dy;
    return Math.hypot(DxC, DyC);
  }

  // Collision check for a configuration
  function isCollisionConfig(q1, q2) {
    const fk = forwardKinematics(q1, q2);
    const rad = state.linkRadius;
    for (const obs of state.obstacles) {
      const C = {x: obs.x, y: obs.y};
      // link 1
      const d1 = pointToSegmentDistance(fk.base, fk.joint, C);
      if (d1 < rad + obs.radius) return true;
      // link 2
      const d2 = pointToSegmentDistance(fk.joint, fk.end, C);
      if (d2 < rad + obs.radius) return true;
    }
    return false;
  }

  // Compute grid resolution from Q
  function computeNfromQ(Q) {
    // Grid size determined by Q (example base 10 * 2^Q), but clamped for responsiveness
    const N = 10 * (1 << Q);
    return Math.min(N, 512); // clamp max
  }

  // Full recomputation: occupancy, BFS from goal, shortest path from start
  function fullRecalculate() {
    state.N = computeNfromQ(state.Q);
    state.cellSize = state.phaseSize / state.N;
    const N = state.N;
    const total = N * N;
    state.occupancy = new Uint8Array(total);
    state.dist = new Int32Array(total);
    state.dist.fill(-1);

    // Build occupancy
    for (let iy = 0; iy < N; iy++) {
      const q2 = indexToAngle(iy, N);
      for (let ix = 0; ix < N; ix++) {
        const q1 = indexToAngle(ix, N);
        const k = iy * N + ix;
        state.occupancy[k] = isCollisionConfig(q1, q2) ? 1 : 0;
      }
    }

    // BFS from goal
    const goal_ix = angleToIndex(state.goalConfig.q1, N);
    const goal_iy = angleToIndex(state.goalConfig.q2, N);
    const goal_k = xyToLin(goal_ix, goal_iy, N);

    state.maxDist = 0;

    if (state.occupancy[goal_k] === 0) {
      const Qqueue = new Int32Array(N * N);
      let head = 0, tail = 0;
      state.dist[goal_k] = 0;
      Qqueue[tail++] = goal_k;

      while (head < tail) {
        const cur = Qqueue[head++];
        const {ix, iy} = linToXY(cur, N);
        const dcur = state.dist[cur];
        // 4-neighbors with toroidal wrapping
        const nx = (ix + 1) % N, px = (ix - 1 + N) % N, ny = (iy + 1) % N, py = (iy - 1 + N) % N;
        const nbrs = [
          xyToLin(nx, iy, N),
          xyToLin(px, iy, N),
          xyToLin(ix, ny, N),
          xyToLin(ix, py, N)
        ];
        for (let i = 0; i < 4; i++) {
          const nk = nbrs[i];
          if (state.occupancy[nk] === 0 && state.dist[nk] === -1) {
            state.dist[nk] = dcur + 1;
            if (state.dist[nk] > state.maxDist) state.maxDist = state.dist[nk];
            Qqueue[tail++] = nk;
          }
        }
      }
    }

    // Build distance field image
    state.phaseFieldDirty = true;

    // Compute shortest path from start by backtracking over dist
    computePathFromStart();

    // Update title
    updatePhaseTitle();
  }

  // Compute path (backtracking) using precomputed dist field
  function computePathFromStart() {
    const N = state.N;
    const total = N * N;
    state.pathIndices = [];
    state.pathAngles = [];
    state.pathPixels = [];
    state.pathLength = 0;

    if (N === 0) return;

    const start_ix = angleToIndex(state.startConfig.q1, N);
    const start_iy = angleToIndex(state.startConfig.q2, N);
    const start_k = xyToLin(start_ix, start_iy, N);

    const goal_ix = angleToIndex(state.goalConfig.q1, N);
    const goal_iy = angleToIndex(state.goalConfig.q2, N);
    const goal_k = xyToLin(goal_ix, goal_iy, N);

    if (state.occupancy[start_k] === 1) {
      // Start in obstacle: no path
      return;
    }
    if (state.dist[ start_k ] < 0 || state.dist[ goal_k ] !== 0) {
      // No route to goal (unreachable)
      return;
    }

    // Backtrack from start to goal using decreasing dist
    let cur_k = start_k;
    let safety = total + 5;
    state.pathIndices.push(cur_k);

    while (cur_k !== goal_k && safety-- > 0) {
      const {ix, iy} = linToXY(cur_k, N);
      const dcur = state.dist[cur_k];
      // choose neighbor with smallest dist (must be dcur-1)
      let best = cur_k;
      let bestd = dcur;

      const nx = (ix + 1) % N, px = (ix - 1 + N) % N, ny = (iy + 1) % N, py = (iy - 1 + N) % N;
      const nbrs = [
        xyToLin(nx, iy, N),
        xyToLin(px, iy, N),
        xyToLin(ix, ny, N),
        xyToLin(ix, py, N)
      ];
      for (let i = 0; i < 4; i++) {
        const nk = nbrs[i];
        const nd = state.dist[nk];
        if (nd >= 0 && nd < bestd) {
          bestd = nd;
          best = nk;
          if (bestd === dcur - 1) break; // early exit
        }
      }
      if (best === cur_k) {
        // stuck unexpectedly
        break;
      }
      cur_k = best;
      state.pathIndices.push(cur_k);
    }

    // Build angles and pixel positions
    const cs = state.cellSize;
    for (const k of state.pathIndices) {
      const {ix, iy} = linToXY(k, N);
      const q1 = indexToAngle(ix, N);
      const q2 = indexToAngle(iy, N);
      state.pathAngles.push({q1, q2});
      state.pathPixels.push({x: (ix + 0.5) * cs, y: (iy + 0.5) * cs});
    }

    // Path length in radians (Euclidean in angle coords)
    let L = 0;
    for (let i = 0; i + 1 < state.pathAngles.length; i++) {
      // handle circular wrap in angles
      const a = state.pathAngles[i], b = state.pathAngles[i+1];
      let d1 = normalizeAngle(b.q1 - a.q1);
      let d2 = normalizeAngle(b.q2 - a.q2);
      L += Math.hypot(d1, d2);
    }
    state.pathLength = L;
  }

  function updatePhaseTitle() {
    const title = document.getElementById('phase-space-title');
    if (state.pathIndices.length <= 1) {
      title.textContent = `robot phase space, path length = ∞ rads`;
    } else {
      title.textContent = `robot phase space, path length = ${state.pathLength.toFixed(2)} rads`;
    }
  }

  // Build the static background field (C-space obstacles + grayscale distances)
  function rebuildPhaseFieldGfx(p) {
    if (!state.phaseGfx) {
      state.phaseGfx = p.createGraphics(state.phaseSize, state.phaseSize);
    }
    const g = state.phaseGfx;
    g.push();
    g.noStroke();
    g.background(0); // will be overwritten per cell
    const N = state.N;
    const cs = state.cellSize;

    for (let iy = 0; iy < N; iy++) {
      for (let ix = 0; ix < N; ix++) {
        const k = iy * N + ix;
        const x = ix * cs, y = iy * cs;

        if (state.occupancy[k] === 1) {
          g.fill('#CC0000'); // obstacle red
        } else {
          const d = state.dist[k];
          if (d < 0) {
            g.fill(0); // unreachable free -> darkest
          } else {
            // goal (d=0) -> bright, far -> dark
            const shade = Math.round(255 * (1 - (state.maxDist > 0 ? (d / state.maxDist) : 0)));
            g.fill(shade);
          }
        }
        g.rect(x, y, cs + 1, cs + 1); // +1 to avoid gaps due to float
      }
    }
    g.pop();
    state.phaseFieldDirty = false;
  }

  // Interpolate along path using P in [0,1]
  function configAlongPath(P) {
    const n = state.pathAngles.length;
    if (n === 0) return { q1: state.startConfig.q1, q2: state.startConfig.q2 };
    if (n === 1) return { q1: state.pathAngles[0].q1, q2: state.pathAngles[0].q2 };
    const t = clamp(P, 0, 1);
    const fpos = t * (n - 1);
    const i = Math.floor(fpos);
    const u = fpos - i;
    if (i >= n - 1) return { q1: state.pathAngles[n-1].q1, q2: state.pathAngles[n-1].q2 };
    const a = state.pathAngles[i], b = state.pathAngles[i+1];
    const d1 = normalizeAngle(b.q1 - a.q1);
    const d2 = normalizeAngle(b.q2 - a.q2);
    return { q1: normalizeAngle(a.q1 + u * d1), q2: normalizeAngle(a.q2 + u * d2) };
  }

  // Interpolate a point along phase-space path for marker
  function pointAlongPath(P) {
    const n = state.pathPixels.length;
    if (n === 0) return null;
    if (n === 1) return { x: state.pathPixels[0].x, y: state.pathPixels[0].y };
    const t = clamp(P, 0, 1);
    const fpos = t * (n - 1);
    const i = Math.floor(fpos);
    const u = fpos - i;
    if (i >= n - 1) return { x: state.pathPixels[n-1].x, y: state.pathPixels[n-1].y };
    const A = state.pathPixels[i], B = state.pathPixels[i+1];
    return { x: A.x + u * (B.x - A.x), y: A.y + u * (B.y - A.y) };
  }

  // UI: Sliders and Reset
  const sliderQ = document.getElementById('slider-q');
  const sliderP = document.getElementById('slider-p');
  const valueQ  = document.getElementById('value-q');
  const valueP  = document.getElementById('value-p');
  const btnReset = document.getElementById('btn-reset');

  function applyQ(val) {
    state.Q = parseInt(val, 10);
    valueQ.textContent = state.Q.toString();
    fullRecalculate();
  }
  function applyP(val) {
    state.P = parseFloat(val);
    valueP.textContent = state.P.toFixed(2);
    // Only visualization update; recomputation not needed
  }
  sliderQ.addEventListener('input', (e) => {
    applyQ(e.target.value);
  });
  sliderP.addEventListener('input', (e) => {
    applyP(e.target.value);
  });
  btnReset.addEventListener('click', () => {
    // Reset everything to defaults
    sliderQ.value = 2;
    sliderP.value = 1;
    applyQ(2);
    applyP(1);
    state.obstacles[0].x = 200; state.obstacles[0].y = 100;
    state.obstacles[1].x = 200; state.obstacles[1].y = 300;
    state.startConfig = { q1: -1.5, q2: 0.5 };
    state.goalConfig  = { q1:  1.5, q2: 1.0 };
    state.isDraggingObstacle1 = false;
    state.isDraggingObstacle2 = false;
    state.isDraggingStart = false;
    state.isDraggingGoal = false;
    state.tempStartConfig = null;
    state.tempGoalConfig = null;
    fullRecalculate();
  });

  // Initialize controls display
  valueQ.textContent = sliderQ.value;
  valueP.textContent = parseFloat(sliderP.value).toFixed(2);

  // p5 Sketch: Workspace (robot animation)
  const workspaceSketch = (p) => {
    p.setup = function() {
      const cnv = p.createCanvas(state.workspaceSize, state.workspaceSize);
      cnv.parent('canvas-workspace');
    };

    function drawObstacle(obs) {
      p.push();
      p.noStroke();
      p.fill(220, 140, 60);
      p.circle(obs.x, obs.y, obs.radius * 2);
      // pattern: concentric rings
      p.noFill();
      p.stroke(140, 80, 30, 160);
      for (let r = obs.radius * 0.4; r < obs.radius; r += 4) {
        p.circle(obs.x, obs.y, r * 2);
      }
      p.pop();
    }

    function drawRobot(config) {
      const q1 = config.q1, q2 = config.q2;
      const fk = forwardKinematics(q1, q2);

      // Angles indicators
      p.push();
      p.noFill();
      p.stroke(50, 120, 200);
      p.strokeWeight(2);
      p.arc(state.base.x, state.base.y, 50, 50, 0, q1);
      p.stroke(200, 50, 120);
      p.arc(fk.joint.x, fk.joint.y, 40, 40, q1, q1 + q2);
      p.pop();

      // Links as capsules with text
      p.push();
      p.stroke(0);
      p.strokeWeight(1);
      p.fill(180);
      // Link 1
      p.translate(state.base.x, state.base.y);
      p.rotate(q1);
      const h = state.linkRadius * 2;
      // draw as rounded rectangle
      p.rectMode(p.CORNER);
      p.rect(-state.linkRadius, -state.linkRadius, state.link1 + 2*state.linkRadius, h, state.linkRadius);
      // Link 2
      p.translate(state.link1, 0);
      p.rotate(q2);
      p.fill(170);
      p.rect(-state.linkRadius, -state.linkRadius, state.link2 + 2*state.linkRadius, h, state.linkRadius);

      // "Robot Arm" text on link 2
      p.noStroke();
      p.fill(30);
      p.textAlign(p.CENTER, p.CENTER);
      p.textSize(12);
      p.push();
      p.translate((state.link2)/2, 0);
      p.rotate(- (q1 + q2)); // keep text roughly upright
      p.text('Robot Arm', 0, 0);
      p.pop();
      p.pop();

      // Joints/endpoints
      p.push();
      p.noStroke();
      p.fill(30);
      p.circle(state.base.x, state.base.y, 6);
      p.circle(fk.joint.x, fk.joint.y, 6);
      p.fill(80);
      p.circle(fk.end.x, fk.end.y, 6);
      p.pop();
    }

    function mouseOverObstacle(ix, iy, obs) {
      const dx = ix - obs.x, dy = iy - obs.y;
      return Math.hypot(dx, dy) <= obs.radius + 6; // small grab margin
    }

    p.mousePressed = function() {
      const mx = p.mouseX, my = p.mouseY;
      // Detect obstacle drag
      if (mouseOverObstacle(mx, my, state.obstacles[0])) {
        state.isDraggingObstacle1 = true;
      } else if (mouseOverObstacle(mx, my, state.obstacles[1])) {
        state.isDraggingObstacle2 = true;
      }
    };

    p.mouseDragged = function() {
      const mx = clamp(p.mouseX, 0, state.workspaceSize);
      const my = clamp(p.mouseY, 0, state.workspaceSize);
      let changed = false;
      if (state.isDraggingObstacle1) {
        state.obstacles[0].x = mx;
        state.obstacles[0].y = my;
        changed = true;
      }
      if (state.isDraggingObstacle2) {
        state.obstacles[1].x = mx;
        state.obstacles[1].y = my;
        changed = true;
      }
      if (changed) {
        fullRecalculate();
      }
    };

    p.mouseReleased = function() {
      state.isDraggingObstacle1 = false;
      state.isDraggingObstacle2 = false;
    };

    p.draw = function() {
      p.background('#F0F0F0');

      // Draw obstacles (behind)
      drawObstacle(state.obstacles[0]);
      drawObstacle(state.obstacles[1]);

      // Determine which configuration to show
      let configToShow = null;
      if (state.isDraggingStart && state.tempStartConfig) {
        configToShow = state.tempStartConfig;
      } else if (state.isDraggingGoal && state.tempGoalConfig) {
        configToShow = state.tempGoalConfig;
      } else {
        configToShow = configAlongPath(state.P);
      }

      drawRobot(configToShow);
    };
  };

  // p5 Sketch: Phase Space
  const phaseSketch = (p) => {
    p.setup = function() {
      const cnv = p.createCanvas(state.phaseSize, state.phaseSize);
      cnv.parent('canvas-phase-space');
      state.phaseGfx = p.createGraphics(state.phaseSize, state.phaseSize);
    };

    function markerColor(valid) {
      return valid ? p.color(0, 200, 0) : p.color(220, 0, 0);
    }

    function drawStartGoalMarkers() {
      const N = state.N;
      const cs = state.cellSize;
      // Start
      let start_ix = angleToIndex(state.startConfig.q1, N);
      let start_iy = angleToIndex(state.startConfig.q2, N);
      let startValid = true;
      if (state.isDraggingStart && state.tempStartConfig) {
        start_ix = angleToIndex(state.tempStartConfig.q1, N);
        start_iy = angleToIndex(state.tempStartConfig.q2, N);
        const k = xyToLin(start_ix, start_iy, N);
        startValid = (state.occupancy && state.occupancy[k] === 0);
      }
      const sx = (start_ix + 0.5) * cs, sy = (start_iy + 0.5) * cs;

      // Goal
      let goal_ix = angleToIndex(state.goalConfig.q1, N);
      let goal_iy = angleToIndex(state.goalConfig.q2, N);
      let goalValid = true;
      if (state.isDraggingGoal && state.tempGoalConfig) {
        goal_ix = angleToIndex(state.tempGoalConfig.q1, N);
        goal_iy = angleToIndex(state.tempGoalConfig.q2, N);
        const k = xyToLin(goal_ix, goal_iy, N);
        goalValid = (state.occupancy && state.occupancy[k] === 0);
      }
      const gx = (goal_ix + 0.5) * cs, gy = (goal_iy + 0.5) * cs;

      // Draw markers
      p.push();
      p.stroke(255);
      p.strokeWeight(2);
      // Start marker
      p.fill(markerColor(startValid));
      p.circle(sx, sy, Math.max(8, cs * 0.5));
      p.line(sx - 6, sy, sx + 6, sy);
      p.line(sx, sy - 6, sx, sy + 6);
      // Goal marker
      p.fill(markerColor(goalValid));
      p.circle(gx, gy, Math.max(8, cs * 0.5));
      p.line(gx - 6, gy, gx + 6, gy);
      p.line(gx, gy - 6, gx, gy + 6);
      p.pop();
    }

    function drawPath() {
      if (state.pathPixels.length <= 1) return;
      p.push();
      p.stroke('#00FF00');
      p.strokeWeight(3);
      p.noFill();
      p.beginShape();
      for (const pt of state.pathPixels) {
        p.vertex(pt.x, pt.y);
      }
      p.endShape();
      // Path traversal marker
      const m = pointAlongPath(state.P);
      if (m) {
        p.noStroke();
        p.fill(255, 230, 0);
        p.circle(m.x, m.y, 8);
        p.stroke(60, 60, 60, 150);
        p.noFill();
        p.circle(m.x, m.y, 14);
      }
      p.pop();
    }

    function mouseToCell(mx, my) {
      const cs = state.cellSize;
      let ix = Math.floor(mx / cs);
      let iy = Math.floor(my / cs);
      ix = clamp(ix, 0, state.N - 1);
      iy = clamp(iy, 0, state.N - 1);
      return {ix, iy};
    }

    p.mousePressed = function() {
      const mx = p.mouseX, my = p.mouseY;
      const cs = state.cellSize;
      const N = state.N;

      // Check proximity to start and goal for grabbing (within ~0.6*cellSize)
      const s_ix = angleToIndex(state.startConfig.q1, N);
      const s_iy = angleToIndex(state.startConfig.q2, N);
      const s_cx = (s_ix + 0.5) * cs, s_cy = (s_iy + 0.5) * cs;

      const g_ix = angleToIndex(state.goalConfig.q1, N);
      const g_iy = angleToIndex(state.goalConfig.q2, N);
      const g_cx = (g_ix + 0.5) * cs, g_cy = (g_iy + 0.5) * cs;

      const ds = Math.hypot(mx - s_cx, my - s_cy);
      const dg = Math.hypot(mx - g_cx, my - g_cy);
      const radius = Math.max(8, cs * 0.5) + 4;

      if (ds <= radius) {
        state.isDraggingStart = true;
        state.tempStartConfig = { ...state.startConfig };
      } else if (dg <= radius) {
        state.isDraggingGoal = true;
        state.tempGoalConfig = { ...state.goalConfig };
      }
    };

    p.mouseDragged = function() {
      const mx = clamp(p.mouseX, 0, state.phaseSize-1);
      const my = clamp(p.mouseY, 0, state.phaseSize-1);
      const N = state.N;
      const cell = mouseToCell(mx, my);
      if (state.isDraggingStart) {
        // Snap to center of grid cell
        const q1 = indexToAngle(cell.ix, N);
        const q2 = indexToAngle(cell.iy, N);
        state.tempStartConfig = { q1, q2 };
        // Only path needs to be updated on release; for visual feedback we can redraw now
      } else if (state.isDraggingGoal) {
        const q1 = indexToAngle(cell.ix, N);
        const q2 = indexToAngle(cell.iy, N);
        state.tempGoalConfig = { q1, q2 };
        // Full recalculation is done on release (to avoid excessive recompute)
      }
    };

    p.mouseReleased = function() {
      const N = state.N;
      if (state.isDraggingStart && state.tempStartConfig) {
        const ix = angleToIndex(state.tempStartConfig.q1, N);
        const iy = angleToIndex(state.tempStartConfig.q2, N);
        const k = xyToLin(ix, iy, N);
        if (state.occupancy[k] === 0) {
          state.startConfig = { ...state.tempStartConfig };
          // Only recompute path (backtracking)
          computePathFromStart();
          updatePhaseTitle();
        }
      }
      if (state.isDraggingGoal && state.tempGoalConfig) {
        const ix = angleToIndex(state.tempGoalConfig.q1, N);
        const iy = angleToIndex(state.tempGoalConfig.q2, N);
        const k = xyToLin(ix, iy, N);
        if (state.occupancy[k] === 0) {
          state.goalConfig = { ...state.tempGoalConfig };
          // Full recompute (BFS + path)
          fullRecalculate();
        }
      }
      state.isDraggingStart = false;
      state.isDraggingGoal = false;
      state.tempStartConfig = null;
      state.tempGoalConfig = null;
    };

    p.draw = function() {
      // Rebuild graphics if dirty
      if (state.phaseFieldDirty) {
        rebuildPhaseFieldGfx(p);
      }
      // Draw field
      p.background(240);
      if (state.phaseGfx) {
        p.image(state.phaseGfx, 0, 0, state.phaseSize, state.phaseSize);
      }

      // Draw path and traversal marker
      drawPath();
      // Draw start/goal markers (dynamic, can be invalid while dragging)
      drawStartGoalMarkers();
    };
  };

  // Initialize both sketches and run initial computation
  let workspaceP5 = null;
  let phaseP5 = null;

  function init() {
    // Initial full calculation
    fullRecalculate();
    // Instantiate p5 sketches
    workspaceP5 = new p5(workspaceSketch);
    phaseP5 = new p5(phaseSketch);
  }

  // Start
  window.addEventListener('load', init);
})();
</script>
</body>
</html>
