      </div>

      <div class="group">
        <h4>method</h4>
        <div class="row">
          <button id="btn-prim" class="active">Prim</button>
          <button id="btn-kruskal">Kruskal</button>
        </div>
      </div>

      <div class="group">
        <h4>process</h4>
        <div class="row">
          <button id="btn-play">►</button>
          <button id="btn-pause" disabled>||</button>
          <button id="btn-reset">|◄</button>
        </div>
        <div class="hint">
          • Play adds one MST edge every 10 frames.<br>
          • Changing problem or method stops and resets the animation.
        </div>
      </div>

      <div class="footer-note">
        Minimum Spanning Tree visualization using p5.js (2D/WEBGL).
      </div>
    </div>

    <div id="canvas-container">
      <!-- p5.js will insert the canvas here dynamically -->
    </div>
  </div>

  <script src="https://cdn.jsdelivr.net/npm/p5@1.9.0/lib/p5.min.js"></script>
  <script>
    // Global application state
    const state = {
      dimension: '2D',        // '2D' or '3D'
      seed: 525,
      number: 40,
      algorithm: 'prim',      // 'prim' or 'kruskal'
      animationState: 'stopped', // 'stopped' | 'playing' | 'paused'
      points: [],
      edgesPrim: [],
      edgesKruskal: [],
      edgesSequence: [],
      currentStep: 0,
      worldSize: 500 // used for 3D cube size; set on generation for 3D
    };

    // UI elements
    const btn2D = document.getElementById('btn-2d');
    const btn3D = document.getElementById('btn-3d');
    const sliderSeed = document.getElementById('slider-seed');
    const seedValue = document.getElementById('seed-value');
    const sliderNumber = document.getElementById('slider-number');
    const numberValue = document.getElementById('number-value');

    const btnPrim = document.getElementById('btn-prim');
    const btnKruskal = document.getElementById('btn-kruskal');

    const btnPlay = document.getElementById('btn-play');
    const btnPause = document.getElementById('btn-pause');
    const btnReset = document.getElementById('btn-reset');

    // p5 instance and animation pacing
    let p5Instance = null;
    let framesPerStep = 10;
    let lastStepFrame = 0;

    // Utility: toggle 'active' class on exclusive buttons
    function setExclusiveActive(activeBtn, inactiveBtn) {
      activeBtn.classList.add('active');
      inactiveBtn.classList.remove('active');
    }

    function updateProcessButtons() {
      // Default per animation state
      if (state.animationState === 'playing') {
        btnPlay.disabled = true;
        btnPause.disabled = false;
        btnReset.disabled = false;
      } else if (state.animationState === 'paused') {
        btnPlay.disabled = false;
        btnPause.disabled = true;
        btnReset.disabled = false;
      } else { // 'stopped'
        btnPlay.disabled = false;
        btnPause.disabled = true;
        btnReset.disabled = false;
      }
      // If complete (all edges drawn), disable play and pause
      if (state.edgesSequence && state.currentStep >= state.edgesSequence.length && state.edgesSequence.length > 0) {
        btnPlay.disabled = true;
        btnPause.disabled = true;
        btnReset.disabled = false;
        state.animationState = 'stopped';
      }
    }

    function switchAlgorithm(algo) {
      if (algo === state.algorithm) return;
      state.algorithm = algo;
      if (algo === 'prim') setExclusiveActive(btnPrim, btnKruskal);
      else setExclusiveActive(btnKruskal, btnPrim);

      // Partial reset: keep points, rebuild sequence, reset steps
      state.animationState = 'stopped';
      state.currentStep = 0;
      state.edgesSequence = (state.algorithm === 'prim') ? state.edgesPrim.slice() : state.edgesKruskal.slice();
      updateProcessButtons();
    }

    function fullProblemReset(recreateCanvas = false) {
      state.animationState = 'stopped';
      state.currentStep = 0;

      if (recreateCanvas) {
        // Will regenerate points after canvas is ready within p5 setup
        createSketch();
        // Do not generate points here; canvas dimensions are set in setup
      } else {
        // regenerate within current canvas
        generatePoints();
        computeAllSequences();
        state.edgesSequence = (state.algorithm === 'prim') ? state.edgesPrim.slice() : state.edgesKruskal.slice();
        updateProcessButtons();
      }
    }

    function generatePoints() {
      const pad = 20;
      state.points = [];
      if (!p5Instance) return;
      if (state.dimension === '2D') {
        p5Instance.randomSeed(state.seed);
        for (let i = 0; i < state.number; i++) {
          const x = p5Instance.random(pad, Math.max(pad + 1, p5Instance.width - pad));
          const y = p5Instance.random(pad, Math.max(pad + 1, p5Instance.height - pad));
          state.points.push(new p5.Vector(x, y, 0));
        }
      } else {
        // 3D: points in centered cube; worldSize defines cube edge length
        state.worldSize = Math.min(p5Instance.width, p5Instance.height) * 0.8;
        const half = state.worldSize / 2 - 12;
        p5Instance.randomSeed(state.seed);
        for (let i = 0; i < state.number; i++) {
          const x = p5Instance.random(-half, half);
          const y = p5Instance.random(-half, half);
          const z = p5Instance.random(-half, half);
          state.points.push(new p5.Vector(x, y, z));
        }
      }
    }

    function computeAllSequences() {
      state.edgesPrim = computePrimSequence(state.points);
      state.edgesKruskal = computeKruskalSequence(state.points);
    }

    function computeDist(v1, v2) {
      // p5.Vector.dist is available; both v1, v2 are p5.Vector
      return p5.Vector.dist(v1, v2);
    }

    function computePrimSequence(points) {
      const n = points.length;
      const inMST = Array(n).fill(false);
      const steps = [];
      if (n === 0) return steps;
      inMST[0] = true;

      while (steps.length < n - 1) {
        let bestU = -1, bestV = -1, bestW = Infinity;
        for (let u = 0; u < n; u++) if (inMST[u]) {
          for (let v = 0; v < n; v++) if (!inMST[v]) {
            const w = computeDist(points[u], points[v]);
            if (w < bestW) {
              bestW = w; bestU = u; bestV = v;
            }
          }
        }
        if (bestU === -1 || bestV === -1) break; // disconnected, should not happen
        inMST[bestV] = true;
        steps.push({ i: bestU, j: bestV, w: bestW });
      }
      return steps;
    }

    class DSU {
      constructor(n) {
        this.p = Array(n).fill(0).map((_, i) => i);
        this.r = Array(n).fill(0);
      }
      find(x) {
        if (this.p[x] !== x) this.p[x] = this.find(this.p[x]);
        return this.p[x];
      }
      union(a, b) {
        a = this.find(a);
        b = this.find(b);
        if (a === b) return false;
        if (this.r[a] < this.r[b]) [a, b] = [b, a];
        this.p[b] = a;
        if (this.r[a] === this.r[b]) this.r[a]++;
        return true;
      }
    }

    function computeKruskalSequence(points) {
      const n = points.length;
      const edges = [];
      for (let i = 0; i < n; i++) {
        for (let j = i + 1; j < n; j++) {
          edges.push({ i, j, w: computeDist(points[i], points[j]) });
        }
      }
      edges.sort((a, b) => a.w - b.w);
      const dsu = new DSU(n);
      const steps = [];
      for (const e of edges) {
        if (dsu.union(e.i, e.j)) {
          steps.push(e);
          if (steps.length === n - 1) break;
        }
      }
      return steps;
    }

    function createSketch() {
      // Remove previous instance if any
      if (p5Instance) {
        p5Instance.remove();
        p5Instance = null;
      }

      const container = document.getElementById('canvas-container');

      p5Instance = new p5((p) => {
        p.setup = function () {
          const rect = container.getBoundingClientRect();
          const w = Math.max(10, rect.width);
          const h = Math.max(10, rect.height);

          if (state.dimension === '2D') {
            p.createCanvas(w, h).parent(container);
          } else {
            p.createCanvas(w, h, p.WEBGL).parent(container);
          }

          // After the canvas is ready, build problem
          generatePoints();
          computeAllSequences();
          state.edgesSequence = (state.algorithm === 'prim') ? state.edgesPrim.slice() : state.edgesKruskal.slice();
          state.currentStep = 0;
          state.animationState = 'stopped';
          updateProcessButtons();
        };

        p.windowResized = function () {
          const rect = container.getBoundingClientRect();
          const w = Math.max(10, rect.width);
          const h = Math.max(10, rect.height);
          p.resizeCanvas(w, h);
          // Keep worldSize for 3D as is to preserve points in the same cube scale
        };

        p.draw = function () {
          if (state.dimension === '2D') draw2D(p);
          else draw3D(p);

          // Handle animation stepping
          if (state.animationState === 'playing') {
            if (p.frameCount - lastStepFrame >= framesPerStep) {
              lastStepFrame = p.frameCount;
              if (state.currentStep < state.edgesSequence.length) {
                state.currentStep++;
                if (state.currentStep >= state.edgesSequence.length) {
                  // Completed
                  state.animationState = 'stopped';
                  updateProcessButtons();
                }
              } else {
                // Just in case
                state.animationState = 'stopped';
                updateProcessButtons();
              }
            }
          }
        };

        function draw2D(p) {
          p.background('#E0E0E0');

          // Draw existing MST edges
          p.stroke('red');
          p.strokeWeight(2);
          for (let k = 0; k < state.currentStep; k++) {
            const e = state.edgesSequence[k];
            const a = state.points[e.i], b = state.points[e.j];
            p.line(a.x, a.y, b.x, b.y);
          }

          // Draw points
          p.noStroke();
          p.fill('blue');
          for (const pt of state.points) {
            p.circle(pt.x, pt.y, 7);
          }
        }

        function draw3D(p) {
          p.background('#E0E0E0');

          // 3D camera control
          p.orbitControl();

          // Light for materials
          p.ambientLight(200);
          p.directionalLight(255, 255, 255, 0.25, 0.25, -1);

          // Wireframe cube (world bounds)
          p.push();
          p.noFill();
          p.stroke(150);
          p.strokeWeight(1);
          p.box(state.worldSize);
          p.pop();

          // Draw existing MST edges
          p.push();
          p.stroke('red');
          p.strokeWeight(2);
          for (let k = 0; k < state.currentStep; k++) {
            const e = state.edgesSequence[k];
            const a = state.points[e.i], b = state.points[e.j];
            p.line(a.x, a.y, a.z, b.x, b.y, b.z);
          }
          p.pop();

          // Draw points as spheres
          p.noStroke();
          p.ambientMaterial(50, 100, 255);
          for (const pt of state.points) {
            p.push();
            p.translate(pt.x, pt.y, pt.z);
            p.sphere(6);
            p.pop();
          }
        }
      });
    }

    // UI event listeners
    btn2D.addEventListener('click', () => {
      if (state.dimension !== '2D') {
        state.dimension = '2D';
        setExclusiveActive(btn2D, btn3D);
        fullProblemReset(true); // recreate canvas in 2D mode
      }
    });
    btn3D.addEventListener('click', () => {
      if (state.dimension !== '3D') {
        state.dimension = '3D';
        setExclusiveActive(btn3D, btn2D);
        fullProblemReset(true); // recreate canvas in WEBGL mode
      }
    });

    sliderSeed.addEventListener('input', () => {
      state.seed = parseInt(sliderSeed.value, 10);
      seedValue.textContent = String(state.seed);
      fullProblemReset(false); // regenerate points and sequences
    });

    sliderNumber.addEventListener('input', () => {
      state.number = parseInt(sliderNumber.value, 10);
      numberValue.textContent = String(state.number);
      fullProblemReset(false); // regenerate points and sequences
    });

    btnPrim.addEventListener('click', () => switchAlgorithm('prim'));
    btnKruskal.addEventListener('click', () => switchAlgorithm('kruskal'));

    btnPlay.addEventListener('click', () => {
      if (!state.edgesSequence || state.edgesSequence.length === 0) return;
      if (state.currentStep >= state.edgesSequence.length) {
        // Already completed; keep play disabled
        updateProcessButtons();
        return;
      }
      state.animationState = 'playing';
      lastStepFrame = p5Instance ? p5Instance.frameCount : 0;
      updateProcessButtons();
    });

    btnPause.addEventListener('click', () => {
      state.animationState = 'paused';
      updateProcessButtons();
    });

    btnReset.addEventListener('click', () => {
      state.animationState = 'stopped';
      state.currentStep = 0;
      updateProcessButtons();
    });

    // Initialize
    function init() {
      // Defaults already set in HTML and state
      seedValue.textContent = String(state.seed);
      numberValue.textContent = String(state.number);
      setExclusiveActive(btn2D, btn3D);
      setExclusiveActive(btnPrim, btnKruskal);
      updateProcessButtons();
      createSketch();
    }

    // Start after DOM is ready
    if (document.readyState === 'loading') {
      document.addEventListener('DOMContentLoaded', init);
    } else {
      init();
    }
  </script>
</body>
</html>
