<script>
(function(){
  const selectMethod = document.getElementById('select-method');
  const sliderNum1 = document.getElementById('slider-num1');
  const sliderNum2 = document.getElementById('slider-num2');
  const btnIntYes = document.getElementById('btn-int-yes');
  const btnIntNo = document.getElementById('btn-int-no');
  const output = document.getElementById('output-display');

  function isApproximatelyInteger(x){
    return Math.abs(x - Math.round(x)) < 1e-10;
  }

  function getIntStringAndFactor(n){
    if (isApproximatelyInteger(n)){
      return { intString: String(Math.round(n)), factor: 1 };
    }
    let factor = 1;
    // Increase factor until scaled is integer (within epsilon)
    while (!isApproximatelyInteger(n * factor) && factor < 1e6){
      factor *= 10;
    }
    const scaled = Math.round(n * factor);
    return { intString: String(scaled), factor: factor };
  }

  function fmt(n){
    // up to 6 decimals, strip trailing zeros
    return n.toFixed(6).replace(/\.?0+$/,'');
  }

  function setToggleState(integersOnly){
    if (integersOnly){
      btnIntYes.classList.add('active');
      btnIntYes.classList.remove('inactive');
      btnIntNo.classList.remove('active');
      btnIntNo.classList.add('inactive');
    } else {
      btnIntNo.classList.add('active');
      btnIntNo.classList.remove('inactive');
      btnIntYes.classList.remove('active');
      btnIntYes.classList.add('inactive');
    }
  }

  function updateDisplay(){
    const method = selectMethod.value;
    const num1 = parseFloat(sliderNum1.value);
    const num2 = parseFloat(sliderNum2.value);

    const n1 = getIntStringAndFactor(num1);
    const n2 = getIntStringAndFactor(num2);
    const int1 = parseInt(n1.intString, 10);
    const int2 = parseInt(n2.intString, 10);
    const productOfInts = int1 * int2;
    const productOfFactors = n1.factor * n2.factor;

    const lines = [];
    if (method === 'estimation'){
      lines.push(`start with: ${fmt(num1)} × ${fmt(num2)}`);
      lines.push(`convert to: ${n1.intString}. × ${n2.intString}. = ${productOfInts}.`);
      const r1 = Math.round(num1);
      const r2 = Math.round(num2);
      lines.push(`estimate as: ${r1} × ${r2} = ${r1 * r2}`);
      lines.push(`result is: ${fmt(num1 * num2)}`);
    } else {
      lines.push(`start with: ${fmt(num1)} × ${fmt(num2)}`);
      const part1 = (n1.factor === 1) ? `${n1.intString}.` : `${n1.intString}. ÷ ${n1.factor}`;
      const part2 = (n2.factor === 1) ? `${n2.intString}.` : `${n2.intString}. ÷ ${n2.factor}`;
      lines.push(`rewrite as: (${part1}) × (${part2})`);
      lines.push(`= (${n1.intString}. × ${n2.intString}.) ÷ (${n1.factor} × ${n2.factor})`);
      if (productOfFactors === 1){
        lines.push(`= ${productOfInts}.`);
      } else {
        lines.push(`= ${productOfInts}. ÷ ${productOfFactors}`);
      }
      lines.push(`= ${fmt(num1 * num2)}`);
    }

    // Render
    output.innerHTML = '';
    for (const line of lines){
      const p = document.createElement('p');
      p.textContent = line;
      output.appendChild(p);
    }
  }

  // Event listeners
  selectMethod.addEventListener('change', updateDisplay);
  sliderNum1.addEventListener('input', updateDisplay);
  sliderNum2.addEventListener('input', updateDisplay);

  btnIntYes.addEventListener('click', () => {
    sliderNum2.step = '1';
    // Round current value to nearest integer
    sliderNum2.value = String(Math.round(parseFloat(sliderNum2.value)));
    setToggleState(true);
    updateDisplay();
  });

  btnIntNo.addEventListener('click', () => {
    sliderNum2.step = '0.1';
    setToggleState(false);
    updateDisplay();
  });

  // Initialize defaults
  setToggleState(true); // integers only active by default
  updateDisplay();
})();
</script>
</body>
</html>
