    // Cameras
    moleculeCamera = new THREE.PerspectiveCamera(50, 1, 0.1, 100);
    moleculeCamera.position.set(4.5, 3.5, 7);
    moleculeCamera.lookAt(0,0,0);

    vectorsCamera = new THREE.PerspectiveCamera(50, 1, 0.1, 100);
    vectorsCamera.position.set(0, 0, 8);
    vectorsCamera.lookAt(0,0,0);

    // OrbitControls using two invisible hit areas overlaying the single canvas
    const leftHit = document.getElementById('left-hitarea');
    const rightHit = document.getElementById('right-hitarea');
    leftControls = new THREE.OrbitControls(moleculeCamera, leftHit);
    rightControls = new THREE.OrbitControls(vectorsCamera, rightHit);
    leftControls.enableDamping = true;
    rightControls.enableDamping = true;

    // Model: Carbon
    const carbonGeom = new THREE.SphereGeometry(0.5, 32, 32);
    const carbonMat = new THREE.MeshLambertMaterial({ color: 0x111111 });
    carbonMesh = new THREE.Mesh(carbonGeom, carbonMat);
    moleculeScene.add(carbonMesh);

    // Model: Hydrogens
    const hGeom = new THREE.SphereGeometry(0.28, 24, 24);
    const hMat = new THREE.MeshLambertMaterial({ color: 0xeaf4ff, emissive: 0x000000 });
    const tetra = [
      new THREE.Vector3( 1,  1,  1),
      new THREE.Vector3( 1, -1, -1),
      new THREE.Vector3(-1,  1, -1),
      new THREE.Vector3(-1, -1,  1)
    ];
    for (let i=0; i<4; i++){
      const mesh = new THREE.Mesh(hGeom, hMat);
      const dir = tetra[i].clone().normalize();
      const pos = dir.clone().multiplyScalar(CH0);
      hydrogenInitial.push(pos.clone());
      hydrogenDirs.push(dir.clone());
      mesh.position.copy(pos);
      hydrogenMeshes.push(mesh);
      moleculeScene.add(mesh);
    }

    // Model: Bonds (cylinders from C to each H)
    const bondGeom = new THREE.CylinderGeometry(0.08, 0.08, 1, 20, 1, true);
    const bondMat = new THREE.MeshPhongMaterial({ color: 0x9e9e9e, shininess: 30 });
    for (let i=0; i<4; i++){
      const cyl = new THREE.Mesh(bondGeom, bondMat);
      moleculeScene.add(cyl);
      bonds.push(cyl);
    }

    // Vectors (dipoles) arrows
    const arrowColor = 0x1565c0; // blueish for individual bond dipoles
    for (let i=0; i<4; i++){
      const dir = hydrogenDirs[i].clone().normalize();
      const arrow = new THREE.ArrowHelper(dir, new THREE.Vector3(0,0,0), CH0, arrowColor, 0.35, 0.18);
      vectorsScene.add(arrow);
      dipoleArrows.push(arrow);
    }
    netDipoleArrow = new THREE.ArrowHelper(new THREE.Vector3(1,0,0), new THREE.Vector3(0,0,0), 0.001, 0xc62828, 0.5, 0.3);
    netDipoleArrow.visible = false;
    vectorsScene.add(netDipoleArrow);

    onResize(); // size renderer and first draw
  }

  // ---------------- Animation and Updates ----------------
  function animate(ts){
    if (!isPlaying) return;
    if (!lastTS) lastTS = ts;
    const dt = Math.min(0.05, (ts - lastTS) / 1000);
    lastTS = ts;
    t += dt;

    updateMoleculeAndVectors(t);
    renderOnce();

    rafId = requestAnimationFrame(animate);
  }

  function renderOnce(){
    // Size and set up viewports
    const rect = threeContainer.getBoundingClientRect();
    const width = Math.max(2, Math.floor(rect.width));
    const height = Math.max(2, Math.floor(rect.height));
    if (renderer.domElement.width !== Math.floor(width * renderer.getPixelRatio()) ||
        renderer.domElement.height !== Math.floor(height * renderer.getPixelRatio())) {
      renderer.setSize(width, height, false);
    }

    const halfW = Math.floor(width / 2);

    // Left: Molecule
    renderer.setViewport(0, 0, halfW, height);
    renderer.setScissor(0, 0, halfW, height);
    moleculeCamera.aspect = halfW / height;
    moleculeCamera.updateProjectionMatrix();
    leftControls.update();
    renderer.render(moleculeScene, moleculeCamera);

    // Right: Vectors
    renderer.setViewport(halfW, 0, width - halfW, height);
    renderer.setScissor(halfW, 0, width - halfW, height);
    vectorsCamera.aspect = (width - halfW) / height;
    vectorsCamera.updateProjectionMatrix();
    rightControls.update();
    renderer.render(vectorsScene, vectorsCamera);
  }

  function updateMoleculeAndVectors(time){
    const omega = FREQS[currentMode] || 1.0;
    const s = Math.sin(omega * time);
    const axis = new THREE.Vector3(0,1,0);

    // Compute new positions per mode
    for (let i=0; i<4; i++){
      const h = hydrogenMeshes[i];
      const n = hydrogenDirs[i];
      let pos = hydrogenInitial[i].clone();

      if (currentMode === 'symmetric-stretch') {
        pos.addScaledVector(n, AMP_STRETCH * s);
      } else if (currentMode === 'asymmetric-stretch') {
        // Triply degenerate F2: create an asymmetric pattern
        // Two opposite bonds out of phase, the other two smaller compensations
        const coeffs = [+1.0, -1.0, +0.4, -0.4];
        pos.addScaledVector(n, coeffs[i] * AMP_STRETCH * s);
      } else if (currentMode === 'bending-e') {
        // E (doubly degenerate) bending: tangential with pairwise opposite displacements
        const tangent = n.clone().cross(axis).normalize();
        const coeffs = [+1, -1, +1, -1];
        pos.addScaledVector(tangent, coeffs[i] * AMP_BEND * s * 0.5);
      } else if (currentMode === 'bending-f2') {
        // F2 (triply degenerate) bending: tangential with a net dipole change
        const tangent = n.clone().cross(axis).normalize();
        const coeffs = [+1.0, +0.6, -0.4, -0.2];
        pos.addScaledVector(tangent, coeffs[i] * AMP_BEND * s * 0.7);
      }

      // Update hydrogen position
      h.position.copy(pos);

      // Update bond cylinder from Carbon(0,0,0) to H
      alignCylinder(bonds[i], new THREE.Vector3(0,0,0), pos);
    }

    // Update individual dipole arrows based on current bond vectors
    let net = new THREE.Vector3(0,0,0);
    for (let i=0; i<4; i++){
      const v = hydrogenMeshes[i].position.clone().sub(new THREE.Vector3(0,0,0)); // C->H
      net.add(v);
      const len = v.length();
      const dir = (len > 1e-6) ? v.clone().normalize() : new THREE.Vector3(1,0,0);
      dipoleArrows[i].setDirection(dir);
      dipoleArrows[i].setLength(Math.max(0.1, len * 0.9), 0.35, 0.18);
      dipoleArrows[i].position.set(0,0,0);
    }

    // Net dipole arrow (only visible for F2 modes)
    const isF2 = (currentMode === 'asymmetric-stretch' || currentMode === 'bending-f2');
    const netLen = net.length();
    if (isF2 && netLen > 0.01) {
      netDipoleArrow.visible = true;
      netDipoleArrow.setDirection(net.clone().normalize());
      netDipoleArrow.setLength(netLen, 0.6, 0.3);
    } else {
      netDipoleArrow.visible = false;
      netDipoleArrow.setLength(0.0001);
    }
  }

  function alignCylinder(cyl, start, end){
    const dir = new THREE.Vector3().subVectors(end, start);
    const len = dir.length();
    const mid = new THREE.Vector3().addVectors(start, end).multiplyScalar(0.5);

    cyl.position.copy(mid);
    cyl.scale.set(1, len, 1);

    const yAxis = new THREE.Vector3(0,1,0);
    const quat = new THREE.Quaternion().setFromUnitVectors(yAxis, dir.clone().normalize());
    cyl.setRotationFromQuaternion(quat);
  }

  // ---------------- Controls and Mode Handling ----------------
  function onPlay(){
    if (isPlaying) return;
    isPlaying = true;
    btnPlay.disabled = true;
    btnPause.disabled = false;
    rafId = requestAnimationFrame(animate);
  }

  function onPause(){
    if (!isPlaying) return;
    isPlaying = false;
    btnPlay.disabled = false;
    btnPause.disabled = true;
    if (rafId) cancelAnimationFrame(rafId);
    rafId = null;
  }

  function onReset(){
    onPause();
    resetAnimation();
    renderOnce();
  }

  function resetAnimation(){
    t = 0;
    lastTS = 0;
    // Reset H positions to initial
    for (let i=0; i<4; i++){
      hydrogenMeshes[i].position.copy(hydrogenInitial[i]);
      alignCylinder(bonds[i], new THREE.Vector3(0,0,0), hydrogenInitial[i]);
      // Reset dipole arrows
      const dir = hydrogenInitial[i].clone().normalize();
      dipoleArrows[i].setDirection(dir);
      dipoleArrows[i].setLength(CH0 * 0.9, 0.35, 0.18);
    }
    netDipoleArrow.visible = false;
  }

  function onModeChange(){
    const val = selectMode.value;
    currentMode = val;

    if (val === 'ir-spectrum') {
      // Switch to spectrum view
      threeContainer.style.display = 'none';
      descArea.style.display = 'none';
      plotlyContainer.style.display = 'block';

      // Disable controls
      btnPlay.disabled = true;
      btnPause.disabled = true;
      btnReset.disabled = true;

      // Stop animation and reset
      onPause();

      // Create plot if not yet
      if (!plotlyContainer.dataset.rendered) {
        createIRSpectrumPlot().then(() => {
          plotlyContainer.dataset.rendered = '1';
        });
      }
    } else {
      // Back to animation view
      plotlyContainer.style.display = 'none';
      threeContainer.style.display = 'block';
      descArea.style.display = 'flex';

      // Enable controls
      btnPlay.disabled = false;
      btnPause.disabled = false;
      btnReset.disabled = false;

      // Reset animation state
      onReset();

      // Update labels/colors
      updateLabels(val);
    }
    onResize();
    renderOnce();
  }

  function updateLabels(mode){
    if (mode === 'symmetric-stretch') {
      labelLeft.textContent = 'stretching A₁ Raman active 2916 cm⁻¹';
      labelRight.textContent = 'no change in the molecular dipole moment';
      labelLeft.style.color = 'blue';
      labelRight.style.color = 'blue';
    } else if (mode === 'asymmetric-stretch') {
      labelLeft.textContent = 'stretching F₂ IR active 3019 cm⁻¹';
      labelRight.textContent = 'change in the molecular dipole moment';
      labelLeft.style.color = 'red';
      labelRight.style.color = 'red';
    } else if (mode === 'bending-e') {
      labelLeft.textContent = 'bending E Raman active 1534 cm⁻¹';
      labelRight.textContent = 'no change in the molecular dipole moment';
      labelLeft.style.color = 'blue';
      labelRight.style.color = 'blue';
    } else if (mode === 'bending-f2') {
      labelLeft.textContent = 'bending F₂ IR active 1306 cm⁻¹';
      labelRight.textContent = 'change in the molecular dipole moment';
      labelLeft.style.color = 'red';
      labelRight.style.color = 'red';
    }
  }

  function onResize(){
    // Resize renderer canvas and hitareas to match container
    const rect = threeContainer.getBoundingClientRect();
    renderer.setSize(rect.width, rect.height, false);
    renderOnce();
  }

  // ---------------- IR Spectrum (Plotly) ----------------
  async function createIRSpectrumPlot(){
    // Generate x
    const xmin = 400, xmax = 4000, step = 2;
    const x = [];
    for (let v = xmin; v <= xmax; v += step) x.push(v);

    // Baseline with noise
    const baseline = 0.95;
    const y = x.map(() => baseline + (Math.random()-0.5)*0.01);

    // Gaussian helper
    const gaussian = (x, mu, sigma) => Math.exp(-0.5 * Math.pow((x - mu)/sigma, 2));

    // Major peaks F2 modes: 1306 and 3019 cm^-1 (absorption dips)
    const peaks = [
      { mu: 1306, sigma: 25, A: 0.80 },
      { mu: 3019, sigma: 30, A: 0.83 },
    ];
    for (let i=0; i<x.length; i++){
      const xi = x[i];
      for (const p of peaks) {
        y[i] -= p.A * gaussian(xi, p.mu, p.sigma);
      }
    }

    // Rotational fine structure: multiple smaller dips around each main peak
    function addFineStructure(center, width, count){
      for (let k=0; k<count; k++){
        const mu = center + (Math.random()*2-1) * width;
        const sigma = 2 + Math.random()*3; // sharp lines
        const A = 0.02 + Math.random()*0.06;
        for (let i=0; i<x.length; i++){
          y[i] -= A * gaussian(x[i], mu, sigma);
        }
      }
    }
    addFineStructure(1306, 60, 22);
    addFineStructure(3019, 90, 26);

    // Clamp y to [0, 1.1] visually nice
    for (let i=0; i<y.length; i++) {
      y[i] = Math.min(1.05, Math.max(0, y[i]));
    }

    // Prepare molecule pose images using offscreen three.js scenes
    const imgStretch = await createOffscreenMoleculePose('f2-stretch');
    const imgBend = await createOffscreenMoleculePose('f2-bend');

    const data = [{
      x, y,
      mode: 'lines',
      line: { color: '#37474f', width: 2 },
      hovertemplate: 'wavenumber: %{x} cm⁻¹<br>Transmittance: %{y:.2f}<extra></extra>',
      name: 'CH₄ IR'
    }];

    const layout = {
      margin: { l: 60, r: 40, t: 40, b: 60 },
      xaxis: { title: 'wavenumber (cm⁻¹)', range: [xmin, xmax], mirror: 'ticks', zeroline: false },
      yaxis: { title: 'Transmittance', range: [0, 1.1], mirror: 'ticks', zeroline: false },
      showlegend: false,
      annotations: [
        {
          x: 1306, y: 0.22, xref: 'x', yref: 'y',
          text: 'ν F₂ 1306 cm⁻¹',
          font: { color: '#c62828', size: 12 },
          ax: 40, ay: -40,
          arrowcolor: '#c62828',
          arrowsize: 1, arrowwidth: 1.5, arrowhead: 3,
          bgcolor: 'rgba(255,255,255,0.6)'
        },
        {
          x: 3019, y: 0.22, xref: 'x', yref: 'y',
          text: 'ν F₂ 3019 cm⁻¹',
          font: { color: '#c62828', size: 12 },
          ax: -40, ay: -40,
          arrowcolor: '#c62828',
          arrowsize: 1, arrowwidth: 1.5, arrowhead: 3,
          bgcolor: 'rgba(255,255,255,0.6)'
        }
      ],
      images: [
        {
          source: imgBend,
          xref: 'x', yref: 'paper',
          x: 1306, y: 0.95,
          sizex: 230, sizey: 0.32, // sizex in cm^-1 units; sizey in paper (0..1)
          xanchor: 'center', yanchor: 'top',
          opacity: 0.95,
          layer: 'above'
        },
        {
          source: imgStretch,
          xref: 'x', yref: 'paper',
          x: 3019, y: 0.95,
          sizex: 260, sizey: 0.32,
          xanchor: 'center', yanchor: 'top',
          opacity: 0.95,
          layer: 'above'
        }
      ]
    };

    Plotly.newPlot(plotlyContainer, data, layout, {responsive: true, displayModeBar: false});
  }

  // Create an offscreen Three.js render of methane illustrating a pose for F2 modes
  function createOffscreenMoleculePose(type='f2-stretch'){
    return new Promise((resolve) => {
      const size = 220;
      const rendererOff = new THREE.WebGLRenderer({ antialias: true, alpha: true });
      rendererOff.setSize(size, size, false);
      rendererOff.setPixelRatio(1);

      const scene = new THREE.Scene();
      const camera = new THREE.PerspectiveCamera(40, 1, 0.1, 100);
      camera.position.set(2.8, 2.6, 5.4);
      camera.lookAt(0,0,0);

      const amb = new THREE.AmbientLight(0xffffff, 0.7);
      const dir = new THREE.DirectionalLight(0xffffff, 0.9);
      dir.position.set(4,6,8);
      scene.add(amb, dir);

      // Carbon
      const carbon = new THREE.Mesh(new THREE.SphereGeometry(0.45, 28, 28), new THREE.MeshLambertMaterial({ color: 0x222222 }));
      scene.add(carbon);

      // Hydrogens
      const hGeom = new THREE.SphereGeometry(0.24, 20, 20);
      const hMat = new THREE.MeshLambertMaterial({ color: 0xeaf4ff });
      const tetra = [
        new THREE.Vector3( 1,  1,  1),
        new THREE.Vector3( 1, -1, -1),
        new THREE.Vector3(-1,  1, -1),
        new THREE.Vector3(-1, -1,  1)
      ];
      const dirs = tetra.map(v => v.clone().normalize());
      const H = dirs.map(d => d.clone().multiplyScalar(1.4)); // slightly shorter for image
      const bondsLocal = [];

      // Apply pose
      const axis = new THREE.Vector3(0,1,0);
      if (type === 'f2-stretch') {
        const coeffs = [+1.0, -1.0, +0.4, -0.4];
        for (let i=0; i<4; i++) {
          H[i].addScaledVector(dirs[i], coeffs[i] * 0.28);
        }
      } else if (type === 'f2-bend') {
        const coeffs = [+1.0, +0.6, -0.4, -0.2];
        for (let i=0; i<4; i++) {
          const tangent = dirs[i].clone().cross(axis).normalize();
          H[i].addScaledVector(tangent, coeffs[i] * 0.35);
        }
      }

      // Add hydrogens and bonds
      const bondGeom = new THREE.CylinderGeometry(0.06, 0.06, 1, 16, 1, true);
      const bondMat = new THREE.MeshPhongMaterial({ color: 0x9e9e9e, shininess: 30 });
      for (let i=0; i<4; i++) {
        const h = new THREE.Mesh(hGeom, hMat);
        h.position.copy(H[i]);
        scene.add(h);

        const b = new THREE.Mesh(bondGeom, bondMat);
        scene.add(b);
        alignCylinder(b, new THREE.Vector3(0,0,0), H[i]);
        bondsLocal.push(b);
      }

      rendererOff.render(scene, camera);
      const dataURL = rendererOff.domElement.toDataURL('image/png');
      resolve(dataURL);
    });
  }

  // ---------------- End IR Spectrum ----------------

})();
</script>
