  <script>
    // --- Constants and helpers ---
    const LEN_MM = 16;          // total physical length visualized (mm)
    const GLASS_MM = 6;         // glass plate thickness (mm)
    const sOfT = t => 0.155 * Math.sqrt(t); // Stefan distance (mm)
    const clamp = (v, min, max) => Math.max(min, Math.min(max, v));

    // --- Element references ---
    const slider = document.getElementById('slider-time');
    const spanTime = document.getElementById('span-time-value');
    const canvas = document.getElementById('canvas-material-bar');
    const ctx = canvas.getContext('2d');
    const plotDiv = document.getElementById('div-plot');

    // --- Canvas drawing ---
    function drawMaterialBar(t) {
      const s = sOfT(t); // mm
      const pxPerMm = canvas.width / LEN_MM;

      // Clear
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      // Bar geometry
      const barTop = 14;
      const barHeight = 28;

      const glassPxW = GLASS_MM * pxPerMm;
      const waterPxW = s * pxPerMm;
      const icePxX0 = glassPxW + waterPxW;
      const icePxW = (LEN_MM * pxPerMm) - icePxX0;

      // Glass gradient: hot (red) to white
      const grad = ctx.createLinearGradient(0, 0, glassPxW, 0);
      grad.addColorStop(0, '#FF0000');
      grad.addColorStop(1, '#FFFFFF');

      // Draw glass
      ctx.fillStyle = grad;
      ctx.fillRect(0, barTop, glassPxW, barHeight);
      ctx.strokeStyle = 'rgba(0,0,0,0.15)';
      ctx.strokeRect(0.5, barTop + 0.5, glassPxW - 1, barHeight - 1);

      // Draw melted water (light blue)
      ctx.fillStyle = '#00BFFF';
      if (waterPxW > 0) {
        ctx.fillRect(glassPxW, barTop, waterPxW, barHeight);
        ctx.strokeStyle = 'rgba(0,0,0,0.08)';
        ctx.strokeRect(glassPxW + 0.5, barTop + 0.5, Math.max(0, waterPxW - 1), barHeight - 1);
      }

      // Draw ice (dark blue)
      ctx.fillStyle = '#003366';
      if (icePxW > 0) {
        ctx.fillRect(icePxX0, barTop, icePxW, barHeight);
        ctx.strokeStyle = 'rgba(255,255,255,0.15)';
        ctx.strokeRect(icePxX0 + 0.5, barTop + 0.5, Math.max(0, icePxW - 1), barHeight - 1);
      }

      // Axis with labels 0, 5, 10, 15 (mm)
      const axisY = barTop + barHeight + 22;
      ctx.strokeStyle = '#444';
      ctx.lineWidth = 1;
      ctx.beginPath();
      ctx.moveTo(0, axisY);
      ctx.lineTo(canvas.width, axisY);
      ctx.stroke();

      // Tick marks and labels
      ctx.fillStyle = '#333';
      ctx.font = '12px Arial';
      ctx.textAlign = 'center';
      ctx.textBaseline = 'top';
      const ticks = [0, 5, 10, 15];
      ticks.forEach(mm => {
        const x = mm * pxPerMm;
        ctx.strokeStyle = '#444';
        ctx.beginPath();
        ctx.moveTo(x, axisY - 5);
        ctx.lineTo(x, axisY + 5);
        ctx.stroke();
        ctx.fillText(String(mm), x, axisY + 8);
      });
    }

    // --- Plotly initialization ---
    function initPlot(t0) {
      const x = [];
      const y = [];
      // Generate curve for t in [0, 100]
      for (let t = 0; t <= 100; t += 0.1) {
        x.push(+t.toFixed(1));
        y.push(sOfT(t));
      }

      const trace = {
        x, y,
        mode: 'lines',
        line: { color: '#FFA500', width: 3 },
        hovertemplate: 't=%{x:.1f} s<br>s=%{y:.3f} mm<extra></extra>'
      };

      const s0 = sOfT(t0);
      const layout = {
        title: 'Stefan distance versus time',
        margin: { l: 60, r: 20, t: 50, b: 50 },
        xaxis: { title: 'time (s)', range: [0, 100], zeroline: false },
        yaxis: { title: 'distance (mm)', range: [0, 1.6], zeroline: true },
        shapes: [
          {
            type: 'line',
            xref: 'x',
            yref: 'y',
            x0: t0,
            x1: t0,
            y0: 0,
            y1: s0,
            line: { color: '#000000', width: 2 }
          }
        ]
      };

      Plotly.newPlot(plotDiv, [trace], layout, { displayModeBar: false, responsive: true });
    }

    function updatePlotIndicator(t) {
      const s = sOfT(t);
      Plotly.relayout(plotDiv, {
        'shapes[0].x0': t,
        'shapes[0].x1': t,
        'shapes[0].y0': 0,
        'shapes[0].y1': s
      });
    }

    // --- Master update ---
    function updateAll(t) {
      const tNum = clamp(Number(t) || 0, 0, 100);
      spanTime.textContent = tNum.toFixed(1);
      drawMaterialBar(tNum);
      updatePlotIndicator(tNum);
    }

    // --- Event bindings ---
    slider.addEventListener('input', () => {
      const t = parseFloat(slider.value);
      updateAll(t);
    });

    document.getElementById('btn-reset').addEventListener('click', () => {
      slider.value = '0';
      slider.dispatchEvent(new Event('input', { bubbles: true }));
    });

    // --- Initial load ---
    (function init() {
      const t0 = parseFloat(slider.value) || 9.8;
      spanTime.textContent = t0.toFixed(1);
      initPlot(t0);
      drawMaterialBar(t0);
      // Ensure indicator aligns after initial plot render
      updatePlotIndicator(t0);
    })();
  </script>
</body>
</html>
