<script>
(function() {
  // Global state
  let userLat = 51.5;
  let userLon = 0.0;

  let localTime = new Date();
  let solarTime = new Date();

  const REF_YEAR = 2018; // Non-leap reference so 07 Dec is Friday (for initial display)

  const dateSlider = document.getElementById('date-slider');
  const dateDisplay = document.getElementById('date-display');
  const todayButton = document.getElementById('today-button');
  const localDigital = document.getElementById('local-time-digital');
  const solarDigital = document.getElementById('solar-time-digital');
  const offsetDisplay = document.getElementById('offset-display');

  // Remove placeholder canvases; p5 will create canvases with the same IDs
  const localCanvasPlaceholder = document.getElementById('local-clock-canvas');
  const solarCanvasPlaceholder = document.getElementById('solar-clock-canvas');
  if (localCanvasPlaceholder) localCanvasPlaceholder.remove();
  if (solarCanvasPlaceholder) solarCanvasPlaceholder.remove();

  // Geolocation
  if (navigator.geolocation && location.protocol === 'https:') {
    navigator.geolocation.getCurrentPosition(
      (pos) => {
        userLat = pos.coords.latitude;
        userLon = pos.coords.longitude;
      },
      () => {
        // Fallback already set to London
      },
      { enableHighAccuracy: false, timeout: 5000, maximumAge: 60000 }
    );
  } else {
    // If not https or not available, keep defaults (London)
  }

  // Utility functions
  function pad2(n) {
    return n.toString().padStart(2, '0');
  }
  function formatTime(t) {
    return `${pad2(t.getHours())}:${pad2(t.getMinutes())}:${pad2(t.getSeconds())}`;
  }
  function toRadians(deg) {
    return deg * Math.PI / 180;
  }
  function calcEoT_minutes(dayIndex0to364) {
    // B in degrees using day number d (0..364)
    const B = (360 / 365.24) * (dayIndex0to364 - 81);
    const Br = toRadians(B);
    // EoT in minutes
    const EoT = 9.87 * Math.sin(2 * Br) - 7.53 * Math.cos(Br) - 1.5 * Math.sin(Br);
    return EoT;
  }
  function isLeapYear(y) {
    return (y % 4 === 0 && y % 100 !== 0) || (y % 400 === 0);
  }
  function dayOfYearNonLeap(date) {
    // Return day index 0..364 for the given date, ignoring Feb 29
    const startOfYear = new Date(date.getFullYear(), 0, 1);
    const msPerDay = 24 * 60 * 60 * 1000;
    let doy = Math.floor((date - startOfYear) / msPerDay);
    if (isLeapYear(date.getFullYear())) {
      // If past Feb 28 in a leap year, compress by one
      const afterFeb28 = date >= new Date(date.getFullYear(), 1, 29);
      if (afterFeb28) {
        doy = Math.max(0, doy - 1);
      }
    }
    // Clamp to [0, 364]
    return Math.max(0, Math.min(364, doy));
  }
  function dateFromDayNonLeap(d) {
    // Create a Date in REF_YEAR from day index d (0..364)
    const dt = new Date(REF_YEAR, 0, 1);
    dt.setDate(dt.getDate() + Number(d));
    return dt;
  }
  function updateDateDisplayFromSlider() {
    const d = Number(dateSlider.value);
    const refDate = dateFromDayNonLeap(d);
    const weekdayNames = ['Sunday','Monday','Tuesday','Wednesday','Thursday','Friday','Saturday'];
    const monthNames = ['January','February','March','April','May','June','July','August','September','October','November','December'];
    const text = `${weekdayNames[refDate.getDay()]}, ${pad2(refDate.getDate())} ${monthNames[refDate.getMonth()]}`;
    dateDisplay.textContent = text;
  }

  // Compute times and update UI
  function computeAndUpdate() {
    // Local system time
    localTime = new Date();

    // Equation of Time depends on selected day-of-year
    const d = Number(dateSlider.value);
    const EoT = calcEoT_minutes(d); // minutes

    // Convert local time to UTC ms
    const utcMs = localTime.getTime() + localTime.getTimezoneOffset() * 60000;

    // Solar time = UTC + (EoT + 4 * longitude) minutes
    const offsetMinutes = EoT + 4 * userLon;
    const solarMs = utcMs + offsetMinutes * 60000;
    solarTime = new Date(solarMs);

    // Update digital displays
    localDigital.textContent = formatTime(localTime);
