    if (localClockSketch) localClockSketch.redraw();
    if (solarClockSketch) solarClockSketch.redraw();
  }

  // Initialize date display from slider default (keeps initial required text, then syncs)
  updateDateDisplayFromSlider();

  // Slider interaction
  dateSlider.addEventListener('input', updateDateDisplayFromSlider);

  // Today button interaction
  todayButton.addEventListener('click', function() {
    const today = new Date();
    const doy = dayOfYearNonLeap(today);
    dateSlider.value = String(doy);
    updateDateDisplayFromSlider();
  });

  // p5 sketches
  const CLOCK_SIZE = 360;

  function drawClock(p, timeObj, faceColor) {
    const w = CLOCK_SIZE;
    const h = CLOCK_SIZE;
    const cx = w / 2;
    const cy = h / 2;
    const radius = Math.min(w, h) * 0.45;

    p.push();
    p.translate(cx, cy);

    // Face: outer thick circle
    p.stroke(0);
    p.strokeWeight(6);
    p.fill(faceColor);
    p.circle(0, 0, radius * 2);

    // Markers
    // Minute ticks (60), Hour ticks (12)
    for (let i = 0; i < 60; i++) {
      const angle = p.radians(i * 6 - 90);
      const isHourTick = i % 5 === 0;
      const r1 = radius * (isHourTick ? 0.82 : 0.88);
      const r2 = radius * 0.96;
      p.stroke(0);
      p.strokeWeight(isHourTick ? 3 : 1.5);
      const x1 = r1 * Math.cos(angle);
      const y1 = r1 * Math.sin(angle);
      const x2 = r2 * Math.cos(angle);
      const y2 = r2 * Math.sin(angle);
      p.line(x1, y1, x2, y2);
    }

    // Numbers 1..12
    p.fill(0);
    p.noStroke();
    p.textAlign(p.CENTER, p.CENTER);
    p.textSize(radius * 0.14);
    for (let n = 1; n <= 12; n++) {
      const angle = p.radians(n * 30 - 90);
      const nr = radius * 0.68;
      const nx = nr * Math.cos(angle);
      const ny = nr * Math.sin(angle);
      p.text(n.toString(), nx, ny);
    }

    // Time components
    const hours = timeObj.getHours() % 12;
    const minutes = timeObj.getMinutes();
    const seconds = timeObj.getSeconds();

    // Angles
    const secAngle = p.radians((seconds * 6) - 90);
    const minAngle = p.radians((minutes * 6 + seconds * 0.1) - 90);
    const hourAngle = p.radians(((hours * 30) + (minutes * 0.5) + (seconds * (0.5/60))) - 90);

    // Hands
    // Hour hand
    p.stroke(0);
    p.strokeWeight(6);
    p.line(0, 0, radius * 0.55 * Math.cos(hourAngle), radius * 0.55 * Math.sin(hourAngle));

    // Minute hand
    p.strokeWeight(4);
    p.line(0, 0, radius * 0.78 * Math.cos(minAngle), radius * 0.78 * Math.sin(minAngle));

    // Second hand
    p.stroke(220, 0, 0);
    p.strokeWeight(2);
    p.line(0, 0, radius * 0.85 * Math.cos(secAngle), radius * 0.85 * Math.sin(secAngle));

    // Center pin
    p.noStroke();
    p.fill(0);
    p.circle(0, 0, 10);

    p.pop();
  }

  let localClockSketch = null;
  let solarClockSketch = null;

  // Local clock p5 instance
  const localSketchFactory = (p) => {
    let cnv;
    p.setup = function() {
