      cnv.parent('local-clock-wrapper');
      cnv.id('local-clock-canvas');
      p.noLoop(); // We'll manually redraw every second
    };
    p.draw = function() {
      p.clear();
      drawClock(p, localTime, '#E6E6FA'); // Lavender
    };
  };

  // Solar clock p5 instance
  const solarSketchFactory = (p) => {
    let cnv;
    p.setup = function() {
      cnv = p.createCanvas(CLOCK_SIZE, CLOCK_SIZE);
      cnv.parent('solar-clock-wrapper');
      cnv.id('solar-clock-canvas');
      p.noLoop();
    };
    p.draw = function() {
      p.clear();
      drawClock(p, solarTime, '#FAFAD2'); // LightGoldenRodYellow
    };
  };

  // Create instances
  localClockSketch = new p5(localSketchFactory);
  solarClockSketch = new p5(solarSketchFactory);

  // Start the continuous update loop
  computeAndUpdate(); // initial
  setInterval(computeAndUpdate, 1000);
})();
</script>
