<body>

<!-- Main container for the demo -->
<div id="demo-container">
    <!-- Control panel section -->
    <div id="control-panel">
        <div>
            <label for="input-steps">steps</label>
            <input type="number" id="input-steps" min="0" max="6" step="1" value="2">
        </div>
        <div>
            <label for="checkbox-zoom">zoom</label>
            <input type="checkbox" id="checkbox-zoom">
        </div>
        <div>
            <label for="select-color">color</label>
            <select id="select-color">
                <option value="DarkRainbow" selected>DarkRainbow</option>
                <option value="SiennaTones">SiennaTones</option>
                <option value="RustTones">RustTones</option>
                <option value="FuchsiaTones">FuchsiaTones</option>
                <option value="Grays">Grays</option>
            </select>
        </div>
    </div>
    <!-- Visualization canvas container -->
    <div id="canvas-container"></div>
    <div class="caption">Recursive rhombus subdivision into 7 parts (scale 1/√7). Toggle Zoom to see the repeating tiling.</div>
</div>

<script>
/*
    Gosper-like rhombic tiling demo
    - Recursively subdivides a rhombus into 7 children
    - Three base orientations (0°, 120°, 240°) used for coloring
    - p5.js rendering; static redraw on control changes
*/

let sqrt7 = Math.sqrt(7);
let thetaDeg = Math.atan(Math.sqrt(3) / 5) * 180 / Math.PI; // ≈ 19.1066°
let baseRhombusSize = 100; // world-units
let baseHexRadiusCache = null; // computed from initial 6-rhombi configuration
let canvas;

// UI elements
let stepsInput, zoomCheckbox, colorSelect;

// Palettes
const palettes = {
    DarkRainbow: ['#1E4363', '#2E6341'],                 // dark blue, dark green (alternate)
    SiennaTones: ['#A0522D', '#CD853F', '#D2B48C'],      // sienna, peru, tan
    RustTones: ['#B7410E', '#000033', '#FFFFFF'],        // rust, very dark blue, white (center hex)
    FuchsiaTones: ['#C71585', '#000000', '#FFFFFF'],     // fuchsia, black, white (center hex)
    Grays: ['#404040', '#808080', '#C0C0C0']             // dark gray, gray, light gray
};

// p5.js core
function setup() {
    const container = document.getElementById('canvas-container');
    const w = Math.max(640, container.clientWidth || window.innerWidth - 24);
    const h = Math.max(420, Math.round(w * 0.56));
    canvas = createCanvas(w, h);
    canvas.parent('canvas-container');
    angleMode(DEGREES);
    noLoop();

    stepsInput = document.getElementById('input-steps');
    zoomCheckbox = document.getElementById('checkbox-zoom');
    colorSelect = document.getElementById('select-color');

    // Enforce attributes per spec
    stepsInput.min = 0;
    stepsInput.max = 6;
    stepsInput.step = 1;
    if (!stepsInput.value) stepsInput.value = 2;

    // Events -> redraw
    stepsInput.addEventListener('input', () => requestRedraw());
    stepsInput.addEventListener('change', () => requestRedraw());
    zoomCheckbox.addEventListener('change', () => requestRedraw());
    colorSelect.addEventListener('change', () => requestRedraw());

    // First render
    requestRedraw();
}

function windowResized() {
    const container = document.getElementById('canvas-container');
    const w = Math.max(640, container.clientWidth || window.innerWidth - 24);
    const h = Math.max(420, Math.round(w * 0.56));
    resizeCanvas(w, h);
    requestRedraw();
}

function requestRedraw() {
    // Defer a tad to coalesce multiple changes
    setTimeout(() => redraw(), 0);
}

function draw() {
    background(255);

    // Read UI state
    const steps = clampInt(parseInt(stepsInput.value, 10), 0, 6);
    const zoomed = zoomCheckbox.checked;
    const paletteName = colorSelect.value || 'DarkRainbow';
    const palette = palettes[paletteName] || palettes.DarkRainbow;

    // Coordinate transform: center + scale
    translate(width / 2, height / 2);

    // Compute base fit scale using the static (steps=0) configuration
    if (!baseHexRadiusCache) {
        baseHexRadiusCache = computeInitialHexRadius(baseRhombusSize);
    }
    const R0 = baseHexRadiusCache; // world radius for steps=0 initial arrangement
    // Keep a margin factor so the full shape stays visible
    const margin = 0.92;
    const baseFitScale = margin * Math.min(width, height) / (2 * R0);

    // Zoom rule:
    // - default (unchecked): show the whole fractal at a fixed scale
    // - zoomed (checked): magnify by (sqrt(7))^steps to focus on the repeating center
    const scaleFactor = zoomed ? baseFitScale * Math.pow(sqrt7, steps) : baseFitScale;

    scale(scaleFactor);

    // Draw the recursive tiling starting from the 6-rhombi central hexagon
    // Two of each of the three base orientations
    const startAngles = [0, 60, 120, 180, 240, 300];
    for (let i = 0; i < startAngles.length; i++) {
        drawTiling(steps, 0, 0, baseRhombusSize, startAngles[i], palette);
    }

    // Optional central hexagon overlay (for "RustTones" and "FuchsiaTones")
    // The plan: For these palettes, the third color (#FFFFFF) is used for the central hexagon
    if (steps > 0 && (paletteName === 'RustTones' || paletteName === 'FuchsiaTones')) {
        const hexColor = palette[2] || '#FFFFFF';
        drawCentralHexagon(baseRhombusSize * 0.78, hexColor);
    }
}

// Utility: Clamp integer
function clampInt(v, lo, hi) {
    if (isNaN(v)) return lo;
    return Math.max(lo, Math.min(hi, v | 0));
}

// Compute the radius (max distance from center) of the initial 6-rhombi arrangement
function computeInitialHexRadius(size) {
    let maxR = 0;
    const angles = [0, 60, 120, 180, 240, 300];
    for (let a of angles) {
        const verts = rhombusVertices(0, 0, size, a);
        for (let p of verts) {
            const r = Math.sqrt(p.x * p.x + p.y * p.y);
            if (r > maxR) maxR = r;
        }
    }
    // Add a tiny buffer
    return maxR * 1.02;
}

// Draw a regular hexagon centered at origin (world units)
function drawCentralHexagon(radius, fillHex) {
    push();
    noStroke();
    fill(fillHex || '#FFFFFF');
    beginShape();
    for (let i = 0; i < 6; i++) {
        const ang = 60 * i - 30; // flat-topped
        vertex(radius * cos(ang), radius * sin(ang));
    }
    endShape(CLOSE);
    pop();
}

// Recursive tiler: subdivide a rhombus into 7 smaller ones (scale 1/sqrt(7))
function drawTiling(level, cx, cy, size, angle, palette) {
    if (level <= 0) {
        // Leaf: draw a single rhombus with color by orientation class
        const colorIndex = orientationClass(angle) % palette.length;
        const fillCol = palette[colorIndex];
        drawRhombus(cx, cy, size, angle, fillCol);
        return;
    }

    // Subdivision constants
    const sc = 1 / sqrt7;             // child size factor
    const childSize = size * sc;
    const d = computeSubdivisionOffsets(size, angle); // array of child positions + angle adjustments

    // Center child (keeps parent angle)
    drawTiling(level - 1, cx, cy, childSize, angle, palette);

    // Six surrounding children (in hex directions)
    // Use prescribed orientation tweak by ±thetaDeg for a Gosper-like motif
    for (let i = 0; i < d.length; i++) {
        const child = d[i];
        const cang = angle + child.dAngle;
        drawTiling(level - 1, cx + child.dx, cy + child.dy, childSize, cang, palette);
    }
}

// Define 6 neighbors around the parent rhombus center
// We use three hex-grid directions based on the parent angle:
// u = <angle>, v = <angle+60>, w = v - u (normalized)
// Children go in directions: [u, v, w, -u, -v, -w]
// Each child gets a small orientation twist of ±thetaDeg
function computeSubdivisionOffsets(size, angle) {
    const u = unitVec(angle);
    const v = unitVec(angle + 60);
    const wVec = normalize({ x: v.x - u.x, y: v.y - u.y });

    const dirs = [
        normalize(u),
        normalize(v),
        wVec,
        { x: -normalize(u).x, y: -normalize(u).y },
        { x: -normalize(v).x, y: -normalize(v).y },
        { x: -wVec.x, y: -wVec.y }
    ];

    // Offset distance calibrated to create a compact, area-preserving cluster
    // Using 0.72 * size keeps children within a parent-scaled footprint (heuristic)
    const R = 0.72 * size;

    // Alternate twist pattern to mimic Gosper's subtle swirl
    // [+theta, -theta, +theta, +theta, -theta, -theta]
    const twists = [ +thetaDeg, -thetaDeg, +thetaDeg, +thetaDeg, -thetaDeg, -thetaDeg ];

    const children = [];
    for (let i = 0; i < 6; i++) {
        children.push({
            dx: R * dirs[i].x,
            dy: R * dirs[i].y,
            dAngle: twists[i]
        });
    }
    return children;
}

// Draw a rhombus (60°-120° diamond) given center, size, and orientation angle
function drawRhombus(cx, cy, size, angle, fillCol) {
    const verts = rhombusVertices(cx, cy, size, angle);

    // Pixel-consistent strokes (account for current global scale)
    // Estimate current scale from transform: we passed a known scale, but p5 doesn't expose it.
    // We'll infer from canvas width vs. world width of initial setup. We cached base scale for strokes:
    // Instead, we approximate with an empirical factor relative to size.
    // Better: pass stroke weight as inverse of the initial scaling used in draw().
    // We'll approximate using a small stroke that works well visually at both scales.
    const outlineAlpha = 42; // subtle outline
    const diagAlpha = 70;    // faint diagonal line

    push();
    noStroke();
    fill(fillCol || '#888');
    beginShape();
    for (let p of verts) vertex(p.x, p.y);
    endShape(CLOSE);
    pop();

    // Subtle outline
    push();
    stroke(0, outlineAlpha);
    strokeWeight(0.7);
    noFill();
    beginShape();
    for (let p of verts) vertex(p.x, p.y);
    endShape(CLOSE);
    pop();

    // Faint diagonal across obtuse vertices: connect v1 <-> v3 (indices 0 and 2 in our ordering)
    push();
    stroke(255, diagAlpha);
    strokeWeight(0.9);
    line(verts[0].x, verts[0].y, verts[2].x, verts[2].y);
    pop();
}

// Return 4 vertices of the 60°-120° rhombus in consistent order
// Constructed from two unit directions separated by 60°
function rhombusVertices(cx, cy, size, angle) {
    const v1 = unitVec(angle);
    const v2 = unitVec(angle + 60);
    const p1 = { x: cx + size * v1.x, y: cy + size * v1.y };
    const p2 = { x: cx + size * v2.x, y: cy + size * v2.y };
    const p3 = { x: cx - size * v1.x, y: cy - size * v1.y };
    const p4 = { x: cx - size * v2.x, y: cy - size * v2.y };
    return [p1, p2, p3, p4];
}

// Compute unit vector from angle (degrees)
function unitVec(a) {
    return { x: cos(a), y: sin(a) };
}

// Normalize vector
function normalize(v) {
    const L = Math.hypot(v.x, v.y) || 1;
    return { x: v.x / L, y: v.y / L };
}

// Map any angle to one of the three base orientation classes: 0°, 120°, 240°
function orientationClass(a) {
    // Normalize to 0..360
    let ang = a % 360;
    if (ang < 0) ang += 360;
    const baseAngles = [0, 120, 240];
    let best = 0;
    let bestDiff = 1e9;
    for (let i = 0; i < baseAngles.length; i++) {
        let d = Math.abs(shortestAngleDiff(ang, baseAngles[i]));
        if (d < bestDiff) {
            bestDiff = d;
            best = i;
        }
    }
    return best;
}

// Smallest signed angle difference (degrees)
function shortestAngleDiff(a, b) {
    let d = (a - b + 540) % 360 - 180;
    return d;
}
</script>
