    ];
    let magnetOrientations = [3, 2, 4, 5, 0]; // [↓, ↑, ⊙, ⊗, →]
    let magnetStrength = 5;
    let plotType = 'stream'; // default

    // Helpers to update UI
    function updateOrientationDisplay(i) {
      const span = document.getElementById('magnet-orientation-display-' + i);
      span.textContent = orientationSymbols[magnetOrientations[i]];
    }
    function setActivePlotButton() {
