        document.getElementById('magnet-orientation-up-' + i).addEventListener('click', () => {
          magnetOrientations[i] = (magnetOrientations[i] + 1) % 6;
          updateOrientationDisplay(i);
          requestRedraw();
        });
        document.getElementById('magnet-orientation-down-' + i).addEventListener('click', () => {
          magnetOrientations[i] = (magnetOrientations[i] + 5) % 6; // decrement with wrap
          updateOrientationDisplay(i);
          requestRedraw();
        });
      }

      // Strength slider
      const slider = document.getElementById('slider-strength');
      const valSpan = document.getElementById('slider-strength-value');
      slider.value = String(magnetStrength);
      valSpan.textContent = String(magnetStrength);
      slider.addEventListener('input', (e) => {
        magnetStrength = parseInt(e.target.value, 10);
        valSpan.textContent = String(magnetStrength);
        requestRedraw();
      });

      // Plot type buttons
      document.getElementById('btn-stream-plot').addEventListener('click', () => {
        plotType = 'stream';
        setActivePlotButton();
        requestRedraw();
      });
      document.getElementById('btn-density-plot').addEventListener('click', () => {
        plotType = 'density';
        setActivePlotButton();
        requestRedraw();
