    }

    function requestRedraw() {
      if (p5instance) p5instance.redraw();
    }

    // p5.js sketch in instance mode
    function makeSketch(p) {
      // Layout and world scale
      let pxPerCm = 50;
      let worldHalfWidth = 8;  // cm
      let worldHalfHeight = 5; // cm
      const L = 1; // cm spacing/width of magnets (front face width)
      p.pixelDensity(1);

      p.setup = function() {
        const container = document.getElementById('visualization-container');
        const w = container.clientWidth;
        const h = Math.max(460, Math.round(w * 0.56));
        const cnv = p.createCanvas(w, h);
        cnv.id('p5-canvas');
        cnv.parent('visualization-container');
        p.noLoop();
        p.textFont('Segoe UI, Arial, sans-serif');
        computeScale();
      };

      function computeScale() {
        const w = p.width, h = p.height;
        // Fit world into canvas while preserving aspect
        pxPerCm = Math.min(w / (2 * worldHalfWidth), h / (2 * worldHalfHeight));
      }

      p.windowResized = function() {
        const container = document.getElementById('visualization-container');
        const w = container.clientWidth;
        const h = Math.max(460, Math.round(w * 0.56));
        p.resizeCanvas(w, h);
        computeScale();
        p.redraw();
      };

      // Magnetic field of five dipoles (3D) sampled in z=0 plane
      function calculateField(x, y) {
        let Bx = 0, By = 0, Bz = 0;
        const soft = 0.15; // cm softening to avoid singularity
        for (let i = 0; i < 5; i++) {
          const mo = orientationVectors[magnetOrientations[i]];
          const mx = mo[0] * magnetStrength;
          const my = mo[1] * magnetStrength;
          const mz = mo[2] * magnetStrength;
          const cx = (i - 2) * L;
          const cy = 0;
          const cz = 0;

          const rx = x - cx;
          const ry = y - cy;
          const rz = 0 - cz;
          const r2 = rx * rx + ry * ry + rz * rz + soft * soft;
          const r = Math.sqrt(r2);
          const invR3 = 1 / (r * r * r);
          const invR5 = 1 / (r2 * r * r); // 1 / r^5
          const mDotR = mx * rx + my * ry + mz * rz;

          // B = 3(m·r)r/r^5 - m/r^3
          Bx += 3 * mDotR * rx * invR5 - mx * invR3;
          By += 3 * mDotR * ry * invR5 - my * invR3;
          Bz += 3 * mDotR * rz * invR5 - mz * invR3;
        }
        const Bmag = Math.sqrt(Bx * Bx + By * By + Bz * Bz);
        return { Bx, By, Bz, Bmag };
      }

      function worldToScreen(x, y) {
        return {
          x: p.width / 2 + x * pxPerCm,
          y: p.height / 2 - y * pxPerCm
        };
      }

      function isInsideMagnetRegion(x, y) {
        // Avoid seed points too close to magnet boxes
        if (Math.abs(y) < 0.7) {
          for (let i = 0; i < 5; i++) {
            const cx = (i - 2) * L;
            if (Math.abs(x - cx) < 0.7) return true;
          }
        }
        return false;
      }
      function isInsideMagnetCore(x, y) {
        // Stop streamlines if we enter front cube area
        if (Math.abs(y) < 0.55) {
          for (let i = 0; i < 5; i++) {
            const cx = (i - 2) * L;
            if (Math.abs(x - cx) < 0.55) return true;
          }
        }
        return false;
      }

      function drawStream() {
        const w = p.width, h = p.height;
        const maxSteps = 140;
        const step = 0.12; // cm per integration step
        const xmin = -worldHalfWidth, xmax = worldHalfWidth;
        const ymin = -worldHalfHeight, ymax = worldHalfHeight;

        p.push();
        p.translate(w / 2, h / 2);
        p.scale(pxPerCm, -pxPerCm);
        p.noFill();
        p.stroke(20, 90, 200);
        p.strokeWeight(1 / pxPerCm);

        const sxStep = 1.3;
        const syStep = 1.0;
        for (let sy = ymin; sy <= ymax; sy += syStep) {
          for (let sx = xmin; sx <= xmax; sx += sxStep) {
            if (isInsideMagnetRegion(sx, sy)) continue;
            traceStream(sx, sy, +1);
            traceStream(sx, sy, -1);
          }
        }
        p.pop();

        function traceStream(x0, y0, dir) {
          let x = x0, y = y0;
          let seg = 0;
          for (let s = 0; s < maxSteps; s++) {
            const f = calculateField(x, y);
            const vxy = Math.hypot(f.Bx, f.By);
            if (vxy < 1e-3) break;
            const vx = dir * f.Bx / vxy;
            const vy = dir * f.By / vxy;
            const nx = x + vx * step;
            const ny = y + vy * step;

            if (nx < xmin || nx > xmax || ny < ymin || ny > ymax) break;
            if (isInsideMagnetCore(nx, ny)) break;

            p.line(x, y, nx, ny);
            seg++;
            if (seg % 14 === 0) {
              drawArrow(nx, ny, Math.atan2(vy, vx));
            }
            x = nx; y = ny;
          }
        }

        function drawArrow(x, y, angle) {
          const ah = 0.25; // arrowhead length (cm)
          const aw = 0.12; // arrowhead width  (cm)
          p.push();
          p.translate(x, y);
          p.rotate(angle);
          p.fill(20, 90, 200);
          p.noStroke();
          p.beginShape();
          p.vertex(0, 0);
          p.vertex(-ah, aw);
          p.vertex(-ah, -aw);
          p.endShape(p.CLOSE);
          p.pop();
