      }

      function drawDensity() {
        const w = p.width, h = p.height;
        // Choose a sampling step that scales with canvas size (pixels)
        const step = Math.max(2, Math.floor(Math.min(w, h) / 160));

        // First pass: find min and max field magnitudes
        let minB = Infinity, maxB = -Infinity;
        for (let py = 0; py < h; py += step) {
          for (let px = 0; px < w; px += step) {
            const x = (px - w / 2) / pxPerCm;
            const y = (h / 2 - py) / pxPerCm;
            const f = calculateField(x, y);
            const m = f.Bmag;
            if (m < minB) minB = m;
            if (m > maxB) maxB = m;
          }
        }

        // Second pass: draw rectangles colored by normalized magnitude
        for (let py = 0; py < h; py += step) {
          for (let px = 0; px < w; px += step) {
            const x = (px - w / 2) / pxPerCm;
            const y = (h / 2 - py) / pxPerCm;
            const f = calculateField(x, y);
            let t = 0;
            if (maxB > minB) t = (f.Bmag - minB) / (maxB - minB);
            if (t < 0) t = 0; else if (t > 1) t = 1;

            const c = colormap(t);
            p.noStroke();
            p.fill(c);
            p.rect(px, py, step + 1, step + 1);
          }
        }

        function colormap(t) {
          // 0.0 -> light yellow/cream, mid -> orange/red, 1.0 -> dark blue
          const c0 = p.color(255, 246, 204); // cream
          const c1 = p.color(255, 106,   0); // orange/red
          const c2 = p.color( 11,  46, 107); // dark blue
          if (t < 0.5) {
            return p.lerpColor(c0, c1, t * 2);
          } else {
            return p.lerpColor(c1, c2, (t - 0.5) * 2);
          }
        }
      }

      function drawMagnets() {
        const w = p.width, h = p.height;
        // Draw cubes (pseudo-3D) in world space
        p.push();
        p.translate(w / 2, h / 2);
        p.scale(pxPerCm, -pxPerCm);

        const half = 0.5; // front face half-width (cm)
        const offsetX = 0.25, offsetY = 0.20; // "depth" offset for top/side faces
        for (let i = 0; i < 5; i++) {
          const cx = (i - 2) * 1;
          const x0 = cx - half, x1 = cx + half, y0 = -half, y1 = half;

          // Front face
          p.stroke(0);
          p.fill(210);
          p.rectMode(p.CORNERS);
          p.rect(x0, y0, x1, y1);

          // Top face
          p.fill(200);
          p.quad(
            x0, y0,
            x1, y0,
            x1 - offsetX, y0 + offsetY,
            x0 - offsetX, y0 + offsetY
          );

          // Right side face
          p.fill(190);
          p.quad(
            x1, y0,
            x1, y1,
            x1 - offsetX, y1 + offsetY,
            x1 - offsetX, y0 + offsetY
          );
        }
        p.pop();

        // Draw orientation symbols centered on front faces (in screen space for crisp text)
        for (let i = 0; i < 5; i++) {
          const cx = (i - 2) * 1;
          const center = worldToScreen(cx, 0);
          p.push();
          p.fill(20, 90, 200);
          p.noStroke();
          p.textAlign(p.CENTER, p.CENTER);
          p.textSize(0.9 * pxPerCm);
          p.text(orientationSymbols[magnetOrientations[i]], center.x, center.y + 0.05 * pxPerCm);
          p.pop();
        }
      }

      p.draw = function () {
        p.clear();
        p.background(255);
        if (plotType === 'density') {
          drawDensity();
        } else {
          drawStream();
        }
        drawMagnets();
      };
    }

    // Boot
    window.addEventListener('DOMContentLoaded', () => {
      setupControls();
      p5instance = new p5(makeSketch);
    });
  </script>
</body>
</html>
