<script>
/* ==========================
   Global state and constants
   ========================== */
let currentView = 'diagram'; // 'diagram' or 'phases'

let showGridLines = false;
let showTieLines = true;
let showAlphaPhase = true;
let showBetaPhase = true;

// User-selected composition point (barycentric)
let userPoint = { xA: 0.42, xB: 0.47, xC: 0.11 };

// Geometry: triangle vertices in pixels (set in setup/resize)
let P_A, P_B, P_C, triSide, triHeight, triBaseY, triCenter;

// Phase envelope data (barycentric arrays)
let alphaCurvePoints = []; // [{xA,xB,xC}, ...] orange segment
let betaCurvePoints  = []; // [{xA,xB,xC}, ...] magenta segment
let envelopePolygonPixels = []; // cached polygon in pixels

// Tie lines data (array of {alpha: {..}, beta: {..}})
let tieLines = [];

// Derived: average tie direction in pixels
let avgTieDir = null;

// p5 canvas reference and container
let cnv;
const container = document.getElementById('canvas-container');

// Info boxes
const infoMain  = document.getElementById('info-box-main');
const infoAlpha = document.getElementById('info-box-alpha');
const infoBeta  = document.getElementById('info-box-beta');

// Controls
const btnDiagram = document.getElementById('btn-diagram');
const btnPhases  = document.getElementById('btn-phases');
const chkGrid    = document.getElementById('check-grid-lines');
const chkTies    = document.getElementById('check-tie-lines');
const chkAlpha   = document.getElementById('check-alpha-phase');
const chkBeta    = document.getElementById('check-beta-phase');
const diagramCheckboxes = document.getElementById('diagram-checkboxes');

/* ==========================
   Utility: numeric helpers
   ========================== */
function clamp(v, a, b){ return Math.max(a, Math.min(b, v)); }
function fmt(x){ return (Math.round(x*100)/100).toFixed(2); }
function baryNormalize(xA, xB, xC){
  const s = xA + xB + xC;
  if (s === 0) return {xA: 1, xB: 0, xC: 0};
  return { xA: xA/s, xB: xB/s, xC: xC/s };
}

/* ===================================
   Ternary coordinate conversions
   =================================== */
function baryToCart(b){
  // P = xA*P_A + xB*P_B + xC*P_C
  const x = b.xA*P_A.x + b.xB*P_B.x + b.xC*P_C.x;
  const y = b.xA*P_A.y + b.xB*P_B.y + b.xC*P_C.y;
  return {x,y};
}
function cartToBary(pt){
  // Solve P = P_A + u*(P_B-P_A) + v*(P_C-P_A)
  const v0 = {x: P_B.x - P_A.x, y: P_B.y - P_A.y};
  const v1 = {x: P_C.x - P_A.x, y: P_C.y - P_A.y};
  const v2 = {x: pt.x - P_A.x,  y: pt.y - P_A.y };
  const den = v0.x*v1.y - v1.x*v0.y;
  if (Math.abs(den) < 1e-9){
    return {xA: -1, xB: -1, xC: -1}; // degenerate
  }
  const u = (v2.x*v1.y - v1.x*v2.y) / den;
  const v = (v0.x*v2.y - v2.x*v0.y) / den;
  const xB = u;
  const xC = v;
  const xA = 1 - u - v;
  return {xA, xB, xC};
}
function isInsideTriangleBary(b){
  return b.xA >= 0 && b.xB >= 0 && b.xC >= 0 && Math.abs(b.xA + b.xB + b.xC - 1) < 1e-6;
}

/* ===================================
   Envelope data generation (hardcoded via deterministic procedure)
   =================================== */
function generatePhaseCurves(){
  // Left common endpoint (near B corner) and right common endpoint (plait point)
  const L = { xA: 0.15, xB: 0.78, xC: 0.07 };
  const P = { xA: 0.28, xB: 0.36, xC: 0.36 }; // plait point

  const N = 20;
  alphaCurvePoints = [];
  betaCurvePoints = [];

  // Alpha curve from L -> P (lower boundary, slightly lower xA along the path)
  for (let i=0;i<=N;i++){
    const t = i/N;
    // Smooth interpolation with small sinusoidal shape to avoid straight line
    const s = Math.sin(Math.PI*t);
    const xB = L.xB*(1-t) + P.xB*t + 0.03*s*(1-t);
    const xC = L.xC*(1-t) + P.xC*t + 0.01*s*(1-t);
    let xA = 1 - xB - xC;
    // Clamp to [0,1] and normalize
    const b = baryNormalize(xA, xB, xC);
    alphaCurvePoints.push(b);
  }

  // Beta curve from L -> P (upper boundary, slightly higher xA along the path)
  for (let i=0;i<=N;i++){
    const t = i/N;
    const s = Math.sin(Math.PI*t);
    const xB = L.xB*(1-t) + P.xB*t + 0.01*s*(1-t);
    const xC = L.xC*(1-t) + P.xC*t + 0.03*s*(1-t);
    let xA = 1 - xB - xC;
    const b = baryNormalize(xA, xB, xC);
    betaCurvePoints.push(b);
  }

  // Tie lines: pair points at same parameter along the two limbs (N_t sparse)
  tieLines = [];
  const M = 9;
  for (let j=0;j<=M;j++){
    const t = j/M;
    const i = Math.round(t*N);
    tieLines.push({
      alpha: alphaCurvePoints[i],
      beta:  betaCurvePoints[i]
    });
  }

  // Cache polygon in pixels: alpha(L->P) then reversed beta(P->L) to make a closed loop
  envelopePolygonPixels = [];
  for (let i=0;i<alphaCurvePoints.length;i++){
    envelopePolygonPixels.push(baryToCart(alphaCurvePoints[i]));
  }
  for (let i=betaCurvePoints.length-1;i>=0;i--){
    envelopePolygonPixels.push(baryToCart(betaCurvePoints[i]));
  }

  // Average tie direction in pixels
  let dx=0, dy=0, cnt=0;
  for (const tl of tieLines){
    const pa = baryToCart(tl.alpha);
    const pb = baryToCart(tl.beta);
    dx += (pb.x - pa.x);
    dy += (pb.y - pa.y);
    cnt++;
  }
  if (cnt>0){
    const Ld = Math.hypot(dx,dy) || 1;
    avgTieDir = {x: dx/Ld, y: dy/Ld};
  } else {
    avgTieDir = {x: 1, y: 0};
  }
}

/* ===================================
   Geometry helpers
   =================================== */
function pointInPolygon(pt, poly){
  // Even-odd rule (ray casting)
  let inside = false;
  for (let i=0, j=poly.length-1; i<poly.length; j=i++){
    const xi = poly[i].x, yi = poly[i].y;
    const xj = poly[j].x, yj = poly[j].y;
    const intersect = ((yi>pt.y)!==(yj>pt.y)) &&
                      (pt.x < (xj - xi)*(pt.y - yi)/(yj - yi + 1e-12) + xi);
    if (intersect) inside = !inside;
  }
  return inside;
}
function lineSegIntersection(p, r, a, b){
  // Intersect line p + t r with segment a + u (b - a)
  const s = {x: b.x - a.x, y: b.y - a.y};
  const rxs = r.x*s.y - r.y*s.x;
  const qp = {x: a.x - p.x, y: a.y - p.y};
  if (Math.abs(rxs) < 1e-9) return null; // parallel
  const t = (qp.x*s.y - qp.y*s.x) / rxs;
  const u = (qp.x*r.y - qp.y*r.x) / rxs;
  if (u < -1e-9 || u > 1+1e-9) return null;
  return { x: p.x + t*r.x, y: p.y + t*r.y, t, u };
}
function intersectPolylineWithLine(lineP, lineDir, polyPoints){
  // Return the intersection closest in |t| to lineP
  let best=null;
  for (let i=0;i<polyPoints.length-1;i++){
    const a = polyPoints[i], b = polyPoints[i+1];
    const hit = lineSegIntersection(lineP, lineDir, a, b);
    if (hit){
      if (!best || Math.abs(hit.t) < Math.abs(best.t)) best = hit;
    }
  }
  return best;
}

/* ===================================
   Projections to triangle sides (for xA,xB,xC)
   =================================== */
function projectionPoint(b, whichZero){
  // whichZero: 'A' -> project to BC (xA=0),
  //            'B' -> project to AC (xB=0),
  //            'C' -> project to AB (xC=0)
  let p;
  if (whichZero === 'A'){
    const denom = b.xB + b.xC;
    if (denom <= 1e-9) return baryToCart({xA:0,xB:0.5,xC:0.5});
    p = baryToCart({ xA: 0, xB: b.xB/denom, xC: b.xC/denom });
  } else if (whichZero === 'B'){
    const denom = b.xA + b.xC;
    if (denom <= 1e-9) return baryToCart({xA:0.5,xB:0,xC:0.5});
    p = baryToCart({ xA: b.xA/denom, xB: 0, xC: b.xC/denom });
  } else {
    const denom = b.xA + b.xB;
    if (denom <= 1e-9) return baryToCart({xA:0.5,xB:0.5,xC:0});
    p = baryToCart({ xA: b.xA/denom, xB: b.xB/denom, xC: 0 });
  }
  return p;
}

/* ===================================
   Phase endpoints for a user point
   =================================== */
function findPhaseEndpointsForPoint(bary){
  // Only meaningful if inside the envelope polygon
  const Pp = baryToCart(bary);
  const dir = avgTieDir || {x:1,y:0};
  // Intersect with alpha and beta polylines (in pixels)
  const alphaPix = alphaCurvePoints.map(baryToCart);
  const betaPix  = betaCurvePoints.map(baryToCart);
  const hitA = intersectPolylineWithLine(Pp, dir, alphaPix);
  const hitB = intersectPolylineWithLine(Pp, dir, betaPix);
  if (!hitA || !hitB) {
    // As fallback: choose closest predefined tie line (by distance to its infinite line)
    let best = null, bestDist = Infinity;
    for (const tl of tieLines){
      const a = baryToCart(tl.alpha);
      const c = baryToCart(tl.beta);
      const ab = {x: c.x - a.x, y: c.y - a.y};
      const ap = {x: Pp.x - a.x, y: Pp.y - a.y};
      const area2 = Math.abs(ab.x*ap.y - ab.y*ap.x);
      const len = Math.hypot(ab.x, ab.y) || 1;
      const dist = area2 / len;
      if (dist < bestDist){ bestDist = dist; best = tl; }
    }
    if (!best) return null;
    return { alpha: best.alpha, beta: best.beta };
  }
  // Convert back to barycentric
  const bA = cartToBary({x: hitA.x, y: hitA.y});
  const bB = cartToBary({x: hitB.x, y: hitB.y});
  return { alpha: baryNormalize(bA.xA,bA.xB,bA.xC), beta: baryNormalize(bB.xA,bB.xB,bB.xC) };
}

/* ===================================
   Info boxes updating
   =================================== */
function updateInfoBoxes(){
  // Main composition box
  const m = userPoint;
  infoMain.innerHTML = `
    <div class="title">mass fractions</div>
    <div>\\(x_A = ${fmt(m.xA)}\\)</div>
    <div>\\(x_B = ${fmt(m.xB)}\\)</div>
    <div>\\(x_C = ${fmt(m.xC)}\\)</div>
  `;

  // Determine if inside two-phase region
  const inTwoPhase = pointInPolygon(baryToCart(userPoint), envelopePolygonPixels);

  if (currentView === 'diagram'){
    infoMain.classList.remove('hidden');

    if (inTwoPhase){
      const endpoints = findPhaseEndpointsForPoint(userPoint);
      if (endpoints){
        const a = endpoints.alpha, b = endpoints.beta;

        if (showAlphaPhase){
          infoAlpha.classList.remove('hidden');
          infoAlpha.innerHTML = `
            <div class="title">alpha phase</div>
            <div>mass fractions</div>
            <div>\\(x_A^{\\alpha} = ${fmt(a.xA)}\\)</div>
            <div>\\(x_B^{\\alpha} = ${fmt(a.xB)}\\)</div>
            <div>\\(x_C^{\\alpha} = ${fmt(a.xC)}\\)</div>
          `;
        } else {
          infoAlpha.classList.add('hidden');
        }

        if (showBetaPhase){
          infoBeta.classList.remove('hidden');
          infoBeta.innerHTML = `
            <div class="title">beta phase</div>
            <div>mass fractions</div>
            <div>\\(x_A^{\\beta} = ${fmt(b.xA)}\\)</div>
            <div>\\(x_B^{\\beta} = ${fmt(b.xB)}\\)</div>
            <div>\\(x_C^{\\beta} = ${fmt(b.xC)}\\)</div>
          `;
        } else {
          infoBeta.classList.add('hidden');
        }
      } else {
        infoAlpha.classList.add('hidden');
        infoBeta.classList.add('hidden');
      }
    } else {
      infoAlpha.classList.add('hidden');
      infoBeta.classList.add('hidden');
    }
  } else {
    // phases view: hide all info boxes
    infoMain.classList.add('hidden');
    infoAlpha.classList.add('hidden');
    infoBeta.classList.add('hidden');
  }

  // Typeset MathJax
  if (window.MathJax && MathJax.typeset) {
    MathJax.typeset();
  }
}

/* ===================================
   Controls events
   =================================== */
btnDiagram.addEventListener('click', () => {
  currentView = 'diagram';
  btnDiagram.classList.add('active');
  btnPhases.classList.remove('active');
  diagramCheckboxes.classList.remove('hidden');
  updateInfoBoxes();
  if (window.redraw) window.redraw();
});
btnPhases.addEventListener('click', () => {
  currentView = 'phases';
  btnPhases.classList.add('active');
  btnDiagram.classList.remove('active');
  diagramCheckboxes.classList.add('hidden');
  updateInfoBoxes();
  if (window.redraw) window.redraw();
});

chkGrid.addEventListener('change', () => { showGridLines = chkGrid.checked; if (window.redraw) window.redraw(); });
chkTies.addEventListener('change', () => { showTieLines = chkTies.checked; if (window.redraw) window.redraw(); });
chkAlpha.addEventListener('change', () => { showAlphaPhase = chkAlpha.checked; updateInfoBoxes(); if (window.redraw) window.redraw(); });
chkBeta.addEventListener('change', () => { showBetaPhase = chkBeta.checked; updateInfoBoxes(); if (window.redraw) window.redraw(); });

/* ===================================
   p5 sketch
   =================================== */
function setup(){
  const size = getCanvasSize();
  cnv = createCanvas(size.w, size.h);
  cnv.parent('canvas-container');
  pixelDensity(window.devicePixelRatio || 1);

  computeTriangle();
  generatePhaseCurves();
  updateInfoBoxes();
  noLoop();
  redraw();
}

function windowResized(){
  const size = getCanvasSize();
  resizeCanvas(size.w, size.h);
  computeTriangle();
  // Recompute polygon pixels cache based on new geometry
  envelopePolygonPixels = [];
  for (let i=0;i<alphaCurvePoints.length;i++){
    envelopePolygonPixels.push(baryToCart(alphaCurvePoints[i]));
  }
  for (let i=betaCurvePoints.length-1;i>=0;i--){
    envelopePolygonPixels.push(baryToCart(betaCurvePoints[i]));
  }
  redraw();
}

function getCanvasSize(){
  const r = container.getBoundingClientRect();
  const w = Math.max(400, Math.floor(r.width));
  const h = Math.max(420, Math.floor(r.height));
  return { w, h };
}

function computeTriangle(){
  const r = { w: width, h: height };
  const margin = Math.max(20, Math.min(r.w, r.h)*0.06);
  const s = Math.min(r.w, r.h) - 2*margin;
  triSide = Math.max(300, Math.min(700, s));
  triHeight = triSide * Math.sqrt(3)/2;
  triCenter = { x: r.w*0.5, y: r.h*0.52 };
  triBaseY = triCenter.y + triHeight/2;

  P_B = { x: triCenter.x - triSide/2, y: triBaseY };
  P_C = { x: triCenter.x + triSide/2, y: triBaseY };
  P_A = { x: triCenter.x, y: triBaseY - triHeight };
}

function mousePressed(){
  // Register clicks only if over the canvas area
  const mx = mouseX, my = mouseY;
  if (mx < 0 || my < 0 || mx > width || my > height) return;

  const bary = cartToBary({x: mx, y: my});
  if (isInsideTriangleBary(bary)){
    userPoint = baryNormalize(bary.xA, bary.xB, bary.xC);
    updateInfoBoxes();
    redraw();
  }
}

/* ===================================
   Drawing helpers
   =================================== */
function drawTriangleBase(){
  stroke(0);
  strokeWeight(2);
  fill(255);
  triangle(P_A.x, P_A.y, P_B.x, P_B.y, P_C.x, P_C.y);

  // Vertex labels
  noStroke();
  fill('#111');
  textAlign(CENTER, BOTTOM);
  textSize(14);
  fill(varGreen()); text('A', P_A.x, P_A.y - 8);
  fill(varBlue());  text('B', P_B.x - 12, P_B.y + 16);
  fill(varRed());   text('C', P_C.x + 12, P_C.y + 16);
}

function drawAxisTicksAndNumbers(){
  textSize(11);
  strokeWeight(1);
  // Sides vectors
  const AB = { x: P_B.x - P_A.x, y: P_B.y - P_A.y };
  const AC = { x: P_C.x - P_A.x, y: P_C.y - P_A.y };
  const BC = { x: P_C.x - P_B.x, y: P_C.y - P_B.y };

  // Helper to draw tick on a side at fraction t from first to second point
  function tickOnSide(P1, P2, color, label, flip, normalScale){
    const n = normalScale || 8;
    for (let i=1;i<=9;i++){
      const t = i/10;
      const x = P1.x*(1-t) + P2.x*t;
      const y = P1.y*(1-t) + P2.y*t;
      // Perpendicular small tick (pointing outward)
      const vx = P2.x - P1.x, vy = P2.y - P1.y;
      const L = Math.hypot(vx,vy) || 1;
      const nx =  (vy / L) * (flip?1:-1);
      const ny = (-vx / L) * (flip?1:-1);
      stroke(color); line(x, y, x + nx*n, y + ny*n);
      noStroke(); fill(color);
      const tx = x + nx*(n+6), ty = y + ny*(n+6);
      textAlign(CENTER, CENTER);
      text((i/10).toFixed(1), tx, ty);
    }
    // Label
    noStroke(); fill(color); textAlign(CENTER, CENTER);
    const midx = (P1.x + P2.x)/2, midy = (P1.y + P2.y)/2;
    text(label, midx, midy + (flip?14:-14));
  }

  // x_A ticks along side BC (opposite A), outward downward (+)
  tickOnSide(P_B, P_C, varGreen(), 'x_A', true, 7);
  // x_B ticks along side AC (opposite B)
  tickOnSide(P_A, P_C, varBlue(), 'x_B', false, 7);
  // x_C ticks along side AB (opposite C)
  tickOnSide(P_A, P_B, varRed(), 'x_C', true, 7);
}

function drawGridLines(){
  stroke(230); strokeWeight(1);
  // Lines of constant xA (parallel to BC)
  for (let i=1;i<=9;i++){
    const t = i/10;
    const p1 = baryToCart({xA:t, xB: 1-t, xC:0});
    const p2 = baryToCart({xA:t, xB: 0,   xC:1-t});
    line(p1.x, p1.y, p2.x, p2.y);
  }
  // Lines of constant xB (parallel to AC)
  for (let i=1;i<=9;i++){
    const t = i/10;
    const p1 = baryToCart({xA:1-t, xB:t, xC:0});
    const p2 = baryToCart({xA:0,   xB:t, xC:1-t});
    line(p1.x, p1.y, p2.x, p2.y);
  }
  // Lines of constant xC (parallel to AB)
  for (let i=1;i<=9;i++){
    const t = i/10;
    const p1 = baryToCart({xA:1-t, xB:0,   xC:t});
    const p2 = baryToCart({xA:0,   xB:1-t, xC:t});
    line(p1.x, p1.y, p2.x, p2.y);
  }
}

function drawPhaseEnvelope(){
  noFill();
  strokeWeight(2);

  // Alpha (orange)
  stroke(varOrange());
  beginShape();
  for (const b of alphaCurvePoints){
    const p = baryToCart(b);
    vertex(p.x, p.y);
  }
  endShape();

  // Beta (magenta)
  stroke(varMagenta());
  beginShape();
  for (const b of betaCurvePoints){
    const p = baryToCart(b);
    vertex(p.x, p.y);
  }
  endShape();
}

function drawTieLines(){
  stroke(150);
  strokeWeight(1);
  for (const tl of tieLines){
    const a = baryToCart(tl.alpha);
    const b = baryToCart(tl.beta);
    line(a.x, a.y, b.x, b.y);
  }
}

function drawArrow(p1, p2, color, dashed=false){
  stroke(color);
  strokeWeight(2);
  if (dashed) dashedLine(p1.x,p1.y,p2.x,p2.y, 7,5); else line(p1.x,p1.y,p2.x,p2.y);
  // Arrowhead at p2
  const angle = Math.atan2(p2.y - p1.y, p2.x - p1.x);
  const size = 6;
  push();
  translate(p2.x, p2.y);
  rotate(angle);
  noStroke();
  fill(color);
  triangle(-size, -size*0.75, -size, size*0.75, 0, 0);
  pop();
}
function dashedLine(x1,y1,x2,y2,dash=6,gap=4){
  const dx = x2-x1, dy=y2-y1;
  const len = Math.hypot(dx,dy);
  const steps = Math.floor(len/(dash+gap));
  const ux = dx/len, uy=dy/len;
  for (let i=0;i<steps;i++){
    const a = (dash+gap)*i;
    const b = a + dash;
    const sx = x1 + ux*a, sy = y1 + uy*a;
    const ex = x1 + ux*Math.min(b,len), ey = y1 + uy*Math.min(b,len);
    line(sx,sy,ex,ey);
  }
}

function varGreen(){ return '#008800'; }
function varBlue(){ return '#0000FF'; }
function varRed(){ return '#FF0000'; }
function varOrange(){ return '#FFA500'; }
function varMagenta(){ return '#FF00FF'; }

/* ===================================
   draw()
   =================================== */
function draw(){
  clear();
  background(255);

  // Triangle and baseline elements
  drawTriangleBase();
  drawAxisTicksAndNumbers();

  if (currentView === 'diagram'){
    if (showGridLines) drawGridLines();

    // Envelope outline
    drawPhaseEnvelope();

    // Tie lines (predefined)
    if (showTieLines) drawTieLines();

    // User point
    const Pu = baryToCart(userPoint);
    fill(0); noStroke();
    circle(Pu.x, Pu.y, 6);

    // Projection lines from user point to each side (colored)
    const pA = projectionPoint(userPoint, 'A'); // towards BC (xA=0)
    const pB = projectionPoint(userPoint, 'B'); // towards AC (xB=0)
    const pC = projectionPoint(userPoint, 'C'); // towards AB (xC=0)
    drawArrow(Pu, pA, varGreen());
    drawArrow(Pu, pB, varBlue());
    drawArrow(Pu, pC, varRed());

    // Check if inside two-phase
    const insideTwoPhase = pointInPolygon(Pu, envelopePolygonPixels);

    if (insideTwoPhase){
      const endpoints = findPhaseEndpointsForPoint(userPoint);
      if (endpoints){
        const Pa = baryToCart(endpoints.alpha);
        const Pb = baryToCart(endpoints.beta);

        // Connect alpha - user - beta (dashed black)
        stroke(0); strokeWeight(1.5);
        dashedLine(Pa.x,Pa.y, Pu.x,Pu.y, 6,4);
        dashedLine(Pu.x,Pu.y, Pb.x,Pb.y, 6,4);

        if (showAlphaPhase){
          // Alpha dot and dashed projections
          noStroke(); fill(varOrange());
          circle(Pa.x, Pa.y, 6);
          const pAa = projectionPoint(endpoints.alpha, 'A');
          const pAb = projectionPoint(endpoints.alpha, 'B');
          const pAc = projectionPoint(endpoints.alpha, 'C');
          stroke(varOrange()); strokeWeight(1.5);
          drawArrow(Pa, pAa, varOrange(), true);
          drawArrow(Pa, pAb, varOrange(), true);
          drawArrow(Pa, pAc, varOrange(), true);
        }
        if (showBetaPhase){
          noStroke(); fill(varMagenta());
          circle(Pb.x, Pb.y, 6);
          const pBa = projectionPoint(endpoints.beta, 'A');
          const pBb = projectionPoint(endpoints.beta, 'B');
          const pBc = projectionPoint(endpoints.beta, 'C');
          stroke(varMagenta()); strokeWeight(1.5);
          drawArrow(Pb, pBa, varMagenta(), true);
          drawArrow(Pb, pBb, varMagenta(), true);
          drawArrow(Pb, pBc, varMagenta(), true);
        }
      }
    }
  } else {
    // phases view
    // Fill entire triangle light red (one phase)
    noStroke();
    fill('#fbe5e6');
    triangle(P_A.x, P_A.y, P_B.x, P_B.y, P_C.x, P_C.y);

    // Fill envelope region light blue
    fill('#d4eafc');
    beginShape();
    for (const p of envelopePolygonPixels) vertex(p.x, p.y);
    endShape(CLOSE);

    // Draw boundary of envelope with colored segments and labels
    noFill(); strokeWeight(2);
    stroke(varOrange());
    beginShape();
    for (const b of alphaCurvePoints){ const p = baryToCart(b); vertex(p.x, p.y); }
    endShape();
    stroke(varMagenta());
    beginShape();
    for (const b of betaCurvePoints){ const p = baryToCart(b); vertex(p.x, p.y); }
    endShape();

    // Labels
    const midAlpha = baryToCart(alphaCurvePoints[Math.floor(alphaCurvePoints.length*0.6)]);
    const midBeta  = baryToCart(betaCurvePoints[Math.floor(betaCurvePoints.length*0.4)]);
    noStroke(); textAlign(CENTER, CENTER); textSize(13);
    fill(varOrange()); text('alpha phase boundary', midAlpha.x, midAlpha.y - 12);
    fill(varMagenta()); text('beta phase boundary',  midBeta.x,  midBeta.y + 16);

    // Region labels
    const centroid = polyCentroid(envelopePolygonPixels);
    fill('#0f3d6e'); textSize(16); text('two phases', centroid.x, centroid.y);
    fill('#7d1a1d'); text('one phase', triCenter.x, triBaseY - triHeight*0.35);

    // User point
    const Pu = baryToCart(userPoint);
    fill(0); noStroke(); circle(Pu.x, Pu.y, 6);

    // Plait point and curved arrow
    const plait = baryToCart(betaCurvePoints[0]); // where both curves meet (our construction)
    fill('#222'); noStroke(); textSize(12); textAlign(LEFT, BOTTOM);
    text('plait point', plait.x + 10, plait.y - 10);
    stroke('#222'); strokeWeight(1.5); noFill();
    // Simple curved arrow (quadratic Bézier imitation)
    const a1 = {x: plait.x - 50, y: plait.y - 40};
    const a2 = {x: plait.x + 10, y: plait.y - 10};
    // Draw arc with multiple segments
    const steps = 20;
    let prev = null;
    for (let i=0;i<=steps;i++){
      const t = i/steps;
      const x = (1-t)*(1-t)*a1.x + 2*(1-t)*t*(plait.x - 15) + t*t*a2.x;
      const y = (1-t)*(1-t)*a1.y + 2*(1-t)*t*(plait.y - 50) + t*t*a2.y;
      if (prev) line(prev.x, prev.y, x, y);
      prev = {x,y};
    }
    // Arrowhead
    drawArrow({x: plait.x - 14, y: plait.y - 20}, {x: plait.x + 1, y: plait.y - 8}, '#222');
  }
}

/* Helper: centroid of polygon */
function polyCentroid(poly){
  let x=0,y=0, a=0;
  for (let i=0,j=poly.length-1;i<poly.length;j=i++){
    const p0 = poly[j], p1 = poly[i];
    const cross = p0.x*p1.y - p1.x*p0.y;
    a += cross;
    x += (p0.x + p1.x)*cross;
    y += (p0.y + p1.y)*cross;
  }
  a *= 0.5;
  if (Math.abs(a) < 1e-9) return {x: triCenter.x, y: triCenter.y};
  return {x: x/(6*a), y: y/(6*a)};
}

/* ===================================
   Initialize defaults on load
   =================================== */
document.addEventListener('DOMContentLoaded', () => {
  // Defaults are already set by HTML checked attributes and variables
  showGridLines = chkGrid.checked; // false by default
  showTieLines  = chkTies.checked; // true by default
  showAlphaPhase = chkAlpha.checked;
  showBetaPhase  = chkBeta.checked;
});
</script>
</body>
