  <script>
    // ---------------- Utilities ----------------
    function LCG(seed) {
      let m = 0x80000000; // 2^31
      let a = 1103515245;
      let c = 12345;
      let state = (seed >>> 0) % m;
      return function() {
        state = (a * state + c) % m;
        return state / (m - 1);
      };
    }
    function formatNum(x, digits = 6) {
      return (typeof x === 'number' && isFinite(x)) ? x.toFixed(digits) : '';
    }
    function euclidean(a, b) {
      let dx = a.x - b.x, dy = a.y - b.y;
      return Math.hypot(dx, dy);
    }
    function buildDistanceMatrix(points) {
      const n = points.length;
      const d = Array.from({length: n}, () => new Array(n).fill(0));
      for (let i = 0; i < n; i++) {
        for (let j = i+1; j < n; j++) {
          const dist = euclidean(points[i], points[j]);
          d[i][j] = dist; d[j][i] = dist;
        }
      }
      return d;
    }
    function tourLength(tour, dist) {
      const n = tour.length;
      let L = 0;
      for (let i = 0; i < n; i++) {
        const a = tour[i];
        const b = tour[(i+1) % n];
        L += dist[a][b];
      }
      return L;
    }

    // ---------------- Data Generation ----------------
    function generatePoints(N, seed) {
      const rnd = LCG(Number(seed) >>> 0);
      const pts = [];
      for (let i = 0; i < N; i++) {
        pts.push({x: rnd(), y: rnd()});
      }
      return pts;
    }

    // ---------------- Initial Tour: Nearest Neighbor ----------------
    function nearestNeighborTour(dist) {
      const n = dist.length;
      const visited = new Array(n).fill(false);
      const tour = [0];
      visited[0] = true;
      let current = 0;
      for (let step = 1; step < n; step++) {
        let best = -1, bestD = Infinity;
        for (let j = 0; j < n; j++) {
          if (!visited[j] && dist[current][j] < bestD) {
            bestD = dist[current][j];
            best = j;
          }
        }
        tour.push(best);
        visited[best] = true;
        current = best;
      }
      return tour;
    }

    // ---------------- 2-Opt ----------------
    function twoOptSwap(tour, i, k) {
      const newTour = tour.slice(0, i).concat(tour.slice(i, k+1).reverse(), tour.slice(k+1));
      return newTour;
    }
    function solveTwoOpt(initialTour, dist) {
      let tour = initialTour.slice();
      const n = tour.length;
      let improved = true;
      while (improved) {
        improved = false;
        for (let i = 1; i < n - 1; i++) {
          for (let k = i + 1; k < n; k++) {
            const a = tour[i-1], b = tour[i];
            const c = tour[k], d = tour[(k+1) % n];
            const delta = (dist[a][b] + dist[c][d]) - (dist[a][c] + dist[b][d]);
            if (delta > 1e-12) {
              tour = twoOptSwap(tour, i, k);
              improved = true;
            }
          }
        }
      }
      return tour;
    }

    // ---------------- Relocate (1-city) for 2.5-Opt and Or-Opt ----------------
    function relocateSegment(tour, i, m, j) {
      // Move segment tour[i..i+m-1] to position j
      const seg = tour.slice(i, i+m);
      const rest = tour.slice(0, i).concat(tour.slice(i+m));
      const newTour = rest.slice(0, j).concat(seg, rest.slice(j));
      return newTour;
    }
    function solveRelocateSingle(initialTour, dist) {
      let tour = initialTour.slice();
      const n = tour.length;
      let improved = true;
      while (improved) {
        improved = false;
        outer: for (let i = 0; i < n; i++) {
          for (let j = 0; j < n; j++) {
            // Build a linear tour, skip inserting segment into its original position or directly adjacent equivalences
            if (j === i || j === i+1) continue;
            const newTour = relocateSegment(tour, i, 1, j < i ? j : j-1);
            const oldLen = tourLength(tour, dist);
            const newLen = tourLength(newTour, dist);
            if (newLen + 1e-12 < oldLen) {
              tour = newTour;
              improved = true;
              break outer;
            }
          }
        }
      }
      return tour;
    }

    // ---------------- Or-Opt (segments of 1,2,3) ----------------
    function solveOrOpt(initialTour, dist) {
      let tour = initialTour.slice();
      const n = tour.length;
      let improved = true;
      while (improved) {
        improved = false;
        for (let m = 1; m <= 3; m++) {
          outer: for (let i = 0; i <= n - m; i++) {
            for (let j = 0; j <= n - m; j++) {
              // Compute insertion index in rest of tour
              const segStart = i, segEnd = i + m - 1;
              if (j >= segStart && j <= segEnd + 1) continue; // skip inserting within segment
              const newTour = relocateSegment(tour, segStart, m, j < segStart ? j : j - m);
              const oldLen = tourLength(tour, dist);
              const newLen = tourLength(newTour, dist);
              if (newLen + 1e-12 < oldLen) {
                tour = newTour;
                improved = true;
                break outer;
              }
            }
          }
        }
      }
      return tour;
    }

    // ---------------- 3-Opt (heuristic) ----------------
    function solve3Opt(initialTour, dist) {
      // Start with a 2-Opt refinement, then perform a 3-Opt improvement heuristic
      let tour = solveTwoOpt(initialTour, dist);
      const n = tour.length;
      let improved = true;
      while (improved) {
        improved = false;
        outer: for (let i = 1; i < n - 4; i++) {
          for (let j = i + 2; j < n - 2; j++) {
            for (let k = j + 2; k < n - (i === 1 ? 1 : 0); k++) {
              const A = tour.slice(0, i);
              const B = tour.slice(i, j);
              const C = tour.slice(j, k);
              const D = tour.slice(k);

              const candidates = [];
              // 3-opt reconnections (subset of the 7 canonical cases)
              candidates.push(A.concat(B.slice().reverse(), C, D)); // reverse B
              candidates.push(A.concat(B, C.slice().reverse(), D)); // reverse C
              candidates.push(A.concat(B.slice().reverse(), C.slice().reverse(), D)); // reverse B and C
              candidates.push(A.concat(C, B, D)); // swap B and C
              candidates.push(A.concat(C.slice().reverse(), B, D)); // move reversed C before B
              candidates.push(A.concat(C, B.slice().reverse(), D)); // move B reversed after C
              // Evaluate candidates
              const baseLen = tourLength(tour, dist);
              for (let cand of candidates) {
                const newLen = tourLength(cand, dist);
                if (newLen + 1e-12 < baseLen) {
                  tour = cand;
                  improved = true;
                  break outer;
                }
              }
            }
          }
        }
      }
      return tour;
    }

    // ---------------- OrZweig (2.5-Opt variant: 2-Opt + Relocate single) ----------------
    function solveOrZweig(initialTour, dist) {
      const twoOpted = solveTwoOpt(initialTour, dist);
      const relocated = solveRelocateSingle(twoOpted, dist);
      return relocated;
    }

    // ---------------- Christofides (approx) ----------------
    function primMST(dist) {
      const n = dist.length;
      const inMST = new Array(n).fill(false);
      const key = new Array(n).fill(Infinity);
      const parent = new Array(n).fill(-1);
      key[0] = 0;
      for (let count = 0; count < n - 1; count++) {
        // pick min key not in MST
        let u = -1, minK = Infinity;
        for (let v = 0; v < n; v++) {
          if (!inMST[v] && key[v] < minK) { minK = key[v]; u = v; }
        }
        if (u === -1) break;
        inMST[u] = true;
        for (let v = 0; v < n; v++) {
          if (!inMST[v] && dist[u][v] < key[v]) {
            key[v] = dist[u][v];
            parent[v] = u;
          }
        }
      }
      // parent[v] gives edge (v, parent[v])
      const edges = [];
      for (let v = 1; v < n; v++) {
        if (parent[v] !== -1) edges.push([v, parent[v]]);
      }
      return edges;
    }
    function oddDegreeVertices(n, mstEdges) {
      const deg = new Array(n).fill(0);
      for (const [u,v] of mstEdges) { deg[u]++; deg[v]++; }
      const odds = [];
      for (let i = 0; i < n; i++) if (deg[i] % 2 === 1) odds.push(i);
      return {odds, deg};
    }
    function greedyPerfectMatching(odds, dist) {
      const pairs = [];
      for (let i = 0; i < odds.length; i++) {
        for (let j = i+1; j < odds.length; j++) {
          pairs.push([odds[i], odds[j], dist[odds[i]][odds[j]]]);
        }
      }
      pairs.sort((a,b) => a[2] - b[2]);
      const matched = new Set();
      const matching = [];
      for (const [u, v] of pairs) {
        if (!matched.has(u) && !matched.has(v)) {
          matched.add(u); matched.add(v);
          matching.push([u,v]);
        }
      }
      return matching;
    }
    function buildMultigraph(n, mstEdges, matchingEdges) {
      // Edge list with ids for Euler
      const edges = [];
      const adj = Array.from({length: n}, () => []);
      let eid = 0;
      function addEdge(u,v) {
        const e = { id: eid++, u, v, used: false };
        edges.push(e);
        adj[u].push(e);
        adj[v].push(e);
      }
      for (const [u,v] of mstEdges) addEdge(u,v);
      for (const [u,v] of matchingEdges) addEdge(u,v);
      return {edges, adj};
    }
    function eulerianCircuit(adj, edges, start=0) {
      const circuit = [];
      const stack = [start];
      const itIndex = adj.map(() => 0); // iterator index per vertex
      // We need to manage used edges; edges are shared objects
      while (stack.length) {
        const v = stack[stack.length - 1];
        // Find next unused edge incident to v
        while (itIndex[v] < adj[v].length && adj[v][itIndex[v]].used) {
          itIndex[v]++;
        }
        if (itIndex[v] === adj[v].length) {
          circuit.push(v);
          stack.pop();
        } else {
          const e = adj[v][itIndex[v]];
          e.used = true;
          const w = (e.u === v) ? e.v : e.u;
          stack.push(w);
        }
      }
      return circuit.reverse();
    }
    function makeHamiltonian(euler, n) {
      const visited = new Array(n).fill(false);
      const tour = [];
      for (const v of euler) {
        if (!visited[v]) {
          tour.push(v);
          visited[v] = true;
        }
      }
      // Ensure it's a full cycle: if last equals first in euler, tour already circular. If some vertices missing, they are appended by order of first seen.
      if (tour.length < n) {
        // Shouldn't happen with Euler traversal, but safeguard
        for (let i = 0; i < n; i++) if (!visited[i]) tour.push(i);
      }
      return tour;
    }
    function solveCCA(dist) {
      const n = dist.length;
      const mst = primMST(dist);
      const {odds} = oddDegreeVertices(n, mst);
      const match = greedyPerfectMatching(odds, dist);
      const mg = buildMultigraph(n, mst, match);
      // Reset used flags in case
      for (const e of mg.edges) e.used = false;
      const euler = eulerianCircuit(mg.adj, mg.edges, 0);
      const ham = makeHamiltonian(euler, n);
      return ham;
    }

    // ---------------- p5 Plot Factory ----------------
    function createPlot(wrapperId, pathColor) {
      const sketch = (p) => {
        let points = [];
        let tour = [];
        let margin = 40;
        let canvas;
        function getWrapperSize() {
          const el = document.getElementById(wrapperId);
          let w = el.clientWidth ? el.clientWidth - 16 : 420;
          w = Math.max(300, Math.min(520, w));
          return {w: w, h: w};
        }
        p.setup = function() {
          const {w,h} = getWrapperSize();
          canvas = p.createCanvas(w, h);
          canvas.parent(wrapperId);
          p.pixelDensity(1);
          p.noLoop();
        };
        p.windowResized = function() {
          const {w,h} = getWrapperSize();
          p.resizeCanvas(w,h);
          p.redraw();
        };
        function xToPx(x) { return margin + x * (p.width - 2*margin); }
        function yToPx(y) { return p.height - margin - y * (p.height - 2*margin); }
        function drawAxes() {
          p.push();
          p.stroke(0);
          p.strokeWeight(1);
          // Axes lines (box)
          p.line(margin, p.height - margin, p.width - margin, p.height - margin); // x-axis
          p.line(margin, p.height - margin, margin, margin); // y-axis
          // Ticks
          p.textSize(10);
          p.fill(60);
          p.noStroke();
          for (let t = 0.2; t <= 1.0001; t += 0.2) {
            const x = xToPx(t);
            const y = yToPx(t);
            // x ticks
            p.stroke(0); p.strokeWeight(1);
            p.line(x, p.height - margin - 5, x, p.height - margin + 5);
            p.noStroke(); p.textAlign(p.CENTER, p.TOP);
            p.text(t.toFixed(1), x, p.height - margin + 6);
            // y ticks
            p.stroke(0); p.strokeWeight(1);
            p.line(margin - 5, y, margin + 5, y);
            p.noStroke(); p.textAlign(p.RIGHT, p.CENTER);
            p.text(t.toFixed(1), margin - 8, y);
          }
          p.pop();
        }
        function drawPoints() {
          p.push();
          p.noStroke();
          p.fill('red');
          for (const pt of points) {
            p.circle(xToPx(pt.x), yToPx(pt.y), 6);
          }
          p.pop();
        }
        function drawTour() {
          if (!tour || tour.length === 0) return;
          p.push();
          p.noFill();
          p.stroke(pathColor);
          p.strokeWeight(2);
          for (let i = 0; i < tour.length; i++) {
            const a = tour[i];
            const b = tour[(i+1)%tour.length];
            const pa = points[a];
            const pb = points[b];
            p.line(xToPx(pa.x), yToPx(pa.y), xToPx(pb.x), yToPx(pb.y));
          }
          p.pop();
        }
        p.draw = function() {
          p.background(255);
          drawAxes();
          drawPoints();
          drawTour();
        };
        p.setData = function(newPoints, newTour) {
          points = newPoints || [];
          tour = (newTour || []).slice();
          p.redraw();
        };
      };
      const pinst = new p5(sketch);
      return pinst;
    }

    // ---------------- Main State and Logic ----------------
    const state = {
      N: 10,
      seed: 1245,
      points: [],
      dist: [],
      results: {}, // method -> {tour, length, time}
      currentMethod: 'OrZweig'
    };

    let leftPlot, rightPlot;

    function updateTable() {
      const r = state.results;
      if (!r) return;
      const safe = (k) => r[k] || {time: 0, length: 0};
      const methods = [
        ['OrZweig', 'timing-orzweig', 'result-orzweig'],
        ['OrOpt', 'timing-oropt', 'result-oropt'],
        ['TwoOpt', 'timing-twoopt', 'result-twoopt'],
        ['CCA', 'timing-cca', 'result-cca'],
        ['3-Opt', 'timing-3opt', 'result-3opt']
      ];
      for (const [name, timeId, lenId] of methods) {
        const rr = safe(name);
        document.getElementById(timeId).textContent = formatNum(rr.time, 6);
        document.getElementById(lenId).textContent = formatNum(rr.length, 5);
      }
    }

    function updateWinner() {
      const r = state.results;
      const eps = 1e-6;
      const mathMethods = ['OrZweig', 'OrOpt', 'TwoOpt', 'CCA'];
      const mathMin = Math.min(...mathMethods.map(m => r[m]?.length ?? Infinity));
      const opt3 = r['3-Opt']?.length ?? Infinity;
      let text = 'Tie';
      if (opt3 + eps < mathMin) text = '3-Opt wins';
      else if (mathMin + eps < opt3) text = 'Mathematica wins';
      else text = 'Tie';
      document.getElementById('winner-display').textContent = text;
    }

    function updatePlots() {
      const selected = state.currentMethod;
      const leftTour = state.results[selected]?.tour || [];
      const rightTour = state.results['3-Opt']?.tour || [];
      leftPlot.setData(state.points, leftTour);
      rightPlot.setData(state.points, rightTour);
    }

    function recalc() {
      // Read inputs
      const seedVal = Number(document.getElementById('seed-input').value || 0);
      const N = Number(document.getElementById('slider-points').value || 10);
      state.seed = seedVal;
      state.N = N;

      // Generate data
      const points = generatePoints(N, seedVal);
      const dist = buildDistanceMatrix(points);
      state.points = points;
      state.dist = dist;

      const results = {};

      // Common initial tour via Nearest Neighbor
      const initialTour = nearestNeighborTour(dist);

      // OrZweig
      let t0 = performance.now();
      const tourOrZ = solveOrZweig(initialTour, dist);
      let t1 = performance.now();
      results['OrZweig'] = { tour: tourOrZ, length: tourLength(tourOrZ, dist), time: (t1 - t0) / 1000 };

      // OrOpt
      t0 = performance.now();
      const tourOrOpt = solveOrOpt(initialTour, dist);
      t1 = performance.now();
      results['OrOpt'] = { tour: tourOrOpt, length: tourLength(tourOrOpt, dist), time: (t1 - t0) / 1000 };

      // TwoOpt
      t0 = performance.now();
      const tourTwo = solveTwoOpt(initialTour, dist);
      t1 = performance.now();
      results['TwoOpt'] = { tour: tourTwo, length: tourLength(tourTwo, dist), time: (t1 - t0) / 1000 };

      // CCA (Christofides)
      t0 = performance.now();
      const tourCCA = solveCCA(dist);
      t1 = performance.now();
      results['CCA'] = { tour: tourCCA, length: tourLength(tourCCA, dist), time: (t1 - t0) / 1000 };

      // 3-Opt
      t0 = performance.now();
      const tour3 = solve3Opt(initialTour, dist);
      t1 = performance.now();
      results['3-Opt'] = { tour: tour3, length: tourLength(tour3, dist), time: (t1 - t0) / 1000 };

      state.results = results;

      // Update UI
      updateTable();
      updateWinner();
      updatePlots();
    }

    // ---------------- Event Wiring ----------------
    window.addEventListener('DOMContentLoaded', () => {
      // Create plots
      leftPlot = createPlot('canvas-left-wrapper', '#0072B2');
      rightPlot = createPlot('canvas-right-wrapper', '#E69F00');

      // Default table values already set per requirement, proceed to calculation
      setTimeout(recalc, 0);

      document.getElementById('btn-random-set').addEventListener('click', () => {
        const newSeed = Math.floor(Math.random() * 99999) + 1;
        document.getElementById('seed-input').value = String(newSeed);
        recalc();
      });
      document.getElementById('btn-reset-seed').addEventListener('click', () => {
        document.getElementById('seed-input').value = '0';
        recalc();
      });
      document.getElementById('slider-points').addEventListener('change', () => {
        recalc();
      });
      const radios = document.querySelectorAll('input[name="math-method"]');
      radios.forEach(r => {
        r.addEventListener('change', () => {
          if (!r.checked) return;
          state.currentMethod = r.value;
          // Update title
          const label = document.querySelector('label[for="' + r.id + '"]');
          document.getElementById('left-plot-title').textContent = label ? label.textContent : r.value;
          // Redraw left plot only using precomputed tour
          updatePlots();
        });
      });
    });
  </script>
</body>
</html>
