          p.noStroke();
          p.fill('#00FFFF');
          for (let i = 0; i < state.fixedPoints.length; i++) {
            const pt = nToXY(state.fixedPoints[i]);
            const c = toCanvasCoord(pt);
            p.circle(c.cx, c.cy, 3);
          }
        }

        function drawTwoCycles() {
          p.noFill();
          p.stroke('#008000');
          p.strokeWeight(1.5);
          for (let i = 0; i < twoCycles.length; i++) {
            const n1 = twoCycles[i][0];
            const n2 = twoCycles[i][1];
            const p1 = toCanvasCoord(nToXY(n1));
            const p2 = toCanvasCoord(nToXY(n2));
            p.line(p1.cx, p1.cy, p2.cx, p2.cy);
          }
        }

        function drawFourCycleAndAnnotations() {
          // Draw the 4-cycle path
          const pts = fourCycle.map(n => toCanvasCoord(nToXY(n)));
          p.noFill();
          p.stroke('#008000');
          p.strokeWeight(2);
          for (let i = 0; i < pts.length; i++) {
            const a = pts[i];
            const b = pts[(i + 1) % pts.length];
            p.line(a.cx, a.cy, b.cx, b.cy);
          }

          // Bounding box
          let minX = Infinity, minY = Infinity, maxX = -Infinity, maxY = -Infinity;
          for (const q of pts) {
            if (q.cx < minX) minX = q.cx;
            if (q.cy < minY) minY = q.cy;
            if (q.cx > maxX) maxX = q.cx;
            if (q.cy > maxY) maxY = q.cy;
          }
          p.noFill();
          p.stroke('#FFA500');
          p.strokeWeight(2);
          p.rect(minX, minY, maxX - minX, maxY - minY);

          // Perpendicular bisectors of segments
          // For each segment, compute midpoint and normal
          p.stroke('#008000');
          p.strokeWeight(1.5);
          const L = 50; // half-length of bisector lines in pixels
          for (let i = 0; i < pts.length; i++) {
            const a = pts[i];
            const b = pts[(i + 1) % pts.length];
            const mx = (a.cx + b.cx) / 2;
            const my = (a.cy + b.cy) / 2;
            const vx = b.cx - a.cx;
            const vy = b.cy - a.cy;
            const len = Math.hypot(vx, vy) || 1;
            // Perpendicular vector normalized
            const nx = -vy / len;
            const ny =  vx / len;
            p.line(mx - nx * L, my - ny * L, mx + nx * L, my + ny * L);
          }
        }

        p.draw = function() {
          p.background('#e0f0e0');

          // 1) All map points overlay
          if (state.showAll) {
            drawAllMapPoints();
          }

          // 2) Automaton Path
          drawSequencePath();

          // 3) Length 1 Cycle Points
          if (state.showCycle1) {
            drawFixedPoints();
          }

          // 4) Length 2 Cycles
          if (state.showCycle2) {
            drawTwoCycles();
          }

          // 5) Length 4 Cycle with bounding box and bisectors
          if (state.showCycle4) {
            drawFourCycleAndAnnotations();
          }
        };
      });

      // Initialize
      precomputeFixedPoints();
      recompute();
      p5Instance.redraw();

      // Event listeners
      sliderInit.addEventListener('input', function() {
        state.init = parseInt(sliderInit.value, 10) || 0;
        spanInit.textContent = "+ " + state.init;
        recompute();
        p5Instance.redraw();
      });

      sliderMaxSteps.addEventListener('input', function() {
        state.maxSteps = parseInt(sliderMaxSteps.value, 10) || 1;
        spanMax.textContent = "+ " + state.maxSteps;
        recompute();
        p5Instance.redraw();
      });

      cbAll.addEventListener('change', function() {
        state.showAll = cbAll.checked;
        p5Instance.redraw();
      });
      cbCycle1.addEventListener('change', function() {
        state.showCycle1 = cbCycle1.checked;
        p5Instance.redraw();
      });
      cbCycle2.addEventListener('change', function() {
        state.showCycle2 = cbCycle2.checked;
        p5Instance.redraw();
      });
      cbCycle4.addEventListener('change', function() {
        state.showCycle4 = cbCycle4.checked;
        p5Instance.redraw();
      });

      cbModify.addEventListener('change', function() {
        state.modifyInit = cbModify.checked;
        // No visual change required; do not redraw.
      });
    })();
  </script>
</body>
</html>
