    <div id="slider-container">
      <label>zenith right ascension</label>
      <input type="range" id="slider-ra" min="0" max="360" step="1" value="180" />
      <label>zenith declination</label>
      <input type="range" id="slider-dec" min="-90" max="90" step="1" value="0" />
    </div>

    <div class="note">
      Mouse: drag to rotate • wheel to zoom • Shift+drag to pan
    </div>
  </div>
  <div id="visualization-container"></div>
