<script type="module">
  // Import three.js and helpers
  import * as THREE from 'https://unpkg.com/three@0.160.0/build/three.module.js';
  import { OrbitControls } from 'https://unpkg.com/three@0.160.0/examples/jsm/controls/OrbitControls.js';
  import { CSS2DRenderer, CSS2DObject } from 'https://unpkg.com/three@0.160.0/examples/jsm/renderers/CSS2DRenderer.js';

  // DOM references
  const container = document.getElementById('container');
  const vizContainer = document.getElementById('visualization-container');
  const familySelect = document.getElementById('select-family');
  const constellationSelect = document.getElementById('select-constellation');
  const checkSphere = document.getElementById('check-sphere');
  const checkStars = document.getElementById('check-stars');
  const checkEquator = document.getElementById('check-equator');
  const checkEcliptic = document.getElementById('check-ecliptic');
  const checkZenith = document.getElementById('check-zenith');
  const checkAries = document.getElementById('check-aries');
  const sliderRA = document.getElementById('slider-ra');
  const sliderDec = document.getElementById('slider-dec');

  // Scene setup
  const scene = new THREE.Scene();
  scene.background = new THREE.Color(0x000000);

  const R = 1; // sphere radius

  const camera = new THREE.PerspectiveCamera(50, 1, 0.01, 100);
  camera.position.set(0, 0.8, 2.4);

  const renderer = new THREE.WebGLRenderer({ antialias: true, alpha: false });
  renderer.setPixelRatio(Math.min(window.devicePixelRatio, 2));
  vizContainer.appendChild(renderer.domElement);

  const labelRenderer = new CSS2DRenderer();
  labelRenderer.domElement.style.position = 'absolute';
  labelRenderer.domElement.style.top = '0';
  labelRenderer.domElement.style.left = '0';
  labelRenderer.domElement.style.pointerEvents = 'none';
  vizContainer.appendChild(labelRenderer.domElement);

  const controls = new OrbitControls(camera, renderer.domElement);
  controls.enableDamping = true;
  controls.dampingFactor = 0.07;
  controls.minDistance = 0.5;
  controls.maxDistance = 6;
  controls.target.set(0, 0, 0);

  // Resize handling
  function resize() {
    const rect = vizContainer.getBoundingClientRect();
    camera.aspect = rect.width / rect.height;
    camera.updateProjectionMatrix();
    renderer.setSize(rect.width, rect.height);
    labelRenderer.setSize(rect.width, rect.height);
  }
  window.addEventListener('resize', resize);

  // Helpers: conversions
  const DEG2RAD = Math.PI / 180;
  const HOUR2RAD = 2 * Math.PI / 24;

  function raHoursDecDegToCartesian(raHours, decDeg, radius = R) {
    const ra = raHours * HOUR2RAD;
    const dec = decDeg * DEG2RAD;
    const x = radius * Math.cos(dec) * Math.cos(ra);
    const y = radius * Math.sin(dec);
    const z = radius * Math.cos(dec) * Math.sin(ra);
    return new THREE.Vector3(x, y, z);
  }

  function raDegDecDegToCartesian(raDeg, decDeg, radius = R) {
    const ra = raDeg * DEG2RAD;
    const dec = decDeg * DEG2RAD;
    const x = radius * Math.cos(dec) * Math.cos(ra);
    const y = radius * Math.sin(dec);
    const z = radius * Math.cos(dec) * Math.sin(ra);
    return new THREE.Vector3(x, y, z);
  }

  // Objects to toggle
  let sphereMesh;
  let equatorLine;
  let eclipticLine;
  let ariesPoint;
  let polarAxis;
  let starsPoints;
  let constellationLines;
  let constellationStars;

  // Zenith group (sphere + line to origin)
  const zenithGroup = new THREE.Group();
  scene.add(zenithGroup);
  const zenithSphere = new THREE.Mesh(
    new THREE.SphereGeometry(0.02, 16, 16),
    new THREE.MeshBasicMaterial({ color: 0x000000 })
  );
  zenithGroup.add(zenithSphere);
  const zenithLineGeo = new THREE.BufferGeometry().setFromPoints([new THREE.Vector3(0,0,0), new THREE.Vector3(0,0,0)]);
  const zenithLine = new THREE.Line(zenithLineGeo, new THREE.LineBasicMaterial({ color: 0x000000 }));
  zenithGroup.add(zenithLine);

  // Polar axis and labels
  function buildPolarAxis() {
    const pts = [
      new THREE.Vector3(0, -R * 1.1, 0), new THREE.Vector3(0, R * 1.1, 0)
    ];
    const geo = new THREE.BufferGeometry().setFromPoints(pts);
    const mat = new THREE.LineBasicMaterial({ color: 0x222222 });
    polarAxis = new THREE.LineSegments(geo, mat);
    scene.add(polarAxis);

    const northDiv = document.createElement('div');
    northDiv.className = 'pole-label';
    northDiv.textContent = 'N';
    const northLabel = new CSS2DObject(northDiv);
    northLabel.position.set(0, R * 1.05, 0);
    scene.add(northLabel);

    const southDiv = document.createElement('div');
    southDiv.className = 'pole-label';
    southDiv.textContent = 'S';
    const southLabel = new CSS2DObject(southDiv);
    southLabel.position.set(0, -R * 1.05, 0);
    scene.add(southLabel);
  }

  // Celestial sphere with vertex colors (red near ecliptic plane, blue near poles)
  function buildCelestialSphere() {
    const geo = new THREE.SphereGeometry(R, 64, 48);
    const pos = geo.getAttribute('position');
    const count = pos.count;
    const colors = new Float32Array(count * 3);

    // Ecliptic plane normal (equator normal rotated by +23.5° about X-axis)
    const eps = 23.5 * DEG2RAD;
    const n = new THREE.Vector3(0, Math.cos(eps), Math.sin(eps)); // normalized

    const colorNear = new THREE.Color(1.0, 0.4, 0.1);  // orange/red near ecliptic
    const colorFar  = new THREE.Color(0.2, 0.4, 1.0);  // bluish near poles
    const v = new THREE.Vector3();

    for (let i = 0; i < count; i++) {
      v.fromBufferAttribute(pos, i).normalize();
      const d = Math.abs(n.dot(v)); // 0 near plane, 1 near ecliptic poles
      const t = d; // mix factor: 0 => ecliptic plane (red), 1 => poles (blue)
      const c = colorNear.clone().multiplyScalar(1 - t).add(colorFar.clone().multiplyScalar(t));
      colors[i * 3 + 0] = c.r;
      colors[i * 3 + 1] = c.g;
      colors[i * 3 + 2] = c.b;
    }
    geo.setAttribute('color', new THREE.BufferAttribute(colors, 3));

    const mat = new THREE.MeshBasicMaterial({
      vertexColors: true,
      opacity: 0.4,
      transparent: true,
      side: THREE.DoubleSide
    });

    sphereMesh = new THREE.Mesh(geo, mat);
    scene.add(sphereMesh);
  }

  // Equator and Ecliptic circles
  function buildGreatCircles() {
    const segments = 360;
    const equatorPts = [];
    const eclipticPts = [];

    const eps = 23.5 * DEG2RAD;
    for (let i = 0; i < segments; i++) {
      const a = (i / segments) * Math.PI * 2;
      // Equator: Y = 0 plane, circle on XZ
      equatorPts.push(new THREE.Vector3(R * Math.cos(a), 0, R * Math.sin(a)));

      // Ecliptic: rotate equator circle about X by +eps
      const x = Math.cos(a) * R;
      const y = -Math.sin(a) * Math.sin(eps) * R;
      const z = Math.sin(a) * Math.cos(eps) * R;
      eclipticPts.push(new THREE.Vector3(x, y, z));
    }

    const eqGeo = new THREE.BufferGeometry().setFromPoints(equatorPts);
    equatorLine = new THREE.LineLoop(eqGeo, new THREE.LineBasicMaterial({ color: 0x3da2ff, linewidth: 2 }));
    scene.add(equatorLine);

    const ecGeo = new THREE.BufferGeometry().setFromPoints(eclipticPts);
    eclipticLine = new THREE.LineLoop(ecGeo, new THREE.LineBasicMaterial({ color: 0xcc66ff, linewidth: 2 }));
    scene.add(eclipticLine);
  }

  // First Point of Aries (intersection at +X)
  function buildAriesPoint() {
    const geo = new THREE.SphereGeometry(0.03, 16, 16);
    const mat = new THREE.MeshBasicMaterial({ color: 0xffd42a });
    ariesPoint = new THREE.Mesh(geo, mat);
    ariesPoint.position.set(R, 0, 0);
    scene.add(ariesPoint);
  }

  // Random 1000 "bright" stars
  function buildStars() {
    const count = 1000;
    const positions = new Float32Array(count * 3);

    for (let i = 0; i < count; i++) {
      // Uniformly sample sphere
      const u = Math.random() * 2 - 1;           // cos(dec)
      const phi = Math.random() * Math.PI * 2;   // RA angle (0..2π)

      const x = R * Math.sqrt(1 - u*u) * Math.cos(phi);
      const y = R * u;
      const z = R * Math.sqrt(1 - u*u) * Math.sin(phi);

      positions[i * 3 + 0] = x;
      positions[i * 3 + 1] = y;
      positions[i * 3 + 2] = z;
    }

    const geo = new THREE.BufferGeometry();
    geo.setAttribute('position', new THREE.BufferAttribute(positions, 3));
    const mat = new THREE.PointsMaterial({ color: 0xffffff, size: 0.01, sizeAttenuation: true });
    starsPoints = new THREE.Points(geo, mat);
    scene.add(starsPoints);
  }

