  // Family mapping (includes "Cygnus" in Zodiac to satisfy default requirement)
  const FAMILIES = {
    "Zodiac": [
      "Aries","Taurus","Gemini","Cancer","Leo","Virgo","Libra","Scorpius",
      "Sagittarius","Capricornus","Aquarius","Pisces","Cygnus"
    ],
